        private void MainForm_Loaded(object sender, RoutedEventArgs e)
        {
            //Create timers
            tmrBlur.Interval = TimeSpan.FromMilliseconds(25);
            tmrBlur.Tick += new EventHandler(tmrBlur_Tick);
            setBlur(false);
            tmrUpdate.Interval = TimeSpan.FromMilliseconds(100);
            tmrUpdate.Tick += new EventHandler(tmrUpdate_Tick);
            tmrLoad.Interval = TimeSpan.FromMilliseconds(100);
            tmrLoad.Tick += new EventHandler(tmrLoad_Tick);

            //Determine if OS is Windows 7 or newer.
            OperatingSystem osInfo = Environment.OSVersion;
            try
            {
                switch (osInfo.Platform)
                {
                    case PlatformID.Win32NT
                        if (osInfo.Version.Major >= 6 && osInfo.Version.Minor >= 1)
                        {
                            win7 = true;
                            int aero = 0;
                            DwmIsCompositionEnabled(ref aero);
                            win6 = (aero > 0);
                        }
                        else
                        {
                            win7 = false;
                            int aero = 0;
                            DwmIsCompositionEnabled(ref aero);
                            win6 = (aero > 0);
                            win6 = (osInfo.Version.Major >= 6 && win6);
                        }
                        break;
                    default
                        win7 = false;
                        win6 = false;
                        break;
                }
            }
            catch (DllNotFoundException)
            {
                win7 = false;
                win6 = false;
            }
            if (win6) setGlass(this); else this.Background = SystemColors.ControlBrush;
            Progress.Fill = lstVideos.Background;
            Progress.Stroke = lstVideos.BorderBrush;

            tmrLoad.IsEnabled = true;
        }


        void tmrLoad_Tick(object sender, EventArgs e)
        {
            tmrLoad.IsEnabled = false;



            //Set culture to InvariantCulture for compatibility with non-US regions.
            CultureInfo culture = CultureInfo.InvariantCulture;
            Thread.CurrentThread.CurrentCulture = culture;
            Thread.CurrentThread.CurrentUICulture = culture;

            //Set default encoding parameters for x264.
            editVid = Set_defaults(editVid);
            appendadvanced = editVid;

            //Set Program Options
            string optionfile = appdir + "\\Options.ini";
            if (File.Exists(optionfile))
            {
                StreamReader oRead = File.OpenText(optionfile);
                string strOption, sub;
                while (oRead.Peek() > -1)
                {
                    strOption = oRead.ReadLine();
                    sub = strOption.Substring(15, strOption.Length - 15);
                    bool bsub = sub.ToLower() == "true";
                    switch (strOption.Substring(0, 15))
                    {
                        case "TempDir=       "
                            tempDir = sub;
                            break;
                        case "AutoUpdate=    "
                            boolUpdate = bsub;
                            break;
                        case "Resize=        "
                            strResize = sub;
                            break;
                        case "Shutdown=      "
                            strShutdown = sub;
                            break;
                        case "AVSAudio=      "
                            strAVSAudio = sub;
                            break;
                        case "Deinterlace=   "
                            strDeinterlace = sub;
                            break;
                        case "Film=          "
                            strFilm = sub;
                            break;
                        case "iTunes=        "
                            useiTunes = bsub;
                            break;
                        case "CustomOutput=  "
                            customoutput = bsub;
                            break;
                        case "OutputDir=     "
                            outputdir = sub;
                            break;
                        case "LogDir=        "
                            logDir = sub;
                            break;
                        case "EVO=           "
                            strEVO = sub;
                            break;
                        case "Profile=       "
                            strDefaultProfile = sub;
                            break;
                        case "64bit=         "
                            bit64 = bsub;
                            break;
                        case "Manual32bit=   "
                            manual32bit = bsub;
                            break;
                        case "Manual64bit=   "
                            manual64bit = bsub;
                            break;
                        case "FilmMode=      "
                            strFilmMode = sub;
                            break;
                        case "MPGDec=        "
                            MPGDec = bsub;
                            break;
                        case "PreloadVOB=       "
                            preloadVOB = bsub;
                            break;
                        case "AVCDec=        "
                            AVCDec = bsub;
                            break;
                        case "FFDec=         "
                            FFDec = bsub;
                            break;
                        case "PreloadMPG=    "
                            preloadMPG = bsub;
                            break;
                        case "PreloadAVC=    "
                            preloadAVC = bsub;
                            break;
                        case "SampleRate=    "
                            strSample = sub;
                            break;
                        case "ProfileUpdate= "
                            strUpdateProfile = sub;
                            break;
                        case "AutoSizeMode=  "
                            strAutoSize = sub;
                            break;
                    }
                }
                while (oRead.Peek() > -1)
                {
                    strOption = oRead.ReadLine();
                    sub = strOption.Substring(15, strOption.Length - 15);
                    bool bsub = sub.ToLower() == "true";
                    switch (strOption.Substring(0, 15))
                    {
                        case "TempDir=       "
                            tempDir = sub;
                            break;
                        case "AutoUpdate=    "
                            boolUpdate = bsub;
                            break;
                        case "Resize=        "
                            strResize = sub;
                            break;
                        case "Shutdown=      "
                            strShutdown = sub;
                            break;
                        case "AVSAudio=      "
                            strAVSAudio = sub;
                            break;
                        case "Deinterlace=   "
                            strDeinterlace = sub;
                            break;
                        case "Film=          "
                            strFilm = sub;
                            break;
                        case "iTunes=        "
                            useiTunes = bsub;
                            break;
                        case "CustomOutput=  "
                            customoutput = bsub;
                            break;
                        case "OutputDir=     "
                            outputdir = sub;
                            break;
                        case "LogDir=        "
                            logDir = sub;
                            break;
                        case "EVO=           "
                            strEVO = sub;
                            break;
                        case "Profile=       "
                            strDefaultProfile = sub;
                            break;
                        case "64bit=         "
                            bit64 = bsub;
                            break;
                        case "Manual32bit=   "
                            manual32bit = bsub;
                            break;
                        case "Manual64bit=   "
                            manual64bit = bsub;
                            break;
                        case "FilmMode=      "
                            strFilmMode = sub;
                            break;
                        case "MPGDec=        "
                            MPGDec = bsub;
                            break;
                        case "PreloadVOB=       "
                            preloadVOB = bsub;
                            break;
                        case "AVCDec=        "
                            AVCDec = bsub;
                            break;
                        case "FFDec=         "
                            FFDec = bsub;
                            break;
                        case "PreloadMPG=    "
                            preloadMPG = bsub;
                            break;
                        case "PreloadAVC=    "
                            preloadAVC = bsub;
                            break;
                        case "SampleRate=    "
                            strSample = sub;
                            break;
                        case "ProfileUpdate= "
                            strUpdateProfile = sub;
                            break;
                        case "AutoSizeMode=  "
                            strAutoSize = sub;
                            break;
                    }
                }
                oRead.Close();
            }

            //Create directories if needed
            if (!Directory.Exists(tempDir))
                Directory.CreateDirectory(tempDir);
            if (!Directory.Exists(outputdir) && customoutput)
                Directory.CreateDirectory(outputdir);
            if (!Directory.Exists(logDir))
                Directory.CreateDirectory(logDir);

            //Set Append Options
            string appendfile = appdir + "\\Append.ini";
            if (File.Exists(appendfile))
            {
                StreamReader aFile = File.OpenText(appendfile);
                string strOption, sub;
                int intsub;
                while (aFile.Peek() > -1)
                {
                    strOption = aFile.ReadLine();
                    sub = strOption.Substring(15, strOption.Length - 15);
                    intsub = Convert.ToInt32(sub);
                    switch (strOption.Substring(0, 15))
                    {
                        case "SizeMode=      "
                            appendsettings.sizemode = intsub;
                            break;
                        case "AspectMode=    "
                            appendsettings.aspectmode = intsub;
                            break;
                        case "Format=        "
                            appendsettings.format = intsub;
                            break;
                        case "AudioFormat=   "
                            appendsettings.audioformat = intsub;
                            break;
                        case "FPSMode=       "
                            appendsettings.fpsmode = intsub;
                            break;
                        case "BitrateMode=   "
                            appendsettings.bitratemode = intsub;
                            break;
                        case "Apply=         "
                            appendsettings.apply = sub.ToLower() == "true";
                            break;
                    }
                }
                while (aFile.Peek() > -1)
                {
                    strOption = aFile.ReadLine();
                    sub = strOption.Substring(15, strOption.Length - 15);
                    intsub = Convert.ToInt32(sub);
                    switch (strOption.Substring(0, 15))
                    {
                        case "SizeMode=      "
                            appendsettings.sizemode = intsub;
                            break;
                        case "AspectMode=    "
                            appendsettings.aspectmode = intsub;
                            break;
                        case "Format=        "
                            appendsettings.format = intsub;
                            break;
                        case "AudioFormat=   "
                            appendsettings.audioformat = intsub;
                            break;
                        case "FPSMode=       "
                            appendsettings.fpsmode = intsub;
                            break;
                        case "BitrateMode=   "
                            appendsettings.bitratemode = intsub;
                            break;
                        case "Apply=         "
                            appendsettings.apply = sub.ToLower() == "true";
                            break;
                    }
                }
                aFile.Close();
            }

            //Check for Updates
            string versionfile = appdir + "\\version.txt";
            if (File.Exists(versionfile))
            {
                StreamReader currentver = File.OpenText(versionfile);
                intversion = Convert.ToInt32(currentver.ReadLine());
                strversion = currentver.ReadLine();
                this.Title = "ASXGui " + strversion;
                currentver.Close();
                if (boolUpdate)
                    tmrUpdate.IsEnabled = true;
            }

            //Initialize Shell
            shell.StartInfo.WorkingDirectory = toolDir;
            shell.StartInfo.UseShellExecute = false;
            shell.StartInfo.CreateNoWindow = true;
            shell.StartInfo.FileName = "cmd.exe";

            //Load Profiles
            profileList = load_profiles();
            Update_Profiles();

            //Load previously saved jobs
            string jf = appdir + "\\jobs.bin";
            if (File.Exists(jf))
            {
                BinaryFormatter BF = new BinaryFormatter();
                byte[] bytes = File.ReadAllBytes(jf);
                videolist = (List<video>)BF.Deserialize(new MemoryStream(bytes));
                File.Delete(jf);
                Update_Jobs();
            }

            //Load append settings
            string af = appdir + "\\append.bin";
            if (File.Exists(af))
            {
                BinaryFormatter BF = new BinaryFormatter();
                byte[] bytes = File.ReadAllBytes(af);
                appendadvanced = (video)BF.Deserialize(new MemoryStream(bytes));
            }
        }


        void tmrUpdate_Tick(object sender, EventArgs e)
        {
            tmrUpdate.IsEnabled = false;

            //Run Updater in a separate thread
            Thread update = new Thread(updater);
            update.Start();
        }


        private void updater()
        {
            try
            {
                //Kill updater if open
                foreach (Process prog in Process.GetProcessesByName("updater"))
                {
                    prog.Kill();
                }
                foreach (Process prog in Process.GetProcessesByName("updater"))
                {
                    prog.Kill();
                }

                //Move temporary upgrade files
                shell.StartInfo.WorkingDirectory = appdir;
                if (Directory.Exists(appdir + "\\update"))
                {
                    shell.StartInfo.Arguments = " /c ECHO Y | del update\\*.* && rd update";
                    shell.Start();
                    shell.WaitForExit();
                }
                if (File.Exists(appdir + "\\GUI.ini"))
                    File.Delete(appdir + "\\GUI.ini");
                if (File.Exists(appdir + "\\temp\\GUI.ini"))
                    File.Delete(appdir + "\\temp\\GUI.ini");
                if (Directory.Exists(appdir + "\\temp"))
                {
                    shell.StartInfo.Arguments = " /c ECHO A | move temp\\*.* *.* && rd temp";
                    shell.Start();
                    shell.WaitForExit();
                }
                shell.StartInfo.WorkingDirectory = toolDir;

                //Check update version
                WebClient wc = new System.Net.WebClient();
                wc.DownloadFile("http
                StreamReader NV = File.OpenText(appdir + "\\update.txt");
                int intNV = Convert.ToInt32(NV.ReadLine());
                string strNV = NV.ReadLine();
                NV.Close();                
                if (intNV > intversion)
                {
                    setBlur(true);
                    if (MessageBox.Show("New version detected, would you like to download?\n\nNew Version
                    {
                        lWrite.Close();
                        Process.Start(appdir + "\\updater.exe");
                        Application.Current.Shutdown();
                    }
                    setBlur(false);
                }
            }
            catch (WebException)
            {
            }
        }


        void tmrBlur_Tick(object sender, EventArgs e)
        {
            BlurEffect effect = new BlurEffect();
            if (BlurOn) radius+=5; else radius-=5;
            if (radius>25) radius=25;
            if (radius<0) radius=0;
            effect.Radius=radius;
            this.Effect=effect;
            if (radius == 25 || radius == 0) tmrBlur.IsEnabled = false;
            this.IsEnabled = (radius != 25);
        }


        public static void setGlass(Window win)
        {
            // Get the window handle
            WindowInteropHelper helper = new WindowInteropHelper(win);
            HwndSource mainWindowSrc = (HwndSource)HwndSource.
                FromHwnd(helper.Handle);
            mainWindowSrc.CompositionTarget.BackgroundColor =
                Colors.Transparent;

            MARGINS margins = new MARGINS();

            margins.cxLeftWidth = (int)(win.ActualWidth);
            margins.cxRightWidth = (int)(win.ActualWidth);
            margins.cyBottomHeight = (int)(win.ActualHeight);
            margins.cyTopHeight = (int)(win.ActualHeight);
            

            int hr = DwmExtendFrameIntoClientArea(mainWindowSrc.Handle,
                        ref margins);
        }


