        private List<string> load_profiles()
        {
            List<string> profiles = new List<string>();
            string pf = appdir + "\\profiles.ini";
            noprofiles = false;
            //Check if profiles.ini exists
            if (!File.Exists(pf))
            {
                if (MessageBox.Show("ASXGui has determined that you don't have any profiles installed. Would you like to download the official ASXGui profiles?", "Download Profiles?", MessageBoxButton.YesNo) == MessageBoxResult.Yes)
                {
                    download_profiles(pf);
                }
                else
                {
                    MessageBox.Show("Select \"Download Official Profiles\" from the tools menu if you ever want to download the oficial profiles.");
                    noprofiles = true;
                    StreamWriter pw = File.CreateText(pf);
                    pw.Close();
                }
            }
            if (File.Exists(pf))
            {
                StreamReader dl = File.OpenText(pf);
                string data, profile = "";
                int tmpversion = intversion;
                while (dl.Peek() > -1)
                {
                    data = dl.ReadLine();
                    if (data.Length > 8)
                    {
                        if (data.Substring(0, 8) == "Profile=")
                        {
                            profile = data.Substring(8, data.Length - 8);
                            profiles.Add(profile);
                        }
                    }
                    if (data.Length > 15)
                    {
                        if (data.Substring(0, 15) == "Version=       ")
                        {
                            int pv = Convert.ToInt32(data.Substring(15, data.Length - 15));
                            if (pv < tmpversion)
                                tmpversion = pv;
                        }
                    }
                }
                while (dl.Peek() > -1)
                {
                    data = dl.ReadLine();
                    if (data.Length > 8)
                    {
                        if (data.Substring(0, 8) == "Profile=")
                        {
                            profile = data.Substring(8, data.Length - 8);
                            profiles.Add(profile);
                        }
                    }
                    if (data.Length > 15)
                    {
                        if (data.Substring(0, 15) == "Version=       ")
                        {
                            int pv = Convert.ToInt32(data.Substring(15, data.Length - 15));
                            if (pv < tmpversion)
                                tmpversion = pv;
                        }
                    }
                }
                dl.Close();

                //Check if profiles need updating            
                if (tmpversion < intversion)
                {
                    profileList = profiles;
                    profile_updater();
                }
            }
            profiles.Sort();
            
            return profiles;
        }


        private void profile_updater()
        {
            setBlur(true);
            Updating frmUpdating = new Updating();
            frmUpdating.ShowDialog();
            setBlur(false);
        }


        private void download_profiles(string pf)
        {
            try
            {
                System.Net.WebClient wc = new System.Net.WebClient();
                wc.DownloadFile("http
            }
            catch
            {
                MessageBox.Show("Error downloading profiles.\n\nChoose \"Download Official Profiles\" from the tools menu to try again.");
                noprofiles = true;
                StreamWriter pw = File.CreateText(pf);
                pw.Close();
            }
        }


        private void Update_Profiles()
        {
            cboSettings.Items.Clear();
            if (!noprofiles)
            {
                foreach (string prof in profileList)
                {
                    cboSettings.Items.Add(prof);
                }
                foreach (string prof in profileList)
                {
                    cboSettings.Items.Add(prof);
                }
            }
            cboSettings.Text = strDefaultProfile;
        }


        public static video Profile_Change(string name, video editvid)
        {
            editvid=Profile_Change(name, editvid, appdir + "\\profiles.ini");
            return editvid;
        }


        public static video Profile_Change(string name, video editvid, string pf)
        {
            editvid = MainForm.Set_defaults(editvid);
            string profile = "", data, sub;
            bool bsub;
            int isub;
            double dsub;
            StreamReader pFile = File.OpenText(pf);
            while (pFile.Peek() > -1)
            {
                data = pFile.ReadLine();
                if (data.Length > 8)
                {
                    if (data.Substring(0, 7) == "Profile")
                    {
                        profile = data.Substring(8, data.Length - 8);
                    }

                    if (data.Length > 15 && profile == name)
                    {

                        sub = data.Substring(15, data.Length - 15);
                        bsub = sub.ToLower() == "true";
                        int i;
                        double d;
                        isub = Int32.TryParse(sub, out i) ? i 
                        dsub = Double.TryParse(sub, out d) ? d 
                        switch (data.Substring(0, 15))
                        {
                            case "MP4=           "
                                editvid.MKV = !bsub;
                                break;
                            case "iPod=          "
                                editvid.iPod = bsub;
                                break;
                            case "Recompressed=  "
                                editvid.remuxaudio = bsub;
                                break;
                            case "1Pass=         "
                                editvid.passes = bsub ? 1 
                                break;
                            case "CQ=            "
                                editvid.CQ = bsub;
                                break;
                            case "Quant=         "
                                editvid.quality = isub;
                                break;
                            case "Quality=       "
                                editvid.quality = isub;
                                break;
                            case "AutoSize=      "
                                editvid.CustomSize = !bsub;
                                break;
                            case "Aspect=        "
                                editvid.UseAspect = bsub;
                                break;
                            case "Ratio=         "
                                switch (sub)
                                {
                                    case "16
                                        editvid.aspectRatio = 16 / 9;
                                        break;
                                    case "16/9"
                                        editvid.aspectRatio = 16 / 9;
                                        break;
                                    case "4
                                        editvid.aspectRatio = 4 / 3;
                                        break;
                                    case "4/3"
                                        editvid.aspectRatio = 4 / 3;
                                        break;
                                    default
                                        editvid.aspectRatio = dsub;
