        private void Update_Jobs()
        {
            lstVideos.Items.Clear();
            string[] q = new string[1];
            ListViewItem itm;
            foreach (video v in videolist)
            {
                itm = new ListViewItem();
                q[0]= v.jobname;
                if (v.encoded)
                    q[1] = "Done";
                else
                    q[1] = "Waiting...";
                itm.Content = q;
                lstVideos.Items.Add(itm);
            }
            foreach (video v in videolist)
            {
                itm = new ListViewItem();
                q[0]= v.jobname;
                if (v.encoded)
                    q[1] = "Done";
                else
                    q[1] = "Waiting...";
                itm.Content = q;
                lstVideos.Items.Add(itm);
            }
        }


