        }


        public static video videoinfo(video editvid)
        {
            string path = editvid.path;
            editvid.avs = path.Substring(path.Length - 3, 3).ToLower() == "avs";
            //Generate unique job key (Over 200 billion possibilities)
            if (editvid.key == "")
            {
                bool used = false;
                while (!used)
                {
                    editvid.key = randomstring(8);
                    used = false;
                    for (int i = 0; i < videolist.Count; i++)
                        if (videolist[i].key == editvid.key)
                            used = true;
                    for (int i = 0; i < videolist.Count; i++)
                        if (videolist[i].key == editvid.key)
                            used = true;
                }
                while (!used)
                {
                    editvid.key = randomstring(8);
                    used = false;
                    for (int i = 0; i < videolist.Count; i++)
                        if (videolist[i].key == editvid.key)
                            used = true;
                }
            }

            //Generate videoinfo file for current job
            if (editvid.avs)
            {
                editvid.avsaudio = 0;
                shell.StartInfo.Arguments = " /c ECHO y | avs2avi \"" + editvid.path + "\" \"" + tempDir + "\\avs2avi.avi\" && mediainfo \"" + tempDir + "\\avs2avi.avi\" \"--Language=file
                shell.Start();
                shell.WaitForExit();
            }
            else
            {
                shell.StartInfo.Arguments = " /c mediainfo \"" + editvid.path + "\" \"--Language=file
                shell.Start();
                shell.WaitForExit();
            }
            editvid.videoinfo = tempDir + "\\" + editvid.key + ".videoinfo";
            return editvid;
        }


        public static video Read_VideoInfo(video editvid)
        {
            if (File.Exists(editvid.videoinfo))
            {
                editvid.inputformat = "";
                editvid.audioformat = "";
                editvid.inputvbitrate = "";
                editvid.inputabitrate = "";
                editvid.inputHeight = 0;
                editvid.inputWidth = 0;
                editvid.inputChannels = 0;
                editvid.inputFPS = 0;
                editvid.inputfilesize = "";
                editvid.fieldorder = "";
                editvid.length = 0;
                editvid.inputAspect = 0;
                editvid.inputChannels = 0;
                editvid.trackcount = 0;
                editvid.videoID = -1;
                editvid.audioID = -1;

                //Read videoinfo file, display contents
                StreamReader vread = File.OpenText(editvid.videoinfo);
                int intAudio = -1;
                string strInfo;
                string evov = "", evoa = "";
                string sub;
                while (vread.Peek() > -1)
                {
                    strInfo = vread.ReadLine();
                    if (strInfo.Length > 4)
                    {
                        if (strInfo.Substring(0, 5) == "Video" && intAudio == -1)
                            intAudio = 0;
                        if (strInfo.Substring(0, 5) == "Audio" && intAudio == 0)
                            intAudio = 1;
                        if (strInfo.Length > 34)
                        {
                            int inum;
                            double dnum;
                            sub = strInfo.Substring(35, strInfo.Length - 35);
                            int isub = Int32.TryParse(sub, out inum) ? inum 
                            double dsub = Double.TryParse(sub, out dnum) ? dnum 
                            string psub = sub.Length > 6 ? sub.Substring(0, sub.Length - 6) 
                            int ipsub = Int32.TryParse(psub, out inum) ? inum 
                            switch (strInfo.Substring(0, 20))
                            {
                                case "Format              "
                                    if (!editvid.avs)
                                    {
                                        if (editvid.inputformat == "" || editvid.inputformat == null)
                                            editvid.inputformat = sub;
                                        switch (intAudio)
                                        {
                                            case 0
                                                evov = sub;
                                                editvid.inputformat = editvid.inputformat + " / " + sub;
                                                break;
                                            case 1
                                                if (editvid.audioformat == "" || editvid.audioformat == null)
                                                {
                                                    evoa = sub;
                                                    editvid.audioformat = sub;
                                                }
                                                break;
                                        }
                                    }
                                    else
                                        editvid.inputformat = "Avisynth Script";
                                    break;
                                case "Codec               "
                                    switch (intAudio)
                                    {
                                        case 0
                                            if (!editvid.avs)
                                                editvid.inputformat = editvid.inputformat + " / " + sub;
                                            break;
                                        case 1
                                            if (editvid.trackcount == 0)
                                                editvid.audioformat = sub;
                                            break;
                                    }
                                    break;
                                case "Bit rate            "
                                    switch (intAudio)
                                    {
                                        case 0
                                            editvid.inputvbitrate = sub;
                                            break;
                                        case 1
                                            if (editvid.trackcount == 0)
                                                editvid.inputabitrate = sub;
                                            break;
                                    }
                                    break;
                                case "Width               "
                                    editvid.inputWidth = ipsub;
                                    break;
                                case "Height              "
                                    editvid.inputHeight = ipsub;
                                    break;
                                case "Channel(s)          "
                                    if (editvid.trackcount == 0)
                                    {
                                        editvid.outputChannels = isub;
                                        editvid.inputChannels = isub;
                                    }
                                    break;
                                case "Display aspect ratio"
                                    editvid.inputAspect = 0;
                                    for (int i = 1; i < sub.Length; i++)
                                        if ((sub.Substring(i, 1) == "/" || sub.Substring(i, 1) == "
                                            editvid.inputAspect = Convert.ToDouble(sub.Substring(0, i)) / Convert.ToDouble(sub.Substring(i + 1, sub.Length - i - 1));
                                    for (int i = 1; i < sub.Length; i++)
                                        if ((sub.Substring(i, 1) == "/" || sub.Substring(i, 1) == "
                                            editvid.inputAspect = Convert.ToDouble(sub.Substring(0, i)) / Convert.ToDouble(sub.Substring(i + 1, sub.Length - i - 1));
                                    if (editvid.inputAspect == 0)
                                        editvid.inputAspect = dsub;
                                    if (!editvid.autocropped)
                                        editvid.autocropAspect = editvid.inputAspect;
                                    break;
                                case "Frame rate          "
                                    editvid.outputFPS = dsub;
                                    editvid.inputFPS = dsub;
                                    break;
                                case "Duration            "
                                    double hours = 0, minutes = 0, seconds = 0;
                                    int cursor = 0;
                                    if (intAudio == -1)
                                    {
                                        int i;
                                        for (i = 1; i < sub.Length; i++)
                                            if (sub.Substring(i, 1) == "h")
                                            {
                                                hours = Convert.ToDouble(sub.Substring(0, i));
                                                cursor = i + 1;
                                            }
                                        for (i = 1; i < sub.Length; i++)
                                            if (sub.Substring(i, 1) == "h")
                                            {
                                                hours = Convert.ToDouble(sub.Substring(0, i));
                                                cursor = i + 1;
                                            }
                                        for (i = cursor + 1; i < (sub.Length - 1); i++)
                                            if (sub.Substring(i, 2) == "mn")
                                            {
                                                minutes = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                                cursor = i + 3;
                                            }
                                        for (i = cursor + 1; i < (sub.Length - 1); i++)
                                            if (sub.Substring(i, 2) == "mn")
                                            {
                                                minutes = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                                cursor = i + 3;
                                            }
                                        for (i = cursor + 1; i < sub.Length; i++)
                                            if (sub.Substring(i, 1) == "s" && sub.Substring(i - 1, 2) != "ms")
                                            {
                                                seconds = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                                cursor = i + 1;
                                            }
                                        for (i = cursor + 1; i < sub.Length; i++)
                                            if (sub.Substring(i, 1) == "s" && sub.Substring(i - 1, 2) != "ms")
                                            {
                                                seconds = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                                cursor = i + 1;
                                            }
                                        editvid.length = hours * 3600 + minutes * 60 + seconds;
                                    }
                                    break;
                                case "File size           "
                                    editvid.inputfilesize = sub;
                                    break;
                                case "Scan type           "
                                    if (sub == "Progressive")
                                        editvid.isinterlaced = false;
                                    else
                                        editvid.isinterlaced = true;
                                    break;
                                case "Scan order          "
                                    editvid.isinterlaced = true;
                                    editvid.film = false;
                                    if (sub == "Bottom Field First")
                                        editvid.fieldorder = "BFF";
                                    else
                                    {
                                        editvid.fieldorder = "TFF";
                                        if (sub == "2
                                        {
                                            editvid.film = true;
                                            if (Math.Abs(editvid.inputFPS - 23.976) < 0.02)
                                                editvid.inputFPS = 29.97;
                                        }
                                    }
                                    break;
                                case "ID                  "
                                    string sd = sub;
                                    for (int i = 0; i < sd.Length; i++)
                                        if (sd.Substring(i, 1) == "(")
                                            sd = sd.Substring(0, i - 1);
                                    for (int i = 0; i < sd.Length; i++)
                                        if (sd.Substring(i, 1) == "(")
                                            sd = sd.Substring(0, i - 1);
                                    int id = Int32.TryParse(sd, out inum) ? inum 
                                    switch (intAudio)
                                    {
                                        case 0
                                            editvid.videoID = id;
                                            break;
                                        case 1
                                            if (editvid.trackcount == 0)
                                                editvid.audioID = id;
                                            break;
                                    }
                                    break;
                                case "Sampling rate       "
                                    if (editvid.trackcount == 0 && sub.Length > 4)
                                    {
                                        string sr = sub.Substring(0, sub.Length - 4);
                                        double srd = double.TryParse(sr, out dnum) ? dnum 
                                        editvid.inputsample = Convert.ToInt32(srd * 1000);
                                    }
                                    editvid.trackcount++;
                                    intAudio++;
                                    break;
                            }
                        }
                    }
                }
                while (vread.Peek() > -1)
                {
                    strInfo = vread.ReadLine();
                    if (strInfo.Length > 4)
                    {
                        if (strInfo.Substring(0, 5) == "Video" && intAudio == -1)
                            intAudio = 0;
                        if (strInfo.Substring(0, 5) == "Audio" && intAudio == 0)
                            intAudio = 1;
                        if (strInfo.Length > 34)
                        {
                            int inum;
                            double dnum;
                            sub = strInfo.Substring(35, strInfo.Length - 35);
                            int isub = Int32.TryParse(sub, out inum) ? inum 
                            double dsub = Double.TryParse(sub, out dnum) ? dnum 
                            string psub = sub.Length > 6 ? sub.Substring(0, sub.Length - 6) 
                            int ipsub = Int32.TryParse(psub, out inum) ? inum 
                            switch (strInfo.Substring(0, 20))
                            {
                                case "Format              "
                                    if (!editvid.avs)
                                    {
                                        if (editvid.inputformat == "" || editvid.inputformat == null)
                                            editvid.inputformat = sub;
                                        switch (intAudio)
                                        {
                                            case 0
                                                evov = sub;
                                                editvid.inputformat = editvid.inputformat + " / " + sub;
                                                break;
                                            case 1
                                                if (editvid.audioformat == "" || editvid.audioformat == null)
                                                {
                                                    evoa = sub;
                                                    editvid.audioformat = sub;
                                                }
                                                break;
                                        }
                                    }
                                    else
                                        editvid.inputformat = "Avisynth Script";
                                    break;
                                case "Codec               "
                                    switch (intAudio)
                                    {
                                        case 0
                                            if (!editvid.avs)
                                                editvid.inputformat = editvid.inputformat + " / " + sub;
                                            break;
                                        case 1
                                            if (editvid.trackcount == 0)
                                                editvid.audioformat = sub;
                                            break;
                                    }
                                    break;
                                case "Bit rate            "
                                    switch (intAudio)
                                    {
                                        case 0
                                            editvid.inputvbitrate = sub;
                                            break;
                                        case 1
                                            if (editvid.trackcount == 0)
                                                editvid.inputabitrate = sub;
                                            break;
                                    }
                                    break;
                                case "Width               "
                                    editvid.inputWidth = ipsub;
                                    break;
                                case "Height              "
                                    editvid.inputHeight = ipsub;
                                    break;
                                case "Channel(s)          "
                                    if (editvid.trackcount == 0)
                                    {
                                        editvid.outputChannels = isub;
                                        editvid.inputChannels = isub;
                                    }
                                    break;
                                case "Display aspect ratio"
                                    editvid.inputAspect = 0;
                                    for (int i = 1; i < sub.Length; i++)
                                        if ((sub.Substring(i, 1) == "/" || sub.Substring(i, 1) == "
                                            editvid.inputAspect = Convert.ToDouble(sub.Substring(0, i)) / Convert.ToDouble(sub.Substring(i + 1, sub.Length - i - 1));
                                    if (editvid.inputAspect == 0)
                                        editvid.inputAspect = dsub;
                                    if (!editvid.autocropped)
                                        editvid.autocropAspect = editvid.inputAspect;
                                    break;
                                case "Frame rate          "
                                    editvid.outputFPS = dsub;
                                    editvid.inputFPS = dsub;
                                    break;
                                case "Duration            "
                                    double hours = 0, minutes = 0, seconds = 0;
                                    int cursor = 0;
                                    if (intAudio == -1)
                                    {
                                        int i;
                                        for (i = 1; i < sub.Length; i++)
                                            if (sub.Substring(i, 1) == "h")
                                            {
                                                hours = Convert.ToDouble(sub.Substring(0, i));
                                                cursor = i + 1;
                                            }
                                        for (i = cursor + 1; i < (sub.Length - 1); i++)
                                            if (sub.Substring(i, 2) == "mn")
                                            {
                                                minutes = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                                cursor = i + 3;
                                            }
                                        for (i = cursor + 1; i < sub.Length; i++)
                                            if (sub.Substring(i, 1) == "s" && sub.Substring(i - 1, 2) != "ms")
                                            {
                                                seconds = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                                cursor = i + 1;
                                            }
                                        editvid.length = hours * 3600 + minutes * 60 + seconds;
                                    }
                                    break;
                                case "File size           "
                                    editvid.inputfilesize = sub;
                                    break;
                                case "Scan type           "
                                    if (sub == "Progressive")
                                        editvid.isinterlaced = false;
                                    else
                                        editvid.isinterlaced = true;
                                    break;
                                case "Scan order          "
                                    editvid.isinterlaced = true;
                                    editvid.film = false;
                                    if (sub == "Bottom Field First")
                                        editvid.fieldorder = "BFF";
                                    else
                                    {
                                        editvid.fieldorder = "TFF";
                                        if (sub == "2
                                        {
                                            editvid.film = true;
                                            if (Math.Abs(editvid.inputFPS - 23.976) < 0.02)
                                                editvid.inputFPS = 29.97;
                                        }
                                    }
                                    break;
                                case "ID                  "
                                    string sd = sub;
                                    for (int i = 0; i < sd.Length; i++)
                                        if (sd.Substring(i, 1) == "(")
                                            sd = sd.Substring(0, i - 1);
                                    int id = Int32.TryParse(sd, out inum) ? inum 
                                    switch (intAudio)
                                    {
                                        case 0
                                            editvid.videoID = id;
                                            break;
                                        case 1
                                            if (editvid.trackcount == 0)
                                                editvid.audioID = id;
                                            break;
                                    }
                                    break;
                                case "Sampling rate       "
                                    if (editvid.trackcount == 0 && sub.Length > 4)
                                    {
                                        string sr = sub.Substring(0, sub.Length - 4);
                                        double srd = double.TryParse(sr, out dnum) ? dnum 
                                        editvid.inputsample = Convert.ToInt32(srd * 1000);
                                    }
                                    editvid.trackcount++;
                                    intAudio++;
                                    break;
                            }
                        }
                    }
                }
                vread.Close();
                string path = editvid.path;
                int len = path.Length;

                string ext = len > 3 ? path.Substring(len - 3, 3).ToLower() 

                //Check if input file is EVO
                string format = editvid.inputformat;
                bool evo = (ext == "evo" && EVO_Check(format));
                if (evo)
                    switch (strEVO)
                    {
                        case "Always Ask"
                            if (MessageBox.Show("Source has been detected as EVO. Would you lie to remux to M2TS for better compatibility?", "Remux EVO?", MessageBoxButton.YesNo) == MessageBoxResult.Yes)
                                editvid = Remux_EVO(editvid, evov, evoa, editvid.videoID, editvid.audioID);
                            return editvid;
                        case "Always Convert to M2TS"
                            editvid = Remux_EVO(editvid, evov, evoa, editvid.videoID, editvid.audioID);
                            return editvid;
                    }

                //Check DGIndex & DGAVCIndex compatibility
                bool mpeg = MPEG_Check(format), avc = AVC_Check(format);
                string jname = editvid.jobname;
                if (mpeg && !File.Exists(editvid.mpeginfo) && jname.Length > 2)
                {
                    bool vob = jname.Substring(jname.Length - 3, 3).ToUpper() == "VOB";

                    if ((vob && preloadVOB) || (!vob && preloadMPG))
                        editvid = MPEGInfo(editvid);
                }
                if (avc && preloadAVC)
                    editvid = AVCInfo(editvid);


                //Check for interlace
                if (editvid.isinterlaced != editvid.Deinterlace)
                    if (editvid.isinterlaced)
                        switch (strDeinterlace)
                        {
                            case "Always Deinterlace"
                                editvid.interlaced = false;
                                editvid.Deinterlace = true;
                                break;
                            case "Never Deinterlace"
                                editvid.interlaced = true;
                                editvid.Deinterlace = false;
                                break;
                            default
                                if (MessageBox.Show("Interlace was detected, would you like to deinterlace?", "Deinterlace?", MessageBoxButton.YesNo) == MessageBoxResult.Yes)
                                {
                                    editvid.interlaced = false;
                                    editvid.Deinterlace = true;
                                }
                                else
                                {
                                    editvid.interlaced = true;
                                    editvid.Deinterlace = false;
                                }
                                break;
                        }
                    else
                        if (MessageBox.Show("Interlace wasn't detected, disable deinterlace? (recommended)", "Disable Deinterlace?", MessageBoxButton.YesNo) == MessageBoxResult.Yes)
                        {
                            editvid.Deinterlace = false;
                            editvid.interlaced = true;
                        }

                //Check for film content
                double fps = 24;
                switch (strFilmMode)
                {
                    case "Based on Input FPS"
                        if (Math.Abs(editvid.inputFPS - 29.97) < 0.02 || Math.Abs(editvid.inputFPS - 59.94) < 0.02)
                            fps = 23.976;
                        if (Math.Abs(editvid.inputFPS - 25) < 0.02 || Math.Abs(editvid.inputFPS - 50) < 0.02)
                            fps = 25;
                        break;
                    case "NTSC (23.976 FPS)"
                        fps = 23.976;
                        break;
                    case "PAL (25 FPS)"
                        fps = 25;
                        break;
                }

                if (editvid.film && !editvid.changefps)
                    switch (strFilm)
                    {
                        case "Always Ask"
                            if (editvid.percentfilm == "" || editvid.percentfilm == null)
                                editvid.percentfilm = " FILM";
                            if (MessageBox.Show("Source was detected as" + editvid.percentfilm.ToLower() + ", would you like the frame rate set at " + fps + " fps? (recommended)", "Convert FPS?", MessageBoxButton.YesNo) == MessageBoxResult.Yes)
                            {
                                editvid.changefps = true;
                                editvid.customFPS = fps;
                            }
                            break;
                        case "Always Convert FPS"
                            editvid.changefps = true;
                            editvid.customFPS = fps;
                            break;
                    }

                //Check sample rate
                if (!editvid.forcesample)
                {
                    List<int> rates = new List<int>();
                    double isa, min;
                    int rate;
                    isa = editvid.inputsample;
                    rates.Add(0);
                    rates.Add(8000);
                    rates.Add(11025);
                    rates.Add(16000);
                    rates.Add(22050);
                    rates.Add(24000);
                    rates.Add(32000);
                    rates.Add(44100);
                    rates.Add(48000);
                    rates.Add(88200);
                    rates.Add(96000);
                    bool common = false;
                    foreach (int i in rates)
                        if (i == isa)
                            common = true;
                    foreach (int i in rates)
                        if (i == isa)
                            common = true;
                    if (!common)
                    {
                        rate = 8000;
                        min = Math.Abs(isa - 8000);
                        foreach (int i in rates)
                            if (Math.Abs(isa - i) < min)
                            {
                                min = Math.Abs(isa - i);
                                rate = i;
                            }
                        foreach (int i in rates)
                            if (Math.Abs(isa - i) < min)
                            {
                                min = Math.Abs(isa - i);
                                rate = i;
                            }
                        MessageBoxResult result;
                        if (strSample == "Always Ask")
                            result = MessageBox.Show("The audio sample rate is not a common value (" + isa + " Hz), would you like to force it to the nearest compatible sample rate? (" + rate + " Hz)", "Change Sample Rate?", MessageBoxButton.YesNo);
                        else
                            result = (strSample == "Always Convert") ? MessageBoxResult.Yes 
                        if (result == MessageBoxResult.Yes)
                        {
                            editvid.forcesample = true;
                            editvid.outputsample = rate;
                        }
                    }
                }
            }

            return editvid;
        }


        public static bool EVO_Check(string format)
        {
            bool container = false, codec = false;
            int len = format.Length;
            if (len > 2)
            {
                if (format.Substring(len - 3, 3) == "AVC")
                    codec = true;
                if (len > 3)
                {
                    if (format.Substring(len - 4, 4) == "VC-1")
                        codec = true;
                    if (len > 6)
                        if (format.Substring(0, 7) == "MPEG-PS")
                            container = true;
                }
            }
            return container && codec;
        }


        public static video Remux_EVO(video editvid, string evov, string evoa, int vtrack, int atrack)
        {
            string em = tempDir + "\\evo.meta";
            if (File.Exists(em))
                File.Delete(em);
            StreamWriter mfile = File.CreateText(em);
            mfile.WriteLine("MUXOPT --no-pcr-on-video-pid --new-audio-pes --vbr  --vbv-len=500");
            string path = editvid.path;
            switch (evov)
            {
                case "AVC"
                    mfile.WriteLine("V_MPEG4/ISO/AVC, \"" + path + "\", track=" + vtrack);
                    break;
                case "VC-1"
                    mfile.WriteLine("V_MS/VFW/WVC1, \"" + path + "\" ,track=" + vtrack);
                    break;
                case "MPEG Video"
                    mfile.WriteLine("V_MPEG-2, \"" + path + "\" track=" + vtrack);
                    break;
            }
            switch (evoa)
            {
                case "AC-3"
                    mfile.WriteLine("A_AC3, \"" + path + "\", track=" + atrack);
                    break;
                case "DTS"
                    mfile.WriteLine("A_DTS, \"" + path + "\", track=" + atrack);
                    break;
                case "AAC"
                    mfile.WriteLine("A_AAC, \"" + path + "\", track=" + atrack);
                    break;
                case "MPEG Audio"
                    mfile.WriteLine("A_MP3, \"" + path + "\", track=" + atrack);
                    break;
                case "LPCM"
                    mfile.WriteLine("A_LPCM, \"" + path + "\", track=" + atrack);
                    break;
            }
            mfile.Close();
            shell.StartInfo.RedirectStandardOutput = true;
            shell.OutputDataReceived += new DataReceivedEventHandler(shell_OutputDataReceived);
            shell.StartInfo.Arguments = "/c tsmuxer \"" + em + "\" \"" + path + ".m2ts\"";
            shell.Start();
            shell.PriorityClass = ProcessPriorityClass.Idle;
            shell.BeginOutputReadLine();
            EVO frmEVO = new EVO();
            frmEVO.Show();
            shell.WaitForExit();
            shell.Close();
            shell.CancelOutputRead();
            tspercent = 100;
            shell.StartInfo.RedirectStandardOutput = false;
            File.Delete(em);
            editvid.path += ".m2ts";
            editvid.jobname += ".m2ts";
            editvid = videoinfo(editvid);
            editvid = Read_VideoInfo(editvid);
            return editvid;
        }


        public static bool MPEG_Check(string format)
        {
            int len = format.Length;
            bool container = false, codec = false;
            string sub = "";
            if (len > 3)
            {
                if (format.Substring(0, 4) == "BDAV")
                    container = true;
                if (len > 6)
                {
                    sub = format.Substring(0, 7);
                    if (sub == "MPEG-TS" || sub == "MPEG-PS")
                        container = true;
                    if (len > 8)
                    {
                        if (format.Substring(format.Length - 9, 9) == "DVD-Video")
                            codec = true;
                        if (len > 9)
                        {
                            if (format.Substring(format.Length - 10, 10) == "MPEG Video")
                                codec = true;
                            if (format.Substring(0, 10) == "MPEG Video")
                                container = true;
                        }
                    }
                }
            }
            return container && codec;
        }


        public static bool AVC_Check(string format)
        {
            bool avc = false;
            int len = format.Length;
            if (len > 8)
            {
                if (format == "AVC / AVC")
                    avc = true;
                if (len > 9)
                {
                    if (format == "BDAV / AVC")
                        avc = true;
                    if (len > 12)
                        if (format == "MPEG-TS / AVC" || format == "MPEG-PS / AVC")
                            avc = true;
                }
            }
            return avc;
        }


        public static video MPEGInfo(video editvid)
        {
            MPEG frmMPEG = new MPEG();
            string key = editvid.key;
            frmMPEG.Show();
            if (!File.Exists(editvid.mpeginfo) || editvid.mpeginfo == null)
            {
                shell.StartInfo.Arguments = "/c DGIndex -HIDE -EXIT -AT=[" + toolDir + "\\template.avs] -AIF=[" + editvid.path + "] -OF=[" + tempDir + "\\" + key + "]";
                shell.Start();
                shell.WaitForExit();
            }
            shell.StartInfo.WorkingDirectory = tempDir;
            shell.StartInfo.Arguments = " /c dir /b " + key + "*.ac3 " + key + "*.wav " + key + "*.dts " + key + "*.pcm " + key + "*.mp* " + key + "*.aac > " + key + ".trackinfo";
            shell.Start();
            shell.WaitForExit();
            shell.StartInfo.WorkingDirectory = toolDir;
            editvid.trackinfo = tempDir + "\\" + key + ".trackinfo";
            string avs = tempDir + "\\" + key + ".avs";
            if (File.Exists(avs))
            {
                StreamReader mread = File.OpenText(avs);
                editvid.mpgaudio = (mread.Peek() > -1) ? mread.ReadLine() 
                editvid.defaultmpgaudio = editvid.mpgaudio;
                string s = (mread.Peek() > -1) ? mread.ReadLine() 
                double d;
                editvid.delay = Double.TryParse(s, out d) ? d 
                editvid = AudioInfo(-1, editvid);
                //Read_AudioInfo();
                mread.Close();
                File.Delete(avs);
            }
            else
                MessageBox.Show("Error reading MPEG file!");
            StreamWriter awrite = File.CreateText(avs);
            editvid.mpeginfo = tempDir + "\\" + key + ".d2v";
            awrite.WriteLine(
                "LoadPlugin(\"" + toolDir + "\\DGDecode.dll\")\r\n" +
                "mpeg2source(\"" + editvid.mpeginfo + "\")");
            awrite.Close();
            string avi = tempDir + "\\" + key + ".avi", ml = tempDir + "\\" + key + ".mpeglength";
            shell.StartInfo.Arguments = " /c ECHO y | avs2avi \"" + avs + "\" \"" + avi + "\" && mediainfo \"" + avi + "\" > \"" + ml + "\"";
            shell.Start();
            shell.WaitForExit();
            File.Delete(avs);
            File.Delete(avi);
            if (File.Exists(ml))
            {
                StreamReader sread = File.OpenText(ml);
                string info = "";
                while (sread.Peek() > -1 && info != "Duration            ")
                {
                    info = sread.ReadLine();
                    if (info.Length > 35)
                    {
                        string sub = info.Substring(35, info.Length - 35);
                        if (info.Substring(0, 20) == "Duration            ")
                        {
                            double hours = 0, minutes = 0, seconds = 0;
                            int cursor = 0;
                            int i;
                            for (i = 1; i < sub.Length; i++)
                                if (sub.Substring(i, 1) == "h")
                                {
                                    hours = Convert.ToDouble(sub.Substring(0, i));
                                    cursor = i + 1;
                                }
                            for (i = 1; i < sub.Length; i++)
                                if (sub.Substring(i, 1) == "h")
                                {
                                    hours = Convert.ToDouble(sub.Substring(0, i));
                                    cursor = i + 1;
                                }
                            for (i = cursor + 1; i < (sub.Length - 1); i++)
                                if (sub.Substring(i, 2) == "mn")
                                {
                                    minutes = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                    cursor = i + 3;
                                }
                            for (i = cursor + 1; i < (sub.Length - 1); i++)
                                if (sub.Substring(i, 2) == "mn")
                                {
                                    minutes = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                    cursor = i + 3;
                                }
                            for (i = cursor + 1; i < sub.Length; i++)
                                if (sub.Substring(i, 1) == "s" && sub.Substring(i - 1, 2) != "ms")
                                {
                                    seconds = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                    cursor = i + 1;
                                }
                            for (i = cursor + 1; i < sub.Length; i++)
                                if (sub.Substring(i, 1) == "s" && sub.Substring(i - 1, 2) != "ms")
                                {
                                    seconds = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                    cursor = i + 1;
                                }
                            editvid.length = hours * 3600 + minutes * 60 + seconds;
                            editvid.mpeglength = editvid.length;
                        }
                    }
                }
                while (sread.Peek() > -1 && info != "Duration            ")
                {
                    info = sread.ReadLine();
                    if (info.Length > 35)
                    {
                        string sub = info.Substring(35, info.Length - 35);
                        if (info.Substring(0, 20) == "Duration            ")
                        {
                            double hours = 0, minutes = 0, seconds = 0;
                            int cursor = 0;
                            int i;
                            for (i = 1; i < sub.Length; i++)
                                if (sub.Substring(i, 1) == "h")
                                {
                                    hours = Convert.ToDouble(sub.Substring(0, i));
                                    cursor = i + 1;
                                }
                            for (i = cursor + 1; i < (sub.Length - 1); i++)
                                if (sub.Substring(i, 2) == "mn")
                                {
                                    minutes = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                    cursor = i + 3;
                                }
                            for (i = cursor + 1; i < sub.Length; i++)
                                if (sub.Substring(i, 1) == "s" && sub.Substring(i - 1, 2) != "ms")
                                {
                                    seconds = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                    cursor = i + 1;
                                }
                            editvid.length = hours * 3600 + minutes * 60 + seconds;
                            editvid.mpeglength = editvid.length;
                        }
                    }
                }
                sread.Close();
                File.Delete(ml);
            }
            if (!editvid.film && File.Exists(editvid.mpeginfo))
            {
                StreamReader dfile = File.OpenText(editvid.mpeginfo);
                while (dfile.Peek() > -1)
                {
                    string d2v = dfile.ReadLine();
                    if (d2v.Length > 8)
                        if (d2v.Substring(0, 8) == "FINISHED" && d2v.Substring(d2v.Length - 4, 4) == "FILM")
                        {
                            editvid.film = true;
                            editvid.percentfilm = d2v.Substring(8, d2v.Length - 8);
                            editvid.isinterlaced = true;
                        }
                }
                while (dfile.Peek() > -1)
                {
                    string d2v = dfile.ReadLine();
                    if (d2v.Length > 8)
                        if (d2v.Substring(0, 8) == "FINISHED" && d2v.Substring(d2v.Length - 4, 4) == "FILM")
                        {
                            editvid.film = true;
                            editvid.percentfilm = d2v.Substring(8, d2v.Length - 8);
                            editvid.isinterlaced = true;
                        }
                }
                dfile.Close();
            }

            frmMPEG.Close();
            return editvid;
        }


        public static video AVCInfo(video editvid)
        {
            AVC frmAVC = new AVC();
            string key = editvid.key;
            frmAVC.Show();
            if (!File.Exists(editvid.avcinfo) || editvid.avcinfo == null)
            {
                shell.StartInfo.Arguments = "/c DGAVCIndex.exe -h -e -i \"" + editvid.path + "\" -o \"" + tempDir + "\\" + key + ".dga\" -a";
                shell.Start();
                shell.WaitForExit();
            }
            shell.StartInfo.WorkingDirectory = tempDir;
            shell.StartInfo.Arguments = " /c dir /b " + key + "*.ac3 " + key + "*.wav " + key + "*.dts " + key + "*.pcm " + key + "*.mp* " + key + "*.aac > " + key + ".trackinfo";
            shell.Start();
            shell.WaitForExit();
            shell.StartInfo.WorkingDirectory = toolDir;
            editvid.trackinfo = tempDir + "\\" + key + ".trackinfo";
            StreamReader ti = File.OpenText(editvid.trackinfo);
            editvid.mpgaudio = "";
            if (ti.Peek() > -1)
                editvid.mpgaudio = ti.ReadLine();
            editvid.defaultmpgaudio = editvid.mpgaudio;
            ti.Close();
            editvid = AudioInfo(-1, editvid);
            //Read_AudioInfo();
            if (!editvid.film && File.Exists(editvid.avcinfo))
            {
                StreamReader dfile = File.OpenText(editvid.avcinfo);
                while (dfile.Peek() > -1)
                {
                    string avc = dfile.ReadLine();
                    if (avc.Length > 8)
                        if (avc.Substring(0, 8) == "FINISHED" && avc.Substring(avc.Length - 4, 4) == "FILM")
                        {
                            editvid.film = true;
                            editvid.percentfilm = avc.Substring(8, avc.Length - 8);
                            editvid.isinterlaced = true;
                        }
                }
                while (dfile.Peek() > -1)
                {
                    string avc = dfile.ReadLine();
                    if (avc.Length > 8)
                        if (avc.Substring(0, 8) == "FINISHED" && avc.Substring(avc.Length - 4, 4) == "FILM")
                        {
                            editvid.film = true;
                            editvid.percentfilm = avc.Substring(8, avc.Length - 8);
                            editvid.isinterlaced = true;
                        }
                }
                dfile.Close();
            }

            frmAVC.Close();
            return editvid;
        }


        static void shell_OutputDataReceived(object sender, DataReceivedEventArgs e)
        {
            string progress = e.Data;
            if (progress != null)
            {
                int len = progress.Length;
                if (len >= 10)
                {
                    string sub = progress.Substring(0, len - 10);
                    double d = new double();
                    if (progress.Substring(len - 10, 10) == "% complete")
                        tspercent = Double.TryParse(sub, out d) ? d 
                }
            }
        }


        public static video AudioInfo(int track, video editvid)
        {
            edittracklist = editvid.tracks;
            string file;
            if (track > -1)
                file = edittracklist[track].path;
            else
                file = editvid.mpgaudio;

            if (file.Substring(file.Length - 3, 3).ToLower() == "avs")
                editvid = AVSAudio(editvid, track);
            else
            {
                shell.StartInfo.Arguments = "/c mediainfo \"" + file + "\" \"--Language=file
                shell.Start();
                shell.WaitForExit();
            }
            editvid.audioinfo = tempDir + "\\" + editvid.key + ".audioinfo";
            return editvid;
        }


        public static video AVSAudio(video editvid, int track)
        {
            edittracklist = editvid.tracks;
            string ai = tempDir + "\\" + editvid.key + ".audioinfo";
            AVS2WAV frmAVS = new AVS2WAV();
            frmAVS.Show();
            StreamWriter atest = File.CreateText(tempDir + "\\atest.avs");
            atest.WriteLine(
                "Import(\"" + editvid.path + "\")" + "\r\n" +
                "Trim(0,30)");
            atest.Close();
            shell.StartInfo.Arguments = "/c test \"" + tempDir + "\\atest.avs\" > \"" + tempDir + "\\atest.txt\"";
            shell.Start();
            shell.WaitForExit();
            StreamReader aread = File.OpenText(tempDir + "\\atest.txt");
            bool err = false;
            string strerr = "";
            while (aread.Peek() > -1 && !err)
            {
                strerr = aread.ReadLine();
                if (strerr.Length > 0)
                    if (strerr.Length > 6)
                        err = strerr.Substring(0, 7) != "Reading";
                    else
                        err = true;
            }
            while (aread.Peek() > -1 && !err)
            {
                strerr = aread.ReadLine();
                if (strerr.Length > 0)
                    if (strerr.Length > 6)
                        err = strerr.Substring(0, 7) != "Reading";
                    else
                        err = true;
            }
            if (err)
                while (aread.Peek() > -1)
                    strerr = strerr + "\r\n" + aread.ReadLine();
                while (aread.Peek() > -1)
                    strerr = strerr + "\r\n" + aread.ReadLine();
            else
            {
                shell.StartInfo.Arguments = "/c avs2wav.exe \"" + editvid.path + "\" \"" + tempDir + "\\audio.wav\" && mediainfo \"" + tempDir + "\\audio.wav\" \"--Language=file
                shell.Start();
                shell.WaitForExit();
            }
            aread.Close();
            if (File.Exists(tempDir + "\\audio.wav"))
            {
                editvid.audio = editvid.path;
                editvid.avsaudio = 1;
                editvid.separatefile = true;
                track newtrack = new track();
                newtrack.path = editvid.path;
                newtrack.delay = 0;
                newtrack.downmix = editvid.Downmix;
                newtrack.custombitrate = editvid.CustomABitrate;
                newtrack.bitrate = editvid.aBitrate;
                newtrack.directshow = editvid.directshow;
                newtrack.AACMode = editvid.AACMode;
                edittracklist[track] = newtrack;
                editvid.tracks = edittracklist;
            }
            else
            {
                editvid.avsaudio = 2;
                MessageBox.Show("No audio stream detected in AVS Script");
            }
            frmAVS.Close();
            if (editvid.avsaudio == 1)
                editvid.audioinfo = ai;
            else
                editvid.audioinfo = "";
            return editvid;
        }


        public static video Read_AudioInfo(video editvid, int track)
        {
            edittracklist = editvid.tracks;
            track newtrack;
            if (track > -1)
                newtrack = edittracklist[track];
            else
            {
                newtrack = new track();
                newtrack.forcesample = editvid.forcesample;
                newtrack.outputsample = editvid.outputsample;
            }
            if (File.Exists(editvid.audioinfo))
            {
                newtrack.format = "";
                newtrack.inputbitrate = "";
                newtrack.inputchannels = 0;
                newtrack.trackcount = 0;

                //Read audio file, display contents
                StreamReader aread = File.OpenText(editvid.audioinfo);
                int intAudio = -1;
                string strInfo;
                string sub;
                while (aread.Peek() > -1)
                {
                    strInfo = aread.ReadLine();
                    if (strInfo.Length > 4)
                    {
                        if (strInfo.Substring(0, 5) == "Video" && intAudio == -1)
                            intAudio = 0;
                        if (strInfo.Substring(0, 5) == "Audio" && intAudio == 0)
                            intAudio = 1;
                        if (strInfo.Length > 34)
                        {
                            int inum;
                            double dnum;
                            sub = strInfo.Substring(35, strInfo.Length - 35);
                            int isub = Int32.TryParse(sub, out inum) ? inum 
                            double dsub = Double.TryParse(sub, out dnum) ? dnum 
                            string psub = sub.Length > 6 ? sub.Substring(0, sub.Length - 6) 
                            int ipsub = Int32.TryParse(psub, out inum) ? inum 
                            switch (strInfo.Substring(0, 20))
                            {
                                case "Format              "
                                    if (editvid.avsaudio != 1)
                                    {
                                        if (intAudio == 1)
                                            if (newtrack.format == "")
                                                newtrack.format = sub;
                                    }
                                    else
                                        newtrack.format = "Avisynth Script";
                                    break;
                                case "Codec               "
                                    if (intAudio == 1)
                                        if (newtrack.trackcount == 0)
                                            newtrack.format = sub;
                                    break;
                                case "Bit rate            "
                                    if (intAudio == 1)
                                        if (newtrack.trackcount == 0)
                                            newtrack.inputbitrate = sub;
                                    break;
                                case "Channel(s)          "
                                    if (newtrack.trackcount == 0)
                                    {
                                        newtrack.outputchannels = isub;
                                        newtrack.inputchannels = isub;
                                    }
                                    break;
                                case "Sampling rate       "
                                    if (newtrack.trackcount == 0)
                                        newtrack.inputsample = isub * 1000;
                                    newtrack.trackcount++;
                                    intAudio++;
                                    break;
                            }
                        }
                    }
                }
                while (aread.Peek() > -1)
                {
                    strInfo = aread.ReadLine();
                    if (strInfo.Length > 4)
                    {
                        if (strInfo.Substring(0, 5) == "Video" && intAudio == -1)
                            intAudio = 0;
                        if (strInfo.Substring(0, 5) == "Audio" && intAudio == 0)
                            intAudio = 1;
                        if (strInfo.Length > 34)
                        {
                            int inum;
                            double dnum;
                            sub = strInfo.Substring(35, strInfo.Length - 35);
                            int isub = Int32.TryParse(sub, out inum) ? inum 
                            double dsub = Double.TryParse(sub, out dnum) ? dnum 
                            string psub = sub.Length > 6 ? sub.Substring(0, sub.Length - 6) 
                            int ipsub = Int32.TryParse(psub, out inum) ? inum 
                            switch (strInfo.Substring(0, 20))
                            {
                                case "Format              "
                                    if (editvid.avsaudio != 1)
                                    {
                                        if (intAudio == 1)
                                            if (newtrack.format == "")
                                                newtrack.format = sub;
                                    }
                                    else
                                        newtrack.format = "Avisynth Script";
                                    break;
                                case "Codec               "
                                    if (intAudio == 1)
                                        if (newtrack.trackcount == 0)
                                            newtrack.format = sub;
                                    break;
                                case "Bit rate            "
                                    if (intAudio == 1)
                                        if (newtrack.trackcount == 0)
                                            newtrack.inputbitrate = sub;
                                    break;
                                case "Channel(s)          "
                                    if (newtrack.trackcount == 0)
                                    {
                                        newtrack.outputchannels = isub;
                                        newtrack.inputchannels = isub;
                                    }
                                    break;
                                case "Sampling rate       "
                                    if (newtrack.trackcount == 0)
                                        newtrack.inputsample = isub * 1000;
                                    newtrack.trackcount++;
                                    intAudio++;
                                    break;
                            }
                        }
                    }
                }
                aread.Close();

                if (!newtrack.forcesample)
                {
                    List<int> rates = new List<int>();
                    double isa, min;
                    int rate;
                    isa = newtrack.inputsample;
                    rates.Add(0);
                    rates.Add(8000);
                    rates.Add(11025);
                    rates.Add(16000);
                    rates.Add(22050);
                    rates.Add(24000);
                    rates.Add(32000);
                    rates.Add(44100);
                    rates.Add(48000);
                    rates.Add(88200);
                    rates.Add(96000);
                    bool common = false;
                    foreach (int i in rates)
                        if (i == isa)
                            common = true;
                    foreach (int i in rates)
                        if (i == isa)
                            common = true;
                    if (!common)
                    {
                        rate = 8000;
                        min = Math.Abs(isa - 8000);
                        foreach (int i in rates)
                            if (Math.Abs(isa - i) < min)
                            {
                                min = Math.Abs(isa - i);
                                rate = i;
                            }
                        foreach (int i in rates)
                            if (Math.Abs(isa - i) < min)
                            {
                                min = Math.Abs(isa - i);
                                rate = i;
                            }
                        MessageBoxResult result;
                        if (strSample == "Always Ask")
                            result = MessageBox.Show("The audio sample rate is not a common value (" + isa + " Hz), would you like to force it to the nearest compatible sample rate? (" + rate + " Hz)", "Change Sample Rate?", MessageBoxButton.YesNo);
                        else
                            result = (strSample == "Always Convert") ? MessageBoxResult.Yes 
                        if (result == MessageBoxResult.Yes)
                        {
                            newtrack.forcesample = true;
                            newtrack.outputsample = rate;
                        }
                    }
                }
            }
            if (track > -1)
            {
                edittracklist[track] = newtrack;
                editvid.tracks = edittracklist;
            }
            else
            {
                editvid.audioinfo = newtrack.format;
                editvid.inputabitrate = newtrack.inputbitrate;
                editvid.inputChannels = newtrack.inputchannels;
                editvid.outputChannels = newtrack.outputchannels;
                editvid.inputsample = newtrack.inputsample;
                editvid.forcesample = newtrack.forcesample;
                editvid.outputsample = newtrack.outputsample;
                editvid.trackcount = newtrack.trackcount;
            }
            return editvid;
        }


        public static video Calculate_Output(video editvid, int currenttrack)
        {
            edittracklist = editvid.tracks;
            double d;
            //Calculate Audio Results
            editvid.totalABitrate = 0;
            if (!editvid.NoAudio)
                if (editvid.separatefile)
                    for (int i = 0; i < edittracklist.Count; i++)
                    {
                        MainForm.track tmptrack = edittracklist[i];
                        if (tmptrack.inputchannels == 0) tmptrack.inputchannels = 2;
                        tmptrack.outputchannels = (tmptrack.inputchannels > 2 && tmptrack.downmix) ? 2 
                        if (!tmptrack.custombitrate)
                        {
                            //Calculate Bitrate
                            int osample = (tmptrack.forcesample) ? tmptrack.outputsample 
                            double c = (!editvid.MKV || (editvid.MKV && !editvid.bluray && editvid.AAC)) ? ((tmptrack.AACMode != 1) ? 38.0546 
                            int r = (c == 115.512) ? 32 
                            tmptrack.outputbitrate = (int)Math.Round(Math.Pow((double)tmptrack.inputchannels * ((double)osample / 48000), 0.75) * c / (double)r + 0.001, 0) * r;
                            if (tmptrack.outputbitrate == 0) tmptrack.outputbitrate = r;
                        }
                        else tmptrack.outputbitrate = tmptrack.bitrate;
                        if (tmptrack.remux && editvid.MKV)
                        {
                            //Determine bitrate of remuxed track
                            tmptrack.outputchannels = tmptrack.inputchannels;
                            string ib = tmptrack.inputbitrate;
                            if (ib == "" || ib == null)
                            {
                                editvid = MainForm.AudioInfo(i, editvid);
                                editvid = MainForm.Read_AudioInfo(editvid, i);
                            }
                            double ob = double.TryParse(ib.Substring(0, ib.Length - 5), out d) ? d 
                            if (ib.Substring(ib.Length - 4, 4) == "Mbps") ob *= 1000;
                            tmptrack.outputbitrate = Convert.ToInt32(ob);
                        }
                        editvid.totalABitrate += tmptrack.outputbitrate;
                        edittracklist[i] = tmptrack;
                    }
                    for (int i = 0; i < edittracklist.Count; i++)
                    {
                        MainForm.track tmptrack = edittracklist[i];
                        if (tmptrack.inputchannels == 0) tmptrack.inputchannels = 2;
                        tmptrack.outputchannels = (tmptrack.inputchannels > 2 && tmptrack.downmix) ? 2 
                        if (!tmptrack.custombitrate)
                        {
                            //Calculate Bitrate
                            int osample = (tmptrack.forcesample) ? tmptrack.outputsample 
                            double c = (!editvid.MKV || (editvid.MKV && !editvid.bluray && editvid.AAC)) ? ((tmptrack.AACMode != 1) ? 38.0546 
                            int r = (c == 115.512) ? 32 
                            tmptrack.outputbitrate = (int)Math.Round(Math.Pow((double)tmptrack.inputchannels * ((double)osample / 48000), 0.75) * c / (double)r + 0.001, 0) * r;
                            if (tmptrack.outputbitrate == 0) tmptrack.outputbitrate = r;
                        }
                        else tmptrack.outputbitrate = tmptrack.bitrate;
                        if (tmptrack.remux && editvid.MKV)
                        {
                            //Determine bitrate of remuxed track
                            tmptrack.outputchannels = tmptrack.inputchannels;
                            string ib = tmptrack.inputbitrate;
                            if (ib == "" || ib == null)
                            {
                                editvid = MainForm.AudioInfo(i, editvid);
                                editvid = MainForm.Read_AudioInfo(editvid, i);
                            }
                            double ob = double.TryParse(ib.Substring(0, ib.Length - 5), out d) ? d 
                            if (ib.Substring(ib.Length - 4, 4) == "Mbps") ob *= 1000;
                            tmptrack.outputbitrate = Convert.ToInt32(ob);
                        }
                        editvid.totalABitrate += tmptrack.outputbitrate;
                        edittracklist[i] = tmptrack;
                    }
                else
                {
                    if (editvid.inputChannels == 0) editvid.inputChannels = 2;
                    editvid.outputChannels = (editvid.Downmix && editvid.inputChannels > 2) ? 2 
                    if (!editvid.CustomABitrate)
                    {
                        //Calculate Bitrate
                        int osample = (editvid.forcesample) ? editvid.outputsample 
                        double c = (!editvid.MKV || (editvid.MKV && !editvid.bluray && editvid.AAC)) ? ((editvid.AACMode != 1) ? 38.0546 
                        int r = (c == 115.512) ? 32 
                        editvid.outputABitrate = (int)Math.Round(Math.Pow((double)editvid.inputChannels * ((double)osample / 48000), 0.75) * c / (double)r + 0.001, 0) * r;
                        if (editvid.outputABitrate == 0) editvid.outputABitrate = r;
                    }
                    else editvid.outputABitrate = editvid.aBitrate;
                    if (editvid.remuxaudio && editvid.MKV)
                    {
                        editvid.outputChannels = editvid.inputChannels;
                        //Determine bitrate of remuxed track
                        string ib = editvid.inputabitrate;
                        if (ib == "" || ib == null)
                        {
                            ib = "0";
                            editvid.inputabitrate = "0";
                        }
                        double ob = double.TryParse(ib.Substring(0, ib.Length - 5), out d) ? d 
                        if (ib.Substring(ib.Length - 4, 4) == "Mbps") ob *= 1000;
                        editvid.outputABitrate = Convert.ToInt32(ob);
                    }
                    editvid.totalABitrate = editvid.outputABitrate;
                }


            //Autocrop if needed
            if (editvid.autocrop && !editvid.autocropped) editvid = Autocrop(editvid);

            //Determine Aspect Ratio
            if (editvid.UseAspect) editvid.outputAspect = editvid.aspectRatio; else editvid.outputAspect = (editvid.autocrop) ? editvid.autocropAspect 
            if (editvid.MaxSize && editvid.blackbars && editvid.UseOAspect) editvid.outputAspect *= ((double)editvid.maxX / (double)editvid.maxY) / editvid.oAspect;

            //Determine Scaled Resolution
            int tmpx, tmpy;
            if (editvid.autocrop)
            {
                tmpx = editvid.autocroppedWidth;
                tmpy = editvid.autocroppedHeight;
            }
            else
            {
                tmpx = editvid.inputWidth;
                tmpy = editvid.inputHeight;
            }
            if (editvid.CustomCrop)
            {
                tmpx -= editvid.cropL - editvid.cropR;
                tmpy -= editvid.cropT - editvid.cropB;
                if (!editvid.UseAspect && !(editvid.MaxSize && editvid.blackbars && editvid.UseOAspect))
                    editvid.outputAspect = editvid.inputAspect / (double)editvid.inputWidth / (double)editvid.inputHeight * (double)tmpx / (double)tmpy;
            }
            if (!editvid.NoResize)
                if ((editvid.outputAspect * tmpy) > tmpx)
                {
                    editvid.scaledWidth = Convert.ToInt32(editvid.outputAspect * (double)tmpy);
                    editvid.scaledHeight = tmpy;
                }
                else
                {
                    editvid.scaledHeight = Convert.ToInt32((double)tmpx / editvid.outputAspect);
                    editvid.scaledWidth = tmpx;
                }
            else
            {
                editvid.scaledWidth = tmpx;
                editvid.scaledHeight = tmpy;
            }

            //Determine Maximum Width & Height
            if (editvid.MaxSize)
                if (editvid.upscale)
                {
                    editvid.maxWidth = editvid.maxX;
                    editvid.maxHeight = editvid.maxY;
                }
                else
                {
                    editvid.maxWidth = Math.Min(editvid.maxX, editvid.scaledWidth);
                    editvid.maxHeight = Math.Min(editvid.maxY, editvid.scaledHeight);
                }
            else
            {
                editvid.maxWidth = editvid.scaledWidth;
                editvid.maxHeight = editvid.scaledHeight;
            }


            editvid.tracks = edittracklist;
            editvid.calculated = true;
            return editvid;
        }


        public static video Autocrop(video editvid)
        {
            Crop frmCrop = new Crop();
            frmCrop.Show();
            string format = editvid.inputformat;
            if (!File.Exists(editvid.inputAVS))
                editvid = inputavs(editvid, ffmpeg_check(format), MPEG_Check(format), AVC_Check(format));
            editvid.cropAVS = tempDir + "\\" + editvid.key + "-crop.avs";
            StreamWriter cFile = new StreamWriter(new FileStream(editvid.cropAVS, FileMode.Create), Encoding.Default);
            cFile.WriteLine("LoadPlugin(\"" + toolDir + "\\autocrop.dll\")");
            cFile.WriteLine("Import(\"" + editvid.inputAVS + "\")");
            cFile.WriteLine("Autocrop(0)");
            cFile.Close();

            shell.StartInfo.Arguments = " /c ECHO y | avs2avi \"" + editvid.cropAVS + "\" \"" + editvid.cropAVS + ".avi\" && mediainfo \"" + editvid.cropAVS + ".avi\" \"--Language=file
            shell.Start();
            shell.WaitForExit();

            if (File.Exists(editvid.cropAVS)) File.Delete(editvid.cropAVS);
            if (File.Exists(editvid.cropAVS + ".avi")) File.Delete(editvid.cropAVS + ".avi");
            video tmpvideo = editvid;
            tmpvideo.videoinfo = editvid.cropAVS + ".cropinfo";
            tmpvideo = Read_VideoInfo(tmpvideo);
            if (File.Exists(editvid.cropAVS + ".cropinfo")) File.Delete(editvid.cropAVS + ".cropinfo");
            editvid.autocroppedWidth = tmpvideo.inputWidth;
            editvid.autocroppedHeight = tmpvideo.inputHeight;
            editvid.autocropAspect = tmpvideo.inputAspect;
            double iaspect = (double)editvid.inputWidth / (double)editvid.inputHeight;
            if (Math.Abs(iaspect - 4 / 3) < 0.05) iaspect = 4 / 3;
            if (Math.Abs(iaspect - 1.85) < 0.05) iaspect = 1.85;
            if (Math.Abs(iaspect - 16 / 9) < 0.05) iaspect = 16 / 9;
            if (Math.Abs(iaspect - 2.39) < 0.05) iaspect = 2.39;

            if (iaspect != editvid.inputAspect)
            {
                double oaspect = (editvid.inputAspect / iaspect) * editvid.autocropAspect;
                if (Math.Abs(oaspect - 4 / 3) < 0.05) oaspect = 4 / 3;
                if (Math.Abs(oaspect - 1.85) < 0.05) oaspect = 1.85;
                if (Math.Abs(oaspect - 16 / 9) < 0.05) oaspect = 16 / 9;
                if (Math.Abs(oaspect - 2.39) < 0.05) oaspect = 2.39;
                editvid.autocropAspect = oaspect;
            }

            frmCrop.Close();
            editvid.autocropped = true;
            return editvid;
        }


        public static video inputavs(video editvid, bool ffmpeg, bool mpeg, bool avc)
        {
            editvid.inputAVS = tempDir + "\\" + editvid.key + "-input.avs";
            StreamWriter ia = File.CreateText(editvid.inputAVS);
            if (ffmpeg)
            {
                ia.WriteLine("LoadPlugin(\"" + toolDir + "\\FFMS2.dll\")");
                ia.WriteLine("a=ffaudiosource(\"" + editvid.path + "\")");
                ia.WriteLine("v=ffvideosource(\"" + editvid.path + "\")");                
                ia.WriteLine("audiodub(v,a)");
            }
            if (mpeg)
            {
                if (File.Exists(editvid.mpeginfo))
                {
                    ia.WriteLine("LoadPlugin(\"" + toolDir + "\\DGDecode.dll\")");
                    ia.WriteLine("v=mpeg2source(\"" + editvid.mpeginfo + "\")");
                    if (File.Exists(editvid.mpgaudio))
                        ia.WriteLine("a=directshowsource(\"" + editvid.mpgaudio + "\")");
                    else
                        ia.WriteLine("a=directshowsource(\"" + editvid.path + "\")");
                    ia.WriteLine("audiodub(v,a)");
                    if (editvid.delay != 0)
                        ia.WriteLine("DelayAudio(" + editvid.delay + ")");
                }
                else
                    ia.WriteLine("Directshowsource(\"" + editvid.path + "\")");
            }
            if (avc)
            {
                if (File.Exists(editvid.avcinfo))
                {
                    ia.WriteLine("LoadPlugin(\"" + toolDir + "\\DGAVCDecode.dll\")");
                    ia.WriteLine("v=avcsource(\"" + editvid.avcinfo + "\")");
                    if (File.Exists(editvid.mpgaudio))
                        ia.WriteLine("a=directshowsource(\"" + editvid.mpgaudio + "\")");
                    else
                        ia.WriteLine("a=directshowsource(\"" + editvid.path + "\")");
                    ia.WriteLine("audiodub(v,a)");
                    if (editvid.delay != 0)
                        ia.WriteLine("DelayAudio(" + editvid.delay + ")");
                }
                else
                    ia.WriteLine("Directshowsource(\"" + editvid.path + "\")");
            }
            if (!ffmpeg && !avc && !mpeg)
                ia.WriteLine("Directshowsource(\"" + editvid.path + "\")");
            double scalex, scaley;
            double ix = (double)editvid.inputWidth, iy = (double)editvid.inputHeight;
            double aspect = ix / iy, iaspect = editvid.inputAspect;
            int factor;
            switch (MainForm.strAutoSize)
            {
                case "Mod8"
                    factor = 8;
                    break;
                case "Mod4"
                    factor = 4;
                    break;
                default
                    factor = 16;
                    break;
            }
            if (iaspect > aspect)
            {
                scalex = Math.Round((iy * iaspect) / factor, 0, MidpointRounding.AwayFromZero) * factor;
                scaley = Math.Round(iy / factor, 0, MidpointRounding.AwayFromZero) * factor;
            }
            else
            {
                scaley = Math.Round((ix / iaspect) / factor, 0, MidpointRounding.AwayFromZero) * factor;
                scalex = Math.Round(ix / factor, 0, MidpointRounding.AwayFromZero) * factor;
            }
            if (scaley == 1088) scaley = 1080;
            if (scalex != ix || scaley != iy)
                ia.WriteLine(strResize + "Resize(" + scalex + ", " + scaley + ")");
            ia.Close();

            return editvid;
        }


        public static bool ffmpeg_check(string ext)
        {
            return (ext == "mov" || ext == "mp4" || ext == "mkv" || ext == "avi");
        }


        private void cmdOpen_Click(object sender, RoutedEventArgs e)
