        private void Window_Loaded(object sender, RoutedEventArgs e)
        {
            editvid = MainForm.editVid;
            if (MainForm.win6) MainForm.setGlass(this); else this.Background = SystemColors.ControlBrush;
            tmrLoad.Interval = TimeSpan.FromMilliseconds(1);
            tmrLoad.Tick += new EventHandler(tmrLoad_Tick);
            tmrLoad.IsEnabled = true;

            tmrLoadControls.Interval = TimeSpan.FromMilliseconds(100);
            tmrLoadControls.Tick += new EventHandler(tmrLoadControls_Tick);

            tmrOutput.Interval = TimeSpan.FromMilliseconds(100);
            tmrOutput.Tick += new EventHandler(tmrOutput_Tick);

            tmrAutoCrop.Interval = TimeSpan.FromMilliseconds(100);
            tmrAutoCrop.Tick += new EventHandler(tmrAutoCrop_Tick);
        }


        void tmrAutoCrop_Tick(object sender, EventArgs e)
        {
            if (editvid.autocropped)
            {
                tmrOutput.IsEnabled = true;
                tmrAutoCrop.IsEnabled = false;
            }
        }


        void tmrOutput_Tick(object sender, EventArgs e)
        {
            double d;
            int I;

            //***Save all settings to editvid***

            //Source - Video
            editvid.remuxvideo = (bool)chkRemuxVideo.IsChecked;
            editvid.directshow = (bool)chkDirectshow.IsChecked;

            if (txtVideo.Text != editvid.path && File.Exists(txtVideo.Text))
            {
                tmrOutput.IsEnabled = false;
                editvid.path = txtVideo.Text;
                editvid = MainForm.videoinfo(editvid);
                Read_VideoInfo();
                tmrOutput.IsEnabled = true;
            }

            //Source - Audio
            editvid.separatefile = (bool)optSeparate.IsChecked;
            if (editvid.separatefile)
            {
                edittracklist = editvid.tracks;
                MainForm.track newtrack = edittracklist[currenttrack];
                newtrack.path = cboAudio.Text;
                newtrack.remux = (bool)chkRemuxAudio.IsChecked;
                newtrack.downmix = (bool)chkDownmix.IsChecked;
                newtrack.custombitrate = (bool)chkABitrate.IsChecked;
                newtrack.bitrate = (Int32.TryParse(cboABitrate.Text, out I)) ? I 
                newtrack.AAC = (bool)optAAC.IsChecked;
                newtrack.delay = (double.TryParse(txtDelay.Text, out d)) ? d 
                newtrack.directshow = (bool)chkADirectshow.IsChecked;
                newtrack.AACMode = cboAACMode.SelectedIndex;
                newtrack.forcesample = (bool)chkSample.IsChecked;
                newtrack.outputsample = (Int32.TryParse(cboSample.Text, out I)) ? I 
                bool readaudio = false;
                if (newtrack.path != edittracklist[currenttrack].path && File.Exists(newtrack.path))
                {
                    readaudio = true;
                    newtrack.delay = 0;
                    for (int i = 0; i < newtrack.path.Length; i++)
                    {
                        string sub = newtrack.path.Substring(i, 6);
                        if (sub == "DELAY ")
                        {
                            newtrack.delay = double.TryParse(sub, out d) ? d 
                            txtDelay.Text = (newtrack.delay * 1000).ToString();
                        }
                    }
                    for (int i = 0; i < newtrack.path.Length; i++)
                    {
                        string sub = newtrack.path.Substring(i, 6);
                        if (sub == "DELAY ")
                        {
                            newtrack.delay = double.TryParse(sub, out d) ? d 
                            txtDelay.Text = (newtrack.delay * 1000).ToString();
                        }
                    }
                }
                edittracklist[currenttrack] = newtrack;
                if (readaudio)
                {
                    editvid = MainForm.AudioInfo(currenttrack, editvid);
                    Read_AudioInfo(currenttrack);
                }
            }
            else
            {
                editvid.remuxaudio = (bool)chkRemuxAudio.IsChecked;
                editvid.Downmix = (bool)chkDownmix.IsChecked;
                editvid.CustomABitrate = (bool)chkABitrate.IsChecked;
                editvid.aBitrate = Int32.TryParse(cboABitrate.Text, out I) ? I 
                editvid.AAC = (bool)optAAC.IsChecked;
                editvid.delay = double.TryParse(txtDelay.Text, out d) ? d 
                editvid.ADirectshow = (bool)chkADirectshow.IsChecked;
                editvid.AACMode = cboAACMode.SelectedIndex;
                editvid.forcesample = (bool)chkSample.IsChecked;
                editvid.outputsample = Int32.TryParse(cboSample.Text, out I) ? I 

                if ((MainForm.AVC_Check(editvid.inputformat) && File.Exists(editvid.avcinfo)) || (MainForm.MPEG_Check(editvid.inputformat) && File.Exists(editvid.mpeginfo)))
                {
                    string mpga = editvid.mpgaudio;
                    if (((cboSourceTrack.Visibility == Visibility.Visible) && cboSourceTrack.Text == "Default") || (cboSourceTrack.Visibility == Visibility.Hidden))
                        editvid.mpgaudio = editvid.defaultmpgaudio;
                    else
                        editvid.mpgaudio = cboSourceTrack.Text;
                    bool readaudio = false;
                    if (mpga != editvid.mpgaudio && File.Exists(editvid.mpgaudio))
                    {
                        readaudio = true;
                        editvid.delay = 0;
                        for (int i = 0; i < editvid.mpgaudio.Length; i++)
                        {
                            string sub = editvid.mpgaudio.Substring(i, 6);
                            if (sub == "DELAY ")
                            {
                                editvid.delay = double.TryParse(sub, out d) ? d 
                                txtDelay.Text = (editvid.delay * 1000).ToString();
                            }
                        }
                        for (int i = 0; i < editvid.mpgaudio.Length; i++)
                        {
                            string sub = editvid.mpgaudio.Substring(i, 6);
                            if (sub == "DELAY ")
                            {
                                editvid.delay = double.TryParse(sub, out d) ? d 
                                txtDelay.Text = (editvid.delay * 1000).ToString();
                            }
                        }
                    }

                    if (readaudio)
                    {
                        editvid = MainForm.AudioInfo(-1, editvid);
                        Read_AudioInfo(-1);
                    }
                }
            }
            editvid.NoAudio = (bool)chkNoAudio.IsChecked;

            //Source - Output
            editvid.MKV = !(bool)optMP4.IsChecked;
            editvid.bluray = (bool)optBluray.IsChecked;
            editvid.iPod = (bool)chkiPod.IsChecked;
            editvid.outfile = txtOutput.Text;
            editvid.profile = cboSettings.Text;


            //Video Options - Resolution
            editvid.CustomSize = (bool)optCustomResize.IsChecked;
            editvid.NoResize = (bool)chkNoResize.IsChecked;
            editvid.customX = Int32.TryParse(txtCustomX.Text, out I) ? I 
            editvid.customY = Int32.TryParse(txtCustomY.Text, out I) ? I 
            editvid.UseAspect = (bool)chkAspect.IsChecked;
            switch (cboAspect.Text)
            {
                case "4
                case "16
                case "1.85
                case "2.39
                default
                    string ar = cboAspect.Text, sub;
                    sub = (ar.Substring(ar.Length - 2, 2) == "
                    editvid.aspectRatio = double.TryParse(sub, out d) ? d 
                    if (Math.Abs(editvid.aspectRatio - 4 / 3) < 0.05) editvid.aspectRatio = 4 / 3;
                    if (Math.Abs(editvid.aspectRatio - 1.85) < 0.05) editvid.aspectRatio = 1.85;
                    if (Math.Abs(editvid.aspectRatio - 16 / 9) < 0.05) editvid.aspectRatio = 16 / 9;
                    if (Math.Abs(editvid.aspectRatio - 2.39) < 0.05) editvid.aspectRatio = 2.39;
                    break;
            }
            editvid.MaxSize = (bool)chkMaxSize.IsChecked;
            editvid.upscale = (bool)chkUpscale.IsChecked;
            editvid.blackbars = (bool)chkBlackBars.IsChecked;
            editvid.UseOAspect = (bool)chkOAspect.IsChecked;
            switch (cboOAspect.Text)
            {
                case "4
                case "16
                case "1.85
                case "2.39
                default
                    string ar = cboOAspect.Text, sub;
                    sub = (ar.Substring(ar.Length - 2, 2) == "
                    editvid.oAspect = double.TryParse(sub, out d) ? d 
                    if (Math.Abs(editvid.oAspect - 4 / 3) < 0.05) editvid.oAspect = 4 / 3;
                    if (Math.Abs(editvid.oAspect - 1.85) < 0.05) editvid.oAspect = 1.85;
                    if (Math.Abs(editvid.oAspect - 16 / 9) < 0.05) editvid.oAspect = 16 / 9;
                    if (Math.Abs(editvid.oAspect - 2.39) < 0.05) editvid.oAspect = 2.39;
                    break;
            }
            editvid.maxX = Int32.TryParse(txtMaxX.Text, out I) ? I 
            editvid.maxY = Int32.TryParse(txtMaxY.Text, out I) ? I 
            editvid.autocrop = (bool)chkAutoCrop.IsChecked;
            editvid.CustomCrop = (bool)chkCrop.IsChecked;
            editvid.cropL = Int32.TryParse(txtCropL.Text, out I) ? I 
            editvid.cropR = Int32.TryParse(txtCropR.Text, out I) ? I 
            editvid.cropT = Int32.TryParse(txtCropT.Text, out I) ? I 
            editvid.cropB = Int32.TryParse(txtCropB.Text, out I) ? I 

            //Video Options - Frame Rate
            editvid.Deinterlace = (bool)chkDeinterlace.IsChecked;
            editvid.DoubleFPS = (bool)chkFPS2x.IsChecked;
            editvid.fieldorder = cboFieldOrder.Text;
            editvid.changefps = (bool)chkFPS.IsChecked;
            editvid.customFPS = double.TryParse(cboFPS.Text, out d) ? d 
            editvid.fpspitch = (bool)chkPitch.IsChecked;


            //Codec Options - Video
            editvid.CustomBitrate = (bool)optCustomBitrate.IsChecked;
            editvid.vBitrate = Int32.TryParse(txtVBitrate.Text, out I) ? I 
            editvid.passes = cboPass.SelectedIndex + 1;
            editvid.fastpass1 = (bool)chkFastPass1.IsChecked;
            editvid.CQ = (bool)chkCQ.IsChecked;
            editvid.quality = Int32.TryParse(cboCQ.Text, out I) ? I 
            editvid.EnforceProfile = (bool)chkProfile.IsChecked;
            editvid.x264profile = cboProfile.Text;
            editvid.x264level = double.TryParse(cboLevel.Text, out d) ? d 
            editvid.usemaxrate = (bool)chkMaxBitrate.IsChecked;
            editvid.maxBitrate = Int32.TryParse(txtMaxBitrate.Text, out I) ? I 
            editvid.usetargetsize = (bool)chkFileSize.IsChecked;
            editvid.filesize = double.TryParse(txtFileSize.Text, out d) ? d 
            editvid.filesizebytes = cboFileSize.Text;


            //Other Options - Subtitles
            editvid.usesubs = (bool)chkSubtitles.IsChecked;
            editvid.subtitle = txtSubtitles.Text;



            //***Calculate and Display Output***
            if (editvid.autocrop && !editvid.autocropped)
            {
                tmrOutput.IsEnabled = false;
                tmrAutoCrop.IsEnabled = true;
            }

            editvid = MainForm.Calculate_Output(editvid, currenttrack);

            //Display Audio and Video Format
            if (!editvid.NoAudio)
                if (!editvid.MKV || (editvid.MKV && !editvid.bluray && editvid.AAC))
                    switch (editvid.separatefile ? edittracklist[currenttrack].AACMode 
                    {
                        case 0
                        case 1
                        case 2
                        case 3
                    }
                else lblOAFormat.Content = "AC-3";
            else
            {
                lblOAFormat.Content = "";
                lblOABitrate.Content = "";
                lblOChannels.Content = "";
            }

            if (editvid.MKV)
                if (editvid.bluray) lblOVFormat.Content = "BDAV / AVC";
                else lblOVFormat.Content = "Matroska / AVC";
            else lblOVFormat.Content = "MPEG-4 / AVC";

            //Display Audio Results
            if (!editvid.NoAudio)
            {
                if (editvid.separatefile)
                {
                    lblOChannels.Content = edittracklist[currenttrack].outputchannels + ((edittracklist[currenttrack].outputchannels > 1) ? " Channels" 
                    lblOABitrate.Content = edittracklist[currenttrack].outputbitrate + " Kbps" + ((edittracklist.Count > 1) ? " (" + editvid.totalABitrate + " Kbps total)" 
                    if (editvid.remuxaudio && editvid.MKV) lblOAFormat.Content = lblAFormat.Content;
                }
                else //if (editvid.audioformat != "" && editvid.audioformat != null)
                {
                    lblOChannels.Content = editvid.outputChannels + ((editvid.outputChannels > 1) ? " Channels" 
                    lblOABitrate.Content = editvid.outputABitrate + " Kbps";
                    if (editvid.remuxaudio && editvid.MKV) lblOAFormat.Content = lblAFormat.Content;
                }
            }
        }


        void tmrLoadControls_Tick(object sender, EventArgs e)
        {

        }


        void tmrLoad_Tick(object sender, EventArgs e)
        {
            tmrLoad.IsEnabled = false;
            profilelist.Sort();
            foreach (string prof in profilelist)
                cboSettings.Items.Add(prof);
            foreach (string prof in profilelist)
                cboSettings.Items.Add(prof);
            edittracklist = editvid.tracks;

            cboAudio.Items.Add("");
            //tabOutput.SelectedIndex = 0;

            if (!File.Exists(editvid.videoinfo))
                editvid = MainForm.videoinfo(editvid);

            Read_VideoInfo();
            if (editvid.avs)
            {
                if (editvid.avsaudio == 0)
                {
                    switch (strAVSAudio)
                    {
                        case "Always Ask"
                            if (MessageBox.Show("Would you like to try to load the audio from the avisynth script?", "Load AVS Audio?", MessageBoxButton.YesNo) == MessageBoxResult.Yes)
                                editvid = MainForm.AVSAudio(editvid, currenttrack);
                            else
                                editvid.avsaudio = 2;
                            break;
                        case "Always Read Audio"
                            editvid = MainForm.AVSAudio(editvid, currenttrack);
                            break;
                        default
                            editvid.avsaudio = 2;
                            break;
                    }
                }
            }

            if (editvid.separatefile)
            {
                editvid = MainForm.AudioInfo(0, editvid);
                Read_AudioInfo(0);
            }
            Load_Settings();

            tmrLoadControls.IsEnabled = true;
            tmrOutput.IsEnabled = true;
        }


        private void Read_VideoInfo()
        {
            //Clear input labels
            lblVFormat.Content = "Loading...";
            lblAFormat.Content = "";
            lblVBitrate.Content = "";
            lblABitrate.Content = "";
            lblResolution.Content = "";
            lblChannels.Content = "";
            lblAspect.Content = "";
            lblFrameRate.Content = "";
            lblLength.Content = "";
            lblFileSize.Content = "";

            //Read videoinfo file
            editvid = MainForm.Read_VideoInfo(editvid);

            //Display Results
            lblVFormat.Content = editvid.inputformat;
            lblAFormat.Content = editvid.audioformat;
            lblVBitrate.Content = editvid.inputvbitrate;
            lblABitrate.Content = editvid.inputabitrate;
            lblResolution.Content = editvid.inputWidth + " x " + editvid.inputHeight;
            lblChannels.Content = editvid.inputChannels > 0 ? editvid.inputChannels + " channel" + (editvid.inputChannels > 1 ? "s" 
            lblFrameRate.Content = editvid.inputFPS + " fps";
            lblFileSize.Content = editvid.inputfilesize;
            cboFieldOrder.Text = editvid.fieldorder;
            lblLength.Content = Display_Length(editvid.length);
            lblAspect.Content = Display_Aspect(editvid.inputAspect);
            chkDownmix.IsEnabled = editvid.inputChannels > 2;
            //trackbar.Maximum = Convert.ToInt32(editvid.length);

            //Check ffmpeg compatibility
            int len = editvid.path.Length;
            string ext = len > 3 ? editvid.path.Substring(len - 3, 3).ToLower() 
            bool ffmpeg = MainForm.ffmpeg_check(ext);
            chkDirectshow.Visibility = ffmpeg ? Visibility.Visible 

            //Check if video can be remuxed
            chkRemuxVideo.Visibility = Remux_Check(editvid.inputformat) ? Visibility.Visible 

            //Check video for demuxed audio tracks (mpeg or avc)
            string format = editvid.inputformat;
            bool mpeg = MainForm.MPEG_Check(format), avc = MainForm.AVC_Check(format);
            if (mpeg || avc)
            {
                cboSourceTrack.Items.Clear();
                cboSourceTrack.Items.Add("Default");
                cboSourceTrack.SelectedIndex = 0;
                for (int i = 0; i < editvid.trackcount; i++)
                    cboAudio.Items.Add("Track " + (i + 1));
                for (int i = 0; i < editvid.trackcount; i++)
                    cboAudio.Items.Add("Track " + (i + 1));
                lblSourceTrack.Visibility = visible(cboAudio.Items.Count > 2);
                cboSourceTrack.Visibility = lblSourceTrack.Visibility;
                txtDelay.Text = Convert.ToString(editvid.delay * 1000);
            }

            //Set default resolution options
            if (!editvid.CustomSize)
            {
                editvid.customX = editvid.inputWidth;
                editvid.customY = editvid.inputHeight;
            }
            if (!editvid.MaxSize)
            {
                editvid.maxX = editvid.inputWidth;
                editvid.maxY = editvid.inputHeight;
            }

            //Create Input AVS file for Video preview
            //editvid = MainForm.inputavs(editvid, ffmpeg, mpeg, avc);

            //double iaspect = editvid.inputAspect;
            
            videoPreview.Source = new Uri(editvid.path);
            videoPreview.Position = TimeSpan.FromSeconds(editvid.length * 0.1);
            videoPreview.Play();
            videoPreview.Pause();
            //videoPreview.Position = TimeSpan.FromSeconds(0);
            load = true;
        }


