        private void optSeparate_Checked(object sender, RoutedEventArgs e)
        {
            cboAudio.SelectedIndex = currenttrack;
            cboAudio.Text = editvid.tracks[currenttrack].path;
            lblCurrentTrack.Content = (currenttrack + 1) + " / " + edittracklist.Count;
        }


        private void chkFPS_Checked(object sender, RoutedEventArgs e)
        {
            ShowPitch();
        }


        private void txtcboFPS_TextChanged(object sender, TextChangedEventArgs e)
        {
            ShowPitch();
        }


        private void ShowPitch()
        {
            if (this.IsLoaded)
            {
                int im, om;
                if (editvid.inputFPS == 23.976 || editvid.inputFPS == 29.97 || editvid.inputFPS == 59.94)
                    im = 0;
                else
                    if (editvid.inputFPS == 25 || editvid.inputFPS == 50)
                        im = 1;
                    else
                        im = 2;

                if (cboFPS.Text == "23.976" || cboFPS.Text == "29.97" || cboFPS.Text == "59.94")
                    om = 0;
                else
                    if (cboFPS.Text == "25" || cboFPS.Text == "50")
                        om = 1;
                    else
                        om = 2;

                bool f = (bool)chkFPS.IsChecked, c = im != om;
                chkPitch.Visibility = visible(f && c);
            }
        }


        private void cboSettings_SelectionChanged(object sender, SelectionChangedEventArgs e)
        {
            tmrOutput.IsEnabled = false;
            editvid = MainForm.Profile_Change(profilelist[cboSettings.SelectedIndex], editvid);
            Load_Settings();
            tmrOutput.IsEnabled = true;
        }


        private void cmdPlay_MouseEnter(object sender, MouseEventArgs e)
        {
            if (cmdPlay.IsMouseOver)
            {
                cmdPlay.Source = new BitmapImage(new Uri("Images/Play-Up.png", UriKind.Relative));
                cmdPlay.Opacity = 1;
                slider.Opacity = 1;
            }
            else
            {
                cmdPlay.Source = new BitmapImage(new Uri("Images/Play.png", UriKind.Relative));
                if (!slider.IsMouseOver)
                {
                    cmdPlay.Opacity = 0.1;
                    slider.Opacity = 0.1;
                }
            }
            
        }


        private void cmdPlay_MouseDown(object sender, MouseButtonEventArgs e)
        {
            cmdPlay.Source = new BitmapImage(new Uri("Images/Play-Down.png", UriKind.Relative));
        }


        private void cmdPlay_MouseLeftButtonUp(object sender, MouseButtonEventArgs e)
        {
            cmdPlay.Source = new BitmapImage(new Uri("Images/Play-Up.png", UriKind.Relative));
            videoPreview.Play();
        }


        private void slider_MouseEnter(object sender, MouseEventArgs e)
        {
            if (slider.IsMouseOver || videoPreview.IsMouseOver)
            {
                cmdPlay.Opacity = 1;
                slider.Opacity = 1;
            }
            else
            {
                if (!cmdPlay.IsMouseOver)
                {
                    cmdPlay.Opacity = 0.1;
                    slider.Opacity = 0.1;
                }
            }
        }


        private void videoPreview_MediaOpened(object sender, RoutedEventArgs e)
        {

        }

