if (slider.IsMouseOver || videoPreview.IsMouseOver)
{
    cmdPlay.Opacity = 1;
    slider.Opacity = 1;
}
else
{
    if (!cmdPlay.IsMouseOver)
    {
        cmdPlay.Opacity = 0.1;
        slider.Opacity = 0.1;
    }
{
    if (!cmdPlay.IsMouseOver)
    {
        cmdPlay.Opacity = 0.1;
        slider.Opacity = 0.1;
    }
}


