namespace ASXGui
{
    /// <summary>
    /// Interaction logic for ExportProfile.xaml
    /// </summary>
    public partial class ExportProfile 
    {
        private bool win6 = MainForm.win6;
        private List<string> profilelist = MainForm.profileList;
        private string appdir = MainForm.appdir;

private IntPtr WndProc(IntPtr hwnd, int msg, IntPtr wParam,
  IntPtr lParam, ref bool handled)
{
    if (msg == 0x031E)
    {
        int aero = 0;
        DwmIsCompositionEnabled(ref aero);
        win6 = (aero > 0);
        if (win6) this.Background = Brushes.Transparent; else this.Background = SystemColors.ControlBrush;
        if (win6) setGlass(this);
        handled = true;
    }
    return IntPtr.Zero;
}







namespace ASXGui
{
    /// <summary>
    /// Interaction logic for ExportProfile.xaml
    /// </summary>
    public partial class ExportProfile 
    {
        private bool win6 = MainForm.win6;
        private List<string> profilelist = MainForm.profileList;
        private string appdir = MainForm.appdir;


public static bool AVC_Check(string format)
{
    bool avc = false;
    int len = format.Length;
    if (len > 8)
    {
        if (format == "AVC / AVC")
            avc = true;
        if (len > 9)
        {
            if (format == "BDAV / AVC")
                avc = true;
            if (len > 12)
                if (format == "MPEG-TS / AVC" || format == "MPEG-PS / AVC")
                    avc = true;
        }
    }
    return avc;
}







namespace ASXGui
{
    /// <summary>
    /// Interaction logic for ExportProfile.xaml
    /// </summary>
    public partial class ExportProfile 
    {
        private bool win6 = MainForm.win6;
        private List<string> profilelist = MainForm.profileList;
        private string appdir = MainForm.appdir;


private IntPtr WndProc(IntPtr hwnd, int msg, IntPtr wParam, IntPtr lParam, ref bool handled)
{
    if (msg == 0x031E)
    {
        if (win6)
        {
            int aero = 0;
            MainForm.DwmIsCompositionEnabled(ref aero);
            win6 = (aero > 0);
            if (win6) this.Background = Brushes.Transparent; else this.Background = SystemColors.ControlBrush;
            if (win6) MainForm.setGlass(this);
        }
        handled = true;
    }
    return IntPtr.Zero;
}







namespace ASXGui
{
    /// <summary>
    /// Interaction logic for ExportProfile.xaml
    /// </summary>
    public partial class ExportProfile 
    {
        private bool win6 = MainForm.win6;
        private List<string> profilelist = MainForm.profileList;
        private string appdir = MainForm.appdir;


private IntPtr WndProc(IntPtr hwnd, int msg, IntPtr wParam, IntPtr lParam, ref bool handled)
{
    if (msg == 0x031E)
    {
        int aero = 0;
        MainForm.DwmIsCompositionEnabled(ref aero);
        win6 = (aero > 0);
        if (win6) this.Background = Brushes.Transparent; else this.Background = SystemColors.ControlBrush;
        if (win6) MainForm.setGlass(this);
        handled = true;
    }
    return IntPtr.Zero;
}

private void Window_Loaded(object sender, RoutedEventArgs e)
{
    if (MainForm.win6) MainForm.setGlass(this); else this.Background = SystemColors.ControlBrush;

    CheckBox c = new CheckBox();
    foreach (string s in profilelist)
    {
        c = new CheckBox();
        c.Content = s;
        cboSettings.Items.Add(c);
    }
}

protected override void OnSourceInitialized(EventArgs e)
{


private void Window_MouseDoubleClick(object sender, MouseButtonEventArgs e)
{
    bool stop = edit;
    if (cmdPage1.IsMouseOver) stop = true;
    if (cmdPage2.IsMouseOver) stop = true;
    if (cmdOpen.IsMouseOver) stop = true;
    if (cmdDelete.IsMouseOver) stop = true;
    if (cmdClear.IsMouseOver) stop = true;
    if (cmdUp.IsMouseOver) stop = true;
    if (cmdDown.IsMouseOver) stop = true;
    if (chkAppend.IsMouseOver) stop = true;
    if (cmdEdit.IsMouseOver) stop = true;
    if (cboSettings.IsMouseOver) stop = true;
    if (cmdEncode.IsMouseOver) stop = true;
    if (lblDonate.IsMouseOver) stop = true;
    if (Menu.IsMouseOver) stop = true;
    if (lstVideos.IsMouseOver) stop = true;
    if (edit) edit = false;

    if (!stop) this.WindowState = (this.WindowState == WindowState.Normal) ? WindowState.Maximized 
}

