private void ShowPitch()
{
    if (this.IsLoaded)
    {
        int im, om;
        if (editvid.inputFPS == 23.976 || editvid.inputFPS == 29.97 || editvid.inputFPS == 59.94)
            im = 0;
        else
            if (editvid.inputFPS == 25 || editvid.inputFPS == 50)
                im = 1;
            else
                im = 2;

        if (cboFPS.Text == "23.976" || cboFPS.Text == "29.97" || cboFPS.Text == "59.94")
            om = 0;
        else
            if (cboFPS.Text == "25" || cboFPS.Text == "50")
                om = 1;
            else
                om = 2;

        bool f = (bool)chkFPS.IsChecked, c = im != om;
        chkPitch.Visibility = visible(f && c);
    }
}

            if (win6) this.Background = Brushes.Transparent; else this.Background = SystemColors.ControlBrush;
            if (win6) MainForm.setGlass(this);
        }
        handled = true;
    }
    return IntPtr.Zero;
}


