private void slider_MouseEnter(object sender, MouseEventArgs e)
{
    if (slider.IsMouseOver || videoPreview.IsMouseOver)
    {
        cmdPlay.Opacity = 1;
        slider.Opacity = 1;
    }
    else
    {
        if (!cmdPlay.IsMouseOver)
        {
            cmdPlay.Opacity = 0.1;
            slider.Opacity = 0.1;
        }
    }
}


