private static string filename_nodir(string name)
{
    int slash = -1, len = name.Length;
    for (int i = 0; i < len; i++)
    {
        string sub = name.Substring(i, 1);
        if (sub == "\\" || sub == "/")
            slash = i;
    }
    for (int i = 0; i < len; i++)
    {
        string sub = name.Substring(i, 1);
        if (sub == "\\" || sub == "/")
            slash = i;
    }
    slash++;
    return name.Substring(slash, len - slash);
}


public static string randomstring(int size)
{
    StringBuilder builder = new StringBuilder();
    Random rnd = new Random();
    Char ch;
    for (int i = 0; i < size; i++)
    {
        ch = Convert.ToChar(Convert.ToInt32(26 * rnd.NextDouble() + 64));
        builder.Append(ch);
    }
    for (int i = 0; i < size; i++)
    {
        ch = Convert.ToChar(Convert.ToInt32(26 * rnd.NextDouble() + 64));
        builder.Append(ch);
    }
    return builder.ToString().ToLower();
}


public static bool EVO_Check(string format)
{
    bool container = false, codec = false;
    int len = format.Length;
    if (len > 2)
    {
        if (format.Substring(len - 3, 3) == "AVC")
            codec = true;
        if (len > 3)
        {
            if (format.Substring(len - 4, 4) == "VC-1")
                codec = true;
            if (len > 6)
                if (format.Substring(0, 7) == "MPEG-PS")
                    container = true;
        }
    }
    return container && codec;
}


public static bool MPEG_Check(string format)
{
    int len = format.Length;
    bool container = false, codec = false;
    string sub = "";
    if (len > 3)
    {
        if (format.Substring(0, 4) == "BDAV")
            container = true;
        if (len > 6)
        {
            sub = format.Substring(0, 7);
            if (sub == "MPEG-TS" || sub == "MPEG-PS")
                container = true;
            if (len > 8)
            {
                if (format.Substring(format.Length - 9, 9) == "DVD-Video")
                    codec = true;
                if (len > 9)
                {
                    if (format.Substring(format.Length - 10, 10) == "MPEG Video")
                        codec = true;
                    if (format.Substring(0, 10) == "MPEG Video")
                        container = true;
                }
            }
        }
    }
    return container && codec;
}


public static bool EVO_Check(string format)
{
    bool container = false, codec = false;
    int len = format.Length;
    if (len > 2)
    {
        if (format.Substring(len - 3, 3) == "AVC")
            codec = true;
        if (len > 3)
        {
            if (format.Substring(len - 4, 4) == "VC-1")
                codec = true;
            if (len > 6)
                if (format.Substring(0, 7) == "MPEG-PS")
                    container = true;
        }
    }
    return container && codec;
}


