    if (len > 8)
    {
        if (format == "AVC / AVC")
            avc = true;
        if (len > 9)
        {
            if (format == "BDAV / AVC")
                avc = true;
            if (len > 12)
                if (format == "MPEG-TS / AVC" || format == "MPEG-PS / AVC")
                    avc = true;
        }
    }
    return avc;
}


