{
    int len = format.Length;
    bool container = false, codec = false;
    string sub = "";
    if (len > 3)
    {
        if (format.Substring(0, 4) == "BDAV")
            container = true;
        if (len > 6)
        {
            sub = format.Substring(0, 7);
            if (sub == "MPEG-TS" || sub == "MPEG-PS")
                container = true;
            if (len > 8)
            {
                if (format.Substring(format.Length - 9, 9) == "DVD-Video")
                    codec = true;
                if (len > 9)
                {
                    if (format.Substring(format.Length - 10, 10) == "MPEG Video")
                        codec = true;
                    if (format.Substring(0, 10) == "MPEG Video")
                        container = true;
                }
            }
        }
    }
    return container && codec;
}


public static bool AVC_Check(string format)
{
    bool avc = false;
    int len = format.Length;
    if (len > 8)
    {
        if (format == "AVC / AVC")
            avc = true;
        if (len > 9)
        {
            if (format == "BDAV / AVC")
                avc = true;
            if (len > 12)
                if (format == "MPEG-TS / AVC" || format == "MPEG-PS / AVC")
                    avc = true;
        }
    }
    return avc;
}


public static bool AVC_Check(string format)
