        }
        handled = true;
    }
    return IntPtr.Zero;
}


private void optFile_Checked(object sender, RoutedEventArgs e)
{
    if (this.IsLoaded)
    {
        txtText.IsEnabled = false;
        txtFile.IsEnabled = true;
        cmdOpen.IsEnabled = true;
    }
}


