    {
        if (imgPSP.IsMouseOver)
        {
            imgPSP.Source = new BitmapImage(new Uri("Images/psp-check-highlight.png", UriKind.Relative));
        }
        else
        {
            imgPSP.Source = new BitmapImage(new Uri("Images/psp-check.png", UriKind.Relative));
        }
    }
    else
    {
