void tmrLoad_Tick(object sender, EventArgs e)
{
    tmrLoad.IsEnabled = false;



    //Set culture to InvariantCulture for compatibility with non-US regions.
    CultureInfo culture = CultureInfo.InvariantCulture;
    Thread.CurrentThread.CurrentCulture = culture;
    Thread.CurrentThread.CurrentUICulture = culture;

    //Set default encoding parameters for x264.
    editVid = Set_defaults(editVid);
    appendadvanced = editVid;

    //Set Program Options
    string optionfile = appdir + "\\Options.ini";
    if (File.Exists(optionfile))
    {
        StreamReader oRead = File.OpenText(optionfile);
        string strOption, sub;
        while (oRead.Peek() > -1)
        {
            strOption = oRead.ReadLine();
            sub = strOption.Substring(15, strOption.Length - 15);
            bool bsub = sub.ToLower() == "true";
            switch (strOption.Substring(0, 15))
            {
                case "TempDir=       "
                    tempDir = sub;
                    break;
                case "AutoUpdate=    "
                    boolUpdate = bsub;
                    break;
                case "Resize=        "
                    strResize = sub;
                    break;
                case "Shutdown=      "
                    strShutdown = sub;
                    break;
                case "AVSAudio=      "
                    strAVSAudio = sub;
                    break;
                case "Deinterlace=   "
                    strDeinterlace = sub;
                    break;
                case "Film=          "
                    strFilm = sub;
                    break;
                case "iTunes=        "
                    useiTunes = bsub;
                    break;
                case "CustomOutput=  "
                    customoutput = bsub;
                    break;
                case "OutputDir=     "
                    outputdir = sub;
                    break;
                case "LogDir=        "
                    logDir = sub;
                    break;
                case "EVO=           "
                    strEVO = sub;
                    break;
                case "Profile=       "
                    strDefaultProfile = sub;
                    break;
                case "64bit=         "
                    bit64 = bsub;
                    break;
                case "Manual32bit=   "
                    manual32bit = bsub;
                    break;
                case "Manual64bit=   "
                    manual64bit = bsub;
                    break;
                case "FilmMode=      "
                    strFilmMode = sub;
                    break;
                case "MPGDec=        "
                    MPGDec = bsub;
                    break;
                case "PreloadVOB=       "
                    preloadVOB = bsub;
                    break;
                case "AVCDec=        "
                    AVCDec = bsub;
                    break;
                case "FFDec=         "
                    FFDec = bsub;
                    break;
                case "PreloadMPG=    "
                    preloadMPG = bsub;
                    break;
                case "PreloadAVC=    "
                    preloadAVC = bsub;
                    break;
                case "SampleRate=    "
                    strSample = sub;
                    break;
                case "ProfileUpdate= "
                    strUpdateProfile = sub;
                    break;
                case "AutoSizeMode=  "
                    strAutoSize = sub;
                    break;
            }
        }
        while (oRead.Peek() > -1)
        {
            strOption = oRead.ReadLine();
            sub = strOption.Substring(15, strOption.Length - 15);
            bool bsub = sub.ToLower() == "true";
            switch (strOption.Substring(0, 15))
            {
                case "TempDir=       "
                    tempDir = sub;
                    break;
                case "AutoUpdate=    "
                    boolUpdate = bsub;
                    break;
                case "Resize=        "
                    strResize = sub;
                    break;
                case "Shutdown=      "
                    strShutdown = sub;
                    break;
                case "AVSAudio=      "
                    strAVSAudio = sub;
                    break;
                case "Deinterlace=   "
                    strDeinterlace = sub;
                    break;
                case "Film=          "
                    strFilm = sub;
                    break;
                case "iTunes=        "
                    useiTunes = bsub;
                    break;
                case "CustomOutput=  "
                    customoutput = bsub;
                    break;
                case "OutputDir=     "
                    outputdir = sub;
                    break;
                case "LogDir=        "
                    logDir = sub;
                    break;
                case "EVO=           "
                    strEVO = sub;
                    break;
                case "Profile=       "
                    strDefaultProfile = sub;
                    break;
                case "64bit=         "
                    bit64 = bsub;
                    break;
                case "Manual32bit=   "
                    manual32bit = bsub;
                    break;
                case "Manual64bit=   "
                    manual64bit = bsub;
                    break;
                case "FilmMode=      "
                    strFilmMode = sub;
                    break;
                case "MPGDec=        "
                    MPGDec = bsub;
                    break;
                case "PreloadVOB=       "
                    preloadVOB = bsub;
                    break;
                case "AVCDec=        "
                    AVCDec = bsub;
                    break;
                case "FFDec=         "
                    FFDec = bsub;
                    break;
                case "PreloadMPG=    "
                    preloadMPG = bsub;
                    break;
                case "PreloadAVC=    "
                    preloadAVC = bsub;
                    break;
                case "SampleRate=    "
                    strSample = sub;
                    break;
                case "ProfileUpdate= "
                    strUpdateProfile = sub;
                    break;
                case "AutoSizeMode=  "
                    strAutoSize = sub;
                    break;
            }
        }
        oRead.Close();
    }

    //Create directories if needed
    if (!Directory.Exists(tempDir))
        Directory.CreateDirectory(tempDir);
    if (!Directory.Exists(outputdir) && customoutput)
        Directory.CreateDirectory(outputdir);
    if (!Directory.Exists(logDir))
        Directory.CreateDirectory(logDir);

    //Set Append Options
    string appendfile = appdir + "\\Append.ini";
    if (File.Exists(appendfile))
    {
        StreamReader aFile = File.OpenText(appendfile);
        string strOption, sub;
        int intsub;
        while (aFile.Peek() > -1)
        {
            strOption = aFile.ReadLine();
            sub = strOption.Substring(15, strOption.Length - 15);
            intsub = Convert.ToInt32(sub);
            switch (strOption.Substring(0, 15))
            {
                case "SizeMode=      "
                    appendsettings.sizemode = intsub;
                    break;
                case "AspectMode=    "
                    appendsettings.aspectmode = intsub;
                    break;
                case "Format=        "
                    appendsettings.format = intsub;
                    break;
                case "AudioFormat=   "
                    appendsettings.audioformat = intsub;
                    break;
                case "FPSMode=       "
                    appendsettings.fpsmode = intsub;
                    break;
                case "BitrateMode=   "
                    appendsettings.bitratemode = intsub;
                    break;
                case "Apply=         "
                    appendsettings.apply = sub.ToLower() == "true";
                    break;
            }
        }
        while (aFile.Peek() > -1)
        {
            strOption = aFile.ReadLine();
            sub = strOption.Substring(15, strOption.Length - 15);
            intsub = Convert.ToInt32(sub);
            switch (strOption.Substring(0, 15))
            {
                case "SizeMode=      "
                    appendsettings.sizemode = intsub;
                    break;
                case "AspectMode=    "
                    appendsettings.aspectmode = intsub;
                    break;
                case "Format=        "
                    appendsettings.format = intsub;
                    break;
                case "AudioFormat=   "
                    appendsettings.audioformat = intsub;
                    break;
                case "FPSMode=       "
                    appendsettings.fpsmode = intsub;
                    break;
                case "BitrateMode=   "
                    appendsettings.bitratemode = intsub;
                    break;
                case "Apply=         "
                    appendsettings.apply = sub.ToLower() == "true";
                    break;
            }
        }
        aFile.Close();
    }

    //Check for Updates
    string versionfile = appdir + "\\version.txt";
    if (File.Exists(versionfile))
    {
        StreamReader currentver = File.OpenText(versionfile);
        intversion = Convert.ToInt32(currentver.ReadLine());
        strversion = currentver.ReadLine();
        this.Title = "ASXGui " + strversion;
        currentver.Close();
        if (boolUpdate)
            tmrUpdate.IsEnabled = true;
    }

    //Initialize Shell
    shell.StartInfo.WorkingDirectory = toolDir;
    shell.StartInfo.UseShellExecute = false;
    shell.StartInfo.CreateNoWindow = true;
    shell.StartInfo.FileName = "cmd.exe";

    //Load Profiles
    profileList = load_profiles();
    Update_Profiles();

    //Load previously saved jobs
    string jf = appdir + "\\jobs.bin";
    if (File.Exists(jf))
    {
        BinaryFormatter BF = new BinaryFormatter();
        byte[] bytes = File.ReadAllBytes(jf);
        videolist = (List<video>)BF.Deserialize(new MemoryStream(bytes));
        File.Delete(jf);
        Update_Jobs();
    }

    //Load append settings
    string af = appdir + "\\append.bin";
    if (File.Exists(af))
    {
        BinaryFormatter BF = new BinaryFormatter();
        byte[] bytes = File.ReadAllBytes(af);
        appendadvanced = (video)BF.Deserialize(new MemoryStream(bytes));
    }
}


static void shell_OutputDataReceived(object sender, DataReceivedEventArgs e)
{
    string progress = e.Data;
    if (progress != null)
    {
        int len = progress.Length;
        if (len >= 10)
        {
            string sub = progress.Substring(0, len - 10);
            double d = new double();
            if (progress.Substring(len - 10, 10) == "% complete")
                tspercent = Double.TryParse(sub, out d) ? d 
        }
    }
}


public static video MPEGInfo(video editvid)
{
    MPEG frmMPEG = new MPEG();
    string key = editvid.key;
    frmMPEG.Show();
    if (!File.Exists(editvid.mpeginfo) || editvid.mpeginfo == null)
    {
        shell.StartInfo.Arguments = "/c DGIndex -HIDE -EXIT -AT=[" + toolDir + "\\template.avs] -AIF=[" + editvid.path + "] -OF=[" + tempDir + "\\" + key + "]";
        shell.Start();
        shell.WaitForExit();
    }
    shell.StartInfo.WorkingDirectory = tempDir;
    shell.StartInfo.Arguments = " /c dir /b " + key + "*.ac3 " + key + "*.wav " + key + "*.dts " + key + "*.pcm " + key + "*.mp* " + key + "*.aac > " + key + ".trackinfo";
    shell.Start();
    shell.WaitForExit();
    shell.StartInfo.WorkingDirectory = toolDir;
    editvid.trackinfo = tempDir + "\\" + key + ".trackinfo";
    string avs = tempDir + "\\" + key + ".avs";
    if (File.Exists(avs))
    {
        StreamReader mread = File.OpenText(avs);
        editvid.mpgaudio = (mread.Peek() > -1) ? mread.ReadLine() 
        editvid.defaultmpgaudio = editvid.mpgaudio;
        string s = (mread.Peek() > -1) ? mread.ReadLine() 
        double d;
        editvid.delay = Double.TryParse(s, out d) ? d 
        editvid = AudioInfo(-1, editvid);
        //Read_AudioInfo();
        mread.Close();
        File.Delete(avs);
    }
    else
        MessageBox.Show("Error reading MPEG file!");
    StreamWriter awrite = File.CreateText(avs);
    editvid.mpeginfo = tempDir + "\\" + key + ".d2v";
    awrite.WriteLine(
        "LoadPlugin(\"" + toolDir + "\\DGDecode.dll\")\r\n" +
        "mpeg2source(\"" + editvid.mpeginfo + "\")");
    awrite.Close();
    string avi = tempDir + "\\" + key + ".avi", ml = tempDir + "\\" + key + ".mpeglength";
    shell.StartInfo.Arguments = " /c ECHO y | avs2avi \"" + avs + "\" \"" + avi + "\" && mediainfo \"" + avi + "\" > \"" + ml + "\"";
    shell.Start();
    shell.WaitForExit();
    File.Delete(avs);
    File.Delete(avi);
    if (File.Exists(ml))
    {
        StreamReader sread = File.OpenText(ml);
        string info = "";
        while (sread.Peek() > -1 && info != "Duration            ")
        {
            info = sread.ReadLine();
            if (info.Length > 35)
            {
                string sub = info.Substring(35, info.Length - 35);
                if (info.Substring(0, 20) == "Duration            ")
                {
                    double hours = 0, minutes = 0, seconds = 0;
                    int cursor = 0;
                    int i;
                    for (i = 1; i < sub.Length; i++)
                        if (sub.Substring(i, 1) == "h")
                        {
                            hours = Convert.ToDouble(sub.Substring(0, i));
                            cursor = i + 1;
                        }
                    for (i = 1; i < sub.Length; i++)
                        if (sub.Substring(i, 1) == "h")
                        {
                            hours = Convert.ToDouble(sub.Substring(0, i));
                            cursor = i + 1;
                        }
                    for (i = cursor + 1; i < (sub.Length - 1); i++)
                        if (sub.Substring(i, 2) == "mn")
                        {
                            minutes = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                            cursor = i + 3;
                        }
                    for (i = cursor + 1; i < (sub.Length - 1); i++)
                        if (sub.Substring(i, 2) == "mn")
                        {
                            minutes = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                            cursor = i + 3;
                        }
                    for (i = cursor + 1; i < sub.Length; i++)
                        if (sub.Substring(i, 1) == "s" && sub.Substring(i - 1, 2) != "ms")
                        {
                            seconds = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                            cursor = i + 1;
                        }
                    for (i = cursor + 1; i < sub.Length; i++)
                        if (sub.Substring(i, 1) == "s" && sub.Substring(i - 1, 2) != "ms")
                        {
                            seconds = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                            cursor = i + 1;
                        }
                    editvid.length = hours * 3600 + minutes * 60 + seconds;
                    editvid.mpeglength = editvid.length;
                }
            }
        }
        while (sread.Peek() > -1 && info != "Duration            ")
        {
            info = sread.ReadLine();
            if (info.Length > 35)
            {
                string sub = info.Substring(35, info.Length - 35);
                if (info.Substring(0, 20) == "Duration            ")
                {
                    double hours = 0, minutes = 0, seconds = 0;
                    int cursor = 0;
                    int i;
                    for (i = 1; i < sub.Length; i++)
                        if (sub.Substring(i, 1) == "h")
                        {
                            hours = Convert.ToDouble(sub.Substring(0, i));
                            cursor = i + 1;
                        }
                    for (i = cursor + 1; i < (sub.Length - 1); i++)
                        if (sub.Substring(i, 2) == "mn")
                        {
                            minutes = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                            cursor = i + 3;
                        }
                    for (i = cursor + 1; i < sub.Length; i++)
                        if (sub.Substring(i, 1) == "s" && sub.Substring(i - 1, 2) != "ms")
                        {
                            seconds = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                            cursor = i + 1;
                        }
                    editvid.length = hours * 3600 + minutes * 60 + seconds;
                    editvid.mpeglength = editvid.length;
                }
            }
        }
        sread.Close();
        File.Delete(ml);
    }
    if (!editvid.film && File.Exists(editvid.mpeginfo))
    {
        StreamReader dfile = File.OpenText(editvid.mpeginfo);
        while (dfile.Peek() > -1)
        {
            string d2v = dfile.ReadLine();
            if (d2v.Length > 8)
                if (d2v.Substring(0, 8) == "FINISHED" && d2v.Substring(d2v.Length - 4, 4) == "FILM")
                {
                    editvid.film = true;
                    editvid.percentfilm = d2v.Substring(8, d2v.Length - 8);
                    editvid.isinterlaced = true;
                }
        }
        while (dfile.Peek() > -1)
        {
            string d2v = dfile.ReadLine();
            if (d2v.Length > 8)
                if (d2v.Substring(0, 8) == "FINISHED" && d2v.Substring(d2v.Length - 4, 4) == "FILM")
                {
                    editvid.film = true;
                    editvid.percentfilm = d2v.Substring(8, d2v.Length - 8);
                    editvid.isinterlaced = true;
                }
        }
        dfile.Close();
    }

    frmMPEG.Close();
    return editvid;
}


private List<string> load_profiles()
{
    List<string> profiles = new List<string>();
    string pf = appdir + "\\profiles.ini";
    noprofiles = false;
    //Check if profiles.ini exists
    if (!File.Exists(pf))
    {
        if (MessageBox.Show("ASXGui has determined that you don't have any profiles installed. Would you like to download the official ASXGui profiles?", "Download Profiles?", MessageBoxButton.YesNo) == MessageBoxResult.Yes)
        {
            download_profiles(pf);
        }
        else
        {
            MessageBox.Show("Select \"Download Official Profiles\" from the tools menu if you ever want to download the oficial profiles.");
            noprofiles = true;
            StreamWriter pw = File.CreateText(pf);
            pw.Close();
        }
    }
    if (File.Exists(pf))
    {
        StreamReader dl = File.OpenText(pf);
        string data, profile = "";
        int tmpversion = intversion;
        while (dl.Peek() > -1)
        {
            data = dl.ReadLine();
            if (data.Length > 8)
            {
                if (data.Substring(0, 8) == "Profile=")
                {
                    profile = data.Substring(8, data.Length - 8);
                    profiles.Add(profile);
                }
            }
            if (data.Length > 15)
            {
                if (data.Substring(0, 15) == "Version=       ")
                {
                    int pv = Convert.ToInt32(data.Substring(15, data.Length - 15));
                    if (pv < tmpversion)
                        tmpversion = pv;
                }
            }
        }
        while (dl.Peek() > -1)
        {
            data = dl.ReadLine();
            if (data.Length > 8)
            {
                if (data.Substring(0, 8) == "Profile=")
                {
                    profile = data.Substring(8, data.Length - 8);
                    profiles.Add(profile);
                }
            }
            if (data.Length > 15)
            {
                if (data.Substring(0, 15) == "Version=       ")
                {
                    int pv = Convert.ToInt32(data.Substring(15, data.Length - 15));
                    if (pv < tmpversion)
                        tmpversion = pv;
                }
            }
        }
        dl.Close();

        //Check if profiles need updating            
        if (tmpversion < intversion)
        {
            profileList = profiles;
            profile_updater();
        }
    }
    profiles.Sort();

    return profiles;
}


public static video AVCInfo(video editvid)
{
    AVC frmAVC = new AVC();
    string key = editvid.key;
    frmAVC.Show();
    if (!File.Exists(editvid.avcinfo) || editvid.avcinfo == null)
    {
        shell.StartInfo.Arguments = "/c DGAVCIndex.exe -h -e -i \"" + editvid.path + "\" -o \"" + tempDir + "\\" + key + ".dga\" -a";
        shell.Start();
        shell.WaitForExit();
    }
    shell.StartInfo.WorkingDirectory = tempDir;
    shell.StartInfo.Arguments = " /c dir /b " + key + "*.ac3 " + key + "*.wav " + key + "*.dts " + key + "*.pcm " + key + "*.mp* " + key + "*.aac > " + key + ".trackinfo";
    shell.Start();
    shell.WaitForExit();
    shell.StartInfo.WorkingDirectory = toolDir;
    editvid.trackinfo = tempDir + "\\" + key + ".trackinfo";
    StreamReader ti = File.OpenText(editvid.trackinfo);
    editvid.mpgaudio = "";
    if (ti.Peek() > -1)
        editvid.mpgaudio = ti.ReadLine();
    editvid.defaultmpgaudio = editvid.mpgaudio;
    ti.Close();
    editvid = AudioInfo(-1, editvid);
    //Read_AudioInfo();
    if (!editvid.film && File.Exists(editvid.avcinfo))
    {
        StreamReader dfile = File.OpenText(editvid.avcinfo);
        while (dfile.Peek() > -1)
        {
            string avc = dfile.ReadLine();
            if (avc.Length > 8)
                if (avc.Substring(0, 8) == "FINISHED" && avc.Substring(avc.Length - 4, 4) == "FILM")
                {
                    editvid.film = true;
                    editvid.percentfilm = avc.Substring(8, avc.Length - 8);
                    editvid.isinterlaced = true;
                }
        }
        while (dfile.Peek() > -1)
        {
            string avc = dfile.ReadLine();
            if (avc.Length > 8)
                if (avc.Substring(0, 8) == "FINISHED" && avc.Substring(avc.Length - 4, 4) == "FILM")
                {
                    editvid.film = true;
                    editvid.percentfilm = avc.Substring(8, avc.Length - 8);
                    editvid.isinterlaced = true;
                }
        }
        dfile.Close();
    }

    frmAVC.Close();
    return editvid;
}


private void cmdImport_Click(object sender, RoutedEventArgs e)
{
    string f = txtFile.Text, p = appdir + "\\profiles.ini", prof = "", data, t = appdir + "\\profiles.tmp";
    if ((bool)optFile.IsChecked)
    {
        if (File.Exists(f))
        {
            StreamReader i = File.OpenText(f);
            StreamWriter o;
            if (File.Exists(p))
                o = File.AppendText(p);
            else
                o = File.CreateText(p);
            while (i.Peek() > -1)
            {
                data = i.ReadLine();
                if (data.Length > 8)
                    if (data.Substring(0, 8) == "Profile=")
                        if (prof == "")
                            prof = data.Substring(8, data.Length - 8);
                        else
                            prof += "\n" + data.Substring(8, data.Length - 8);
                o.WriteLine(data);
            }
            while (i.Peek() > -1)
            {
                data = i.ReadLine();
                if (data.Length > 8)
                    if (data.Substring(0, 8) == "Profile=")
                        if (prof == "")
                            prof = data.Substring(8, data.Length - 8);
                        else
                            prof += "\n" + data.Substring(8, data.Length - 8);
                o.WriteLine(data);
            }
            i.Close();
            o.Close();
            MessageBox.Show("The following profiles were imported successfully
            txtFile.Text = "";
        }
        else
            MessageBox.Show("File not found!");
    }
    else
    {
        StreamWriter o;
        if (File.Exists(p))
            o = File.AppendText(p);
        else
            o = File.CreateText(p);
        StreamWriter tmp = File.CreateText(t);
        tmp.WriteLine(txtText.Text);
        tmp.Close();
        StreamReader i = File.OpenText(t);
        while (i.Peek() > -1)
        {
            data = i.ReadLine();
            if (data.Length > 8)
                if (data.Substring(0, 8) == "Profile=")
                    if (prof == "")
                        prof = data.Substring(8, data.Length - 8);
                    else
                        prof += "\n" + data.Substring(8, data.Length - 8);
            o.WriteLine(data);
        }
        while (i.Peek() > -1)
        {
            data = i.ReadLine();
            if (data.Length > 8)
                if (data.Substring(0, 8) == "Profile=")
                    if (prof == "")
                        prof = data.Substring(8, data.Length - 8);
                    else
                        prof += "\n" + data.Substring(8, data.Length - 8);
            o.WriteLine(data);
        }
        i.Close();
        o.Close();
        MessageBox.Show("The following profiles were imported successfully
        txtFile.Text = "";
        File.Delete(t);
    }
}


public static video Remux_EVO(video editvid, string evov, string evoa, int vtrack, int atrack)
{
    string em = tempDir + "\\evo.meta";
    if (File.Exists(em))
        File.Delete(em);
    StreamWriter mfile = File.CreateText(em);
    mfile.WriteLine("MUXOPT --no-pcr-on-video-pid --new-audio-pes --vbr  --vbv-len=500");
    string path = editvid.path;
    switch (evov)
    {
        case "AVC"
            mfile.WriteLine("V_MPEG4/ISO/AVC, \"" + path + "\", track=" + vtrack);
            break;
        case "VC-1"
            mfile.WriteLine("V_MS/VFW/WVC1, \"" + path + "\" ,track=" + vtrack);
            break;
        case "MPEG Video"
            mfile.WriteLine("V_MPEG-2, \"" + path + "\" track=" + vtrack);
            break;
    }
    switch (evoa)
    {
        case "AC-3"
            mfile.WriteLine("A_AC3, \"" + path + "\", track=" + atrack);
            break;
        case "DTS"
            mfile.WriteLine("A_DTS, \"" + path + "\", track=" + atrack);
            break;
        case "AAC"
            mfile.WriteLine("A_AAC, \"" + path + "\", track=" + atrack);
            break;
        case "MPEG Audio"
            mfile.WriteLine("A_MP3, \"" + path + "\", track=" + atrack);
            break;
        case "LPCM"
            mfile.WriteLine("A_LPCM, \"" + path + "\", track=" + atrack);
            break;
    }
    mfile.Close();
    shell.StartInfo.RedirectStandardOutput = true;
    shell.OutputDataReceived += new DataReceivedEventHandler(shell_OutputDataReceived);
    shell.StartInfo.Arguments = "/c tsmuxer \"" + em + "\" \"" + path + ".m2ts\"";
    shell.Start();
    shell.PriorityClass = ProcessPriorityClass.Idle;
    shell.BeginOutputReadLine();
    EVO frmEVO = new EVO();
    frmEVO.Show();
    shell.WaitForExit();
    shell.Close();
    shell.CancelOutputRead();
    tspercent = 100;
    shell.StartInfo.RedirectStandardOutput = false;
    File.Delete(em);
    editvid.path += ".m2ts";
    editvid.jobname += ".m2ts";
    editvid = videoinfo(editvid);
    editvid = Read_VideoInfo(editvid);
    return editvid;
}


public static video inputavs(video editvid, bool ffmpeg, bool mpeg, bool avc)
{
    editvid.inputAVS = tempDir + "\\" + editvid.key + "-input.avs";
    StreamWriter ia = File.CreateText(editvid.inputAVS);
    if (ffmpeg)
    {
        ia.WriteLine("LoadPlugin(\"" + toolDir + "\\FFMS2.dll\")");
        ia.WriteLine("a=ffaudiosource(\"" + editvid.path + "\")");
        ia.WriteLine("v=ffvideosource(\"" + editvid.path + "\")");                
        ia.WriteLine("audiodub(v,a)");
    }
    if (mpeg)
    {
        if (File.Exists(editvid.mpeginfo))
        {
            ia.WriteLine("LoadPlugin(\"" + toolDir + "\\DGDecode.dll\")");
            ia.WriteLine("v=mpeg2source(\"" + editvid.mpeginfo + "\")");
            if (File.Exists(editvid.mpgaudio))
                ia.WriteLine("a=directshowsource(\"" + editvid.mpgaudio + "\")");
            else
                ia.WriteLine("a=directshowsource(\"" + editvid.path + "\")");
            ia.WriteLine("audiodub(v,a)");
            if (editvid.delay != 0)
                ia.WriteLine("DelayAudio(" + editvid.delay + ")");
        }
        else
            ia.WriteLine("Directshowsource(\"" + editvid.path + "\")");
    }
    if (avc)
    {
        if (File.Exists(editvid.avcinfo))
        {
            ia.WriteLine("LoadPlugin(\"" + toolDir + "\\DGAVCDecode.dll\")");
            ia.WriteLine("v=avcsource(\"" + editvid.avcinfo + "\")");
            if (File.Exists(editvid.mpgaudio))
                ia.WriteLine("a=directshowsource(\"" + editvid.mpgaudio + "\")");
            else
                ia.WriteLine("a=directshowsource(\"" + editvid.path + "\")");
            ia.WriteLine("audiodub(v,a)");
            if (editvid.delay != 0)
                ia.WriteLine("DelayAudio(" + editvid.delay + ")");
        }
        else
            ia.WriteLine("Directshowsource(\"" + editvid.path + "\")");
    }
    if (!ffmpeg && !avc && !mpeg)
        ia.WriteLine("Directshowsource(\"" + editvid.path + "\")");
    double scalex, scaley;
    double ix = (double)editvid.inputWidth, iy = (double)editvid.inputHeight;
    double aspect = ix / iy, iaspect = editvid.inputAspect;
    int factor;
    switch (MainForm.strAutoSize)
    {
        case "Mod8"
            factor = 8;
            break;
        case "Mod4"
            factor = 4;
            break;
        default
            factor = 16;
            break;
    }
    if (iaspect > aspect)
    {
        scalex = Math.Round((iy * iaspect) / factor, 0, MidpointRounding.AwayFromZero) * factor;
        scaley = Math.Round(iy / factor, 0, MidpointRounding.AwayFromZero) * factor;
    }
    else
    {
        scaley = Math.Round((ix / iaspect) / factor, 0, MidpointRounding.AwayFromZero) * factor;
        scalex = Math.Round(ix / factor, 0, MidpointRounding.AwayFromZero) * factor;
    }
    if (scaley == 1088) scaley = 1080;
    if (scalex != ix || scaley != iy)
        ia.WriteLine(strResize + "Resize(" + scalex + ", " + scaley + ")");
    ia.Close();

    return editvid;
}


private static string filename_nodir(string name)
{
    int slash = -1, len = name.Length;
    for (int i = 0; i < len; i++)
    {
        string sub = name.Substring(i, 1);
        if (sub == "\\" || sub == "/")
            slash = i;
    }
    for (int i = 0; i < len; i++)
    {
        string sub = name.Substring(i, 1);
        if (sub == "\\" || sub == "/")
            slash = i;
    }
    slash++;
    return name.Substring(slash, len - slash);
}


public static video videoinfo(video editvid)
{
    string path = editvid.path;
    editvid.avs = path.Substring(path.Length - 3, 3).ToLower() == "avs";
    //Generate unique job key (Over 200 billion possibilities)
    if (editvid.key == "")
    {
        bool used = false;
        while (!used)
        {
            editvid.key = randomstring(8);
            used = false;
            for (int i = 0; i < videolist.Count; i++)
                if (videolist[i].key == editvid.key)
                    used = true;
            for (int i = 0; i < videolist.Count; i++)
                if (videolist[i].key == editvid.key)
                    used = true;
        }
        while (!used)
        {
            editvid.key = randomstring(8);
            used = false;
            for (int i = 0; i < videolist.Count; i++)
                if (videolist[i].key == editvid.key)
                    used = true;
        }
    }

    //Generate videoinfo file for current job
    if (editvid.avs)
    {
        editvid.avsaudio = 0;
        shell.StartInfo.Arguments = " /c ECHO y | avs2avi \"" + editvid.path + "\" \"" + tempDir + "\\avs2avi.avi\" && mediainfo \"" + tempDir + "\\avs2avi.avi\" \"--Language=file
        shell.Start();
        shell.WaitForExit();
    }
    else
    {
        shell.StartInfo.Arguments = " /c mediainfo \"" + editvid.path + "\" \"--Language=file
        shell.Start();
        shell.WaitForExit();
    }
    editvid.videoinfo = tempDir + "\\" + editvid.key + ".videoinfo";
    return editvid;
}


public static video AVSAudio(video editvid, int track)
{
    edittracklist = editvid.tracks;
    string ai = tempDir + "\\" + editvid.key + ".audioinfo";
    AVS2WAV frmAVS = new AVS2WAV();
    frmAVS.Show();
    StreamWriter atest = File.CreateText(tempDir + "\\atest.avs");
    atest.WriteLine(
        "Import(\"" + editvid.path + "\")" + "\r\n" +
        "Trim(0,30)");
    atest.Close();
    shell.StartInfo.Arguments = "/c test \"" + tempDir + "\\atest.avs\" > \"" + tempDir + "\\atest.txt\"";
    shell.Start();
    shell.WaitForExit();
    StreamReader aread = File.OpenText(tempDir + "\\atest.txt");
    bool err = false;
    string strerr = "";
    while (aread.Peek() > -1 && !err)
    {
        strerr = aread.ReadLine();
        if (strerr.Length > 0)
            if (strerr.Length > 6)
                err = strerr.Substring(0, 7) != "Reading";
            else
                err = true;
    }
    while (aread.Peek() > -1 && !err)
    {
        strerr = aread.ReadLine();
        if (strerr.Length > 0)
            if (strerr.Length > 6)
                err = strerr.Substring(0, 7) != "Reading";
            else
                err = true;
    }
    if (err)
        while (aread.Peek() > -1)
            strerr = strerr + "\r\n" + aread.ReadLine();
        while (aread.Peek() > -1)
            strerr = strerr + "\r\n" + aread.ReadLine();
    else
    {
        shell.StartInfo.Arguments = "/c avs2wav.exe \"" + editvid.path + "\" \"" + tempDir + "\\audio.wav\" && mediainfo \"" + tempDir + "\\audio.wav\" \"--Language=file
        shell.Start();
        shell.WaitForExit();
    }
    aread.Close();
    if (File.Exists(tempDir + "\\audio.wav"))
    {
        editvid.audio = editvid.path;
        editvid.avsaudio = 1;
        editvid.separatefile = true;
        track newtrack = new track();
        newtrack.path = editvid.path;
        newtrack.delay = 0;
        newtrack.downmix = editvid.Downmix;
        newtrack.custombitrate = editvid.CustomABitrate;
        newtrack.bitrate = editvid.aBitrate;
        newtrack.directshow = editvid.directshow;
        newtrack.AACMode = editvid.AACMode;
        edittracklist[track] = newtrack;
        editvid.tracks = edittracklist;
    }
    else
    {
        editvid.avsaudio = 2;
        MessageBox.Show("No audio stream detected in AVS Script");
    }
    frmAVS.Close();
    if (editvid.avsaudio == 1)
        editvid.audioinfo = ai;
    else
        editvid.audioinfo = "";
    return editvid;
}


public static video Autocrop(video editvid)
{
    Crop frmCrop = new Crop();
    frmCrop.Show();
    string format = editvid.inputformat;
    if (!File.Exists(editvid.inputAVS))
        editvid = inputavs(editvid, ffmpeg_check(format), MPEG_Check(format), AVC_Check(format));
    editvid.cropAVS = tempDir + "\\" + editvid.key + "-crop.avs";
    StreamWriter cFile = new StreamWriter(new FileStream(editvid.cropAVS, FileMode.Create), Encoding.Default);
    cFile.WriteLine("LoadPlugin(\"" + toolDir + "\\autocrop.dll\")");
    cFile.WriteLine("Import(\"" + editvid.inputAVS + "\")");
    cFile.WriteLine("Autocrop(0)");
    cFile.Close();

    shell.StartInfo.Arguments = " /c ECHO y | avs2avi \"" + editvid.cropAVS + "\" \"" + editvid.cropAVS + ".avi\" && mediainfo \"" + editvid.cropAVS + ".avi\" \"--Language=file
    shell.Start();
    shell.WaitForExit();

    if (File.Exists(editvid.cropAVS)) File.Delete(editvid.cropAVS);
    if (File.Exists(editvid.cropAVS + ".avi")) File.Delete(editvid.cropAVS + ".avi");
    video tmpvideo = editvid;
    tmpvideo.videoinfo = editvid.cropAVS + ".cropinfo";
    tmpvideo = Read_VideoInfo(tmpvideo);
    if (File.Exists(editvid.cropAVS + ".cropinfo")) File.Delete(editvid.cropAVS + ".cropinfo");
    editvid.autocroppedWidth = tmpvideo.inputWidth;
    editvid.autocroppedHeight = tmpvideo.inputHeight;
    editvid.autocropAspect = tmpvideo.inputAspect;
    double iaspect = (double)editvid.inputWidth / (double)editvid.inputHeight;
    if (Math.Abs(iaspect - 4 / 3) < 0.05) iaspect = 4 / 3;
    if (Math.Abs(iaspect - 1.85) < 0.05) iaspect = 1.85;
    if (Math.Abs(iaspect - 16 / 9) < 0.05) iaspect = 16 / 9;
    if (Math.Abs(iaspect - 2.39) < 0.05) iaspect = 2.39;

    if (iaspect != editvid.inputAspect)
    {
        double oaspect = (editvid.inputAspect / iaspect) * editvid.autocropAspect;
        if (Math.Abs(oaspect - 4 / 3) < 0.05) oaspect = 4 / 3;
        if (Math.Abs(oaspect - 1.85) < 0.05) oaspect = 1.85;
        if (Math.Abs(oaspect - 16 / 9) < 0.05) oaspect = 16 / 9;
        if (Math.Abs(oaspect - 2.39) < 0.05) oaspect = 2.39;
        editvid.autocropAspect = oaspect;
    }

    frmCrop.Close();
    editvid.autocropped = true;
    return editvid;
}


public static video AudioInfo(int track, video editvid)
{
    edittracklist = editvid.tracks;
    string file;
    if (track > -1)
        file = edittracklist[track].path;
    else
        file = editvid.mpgaudio;

    if (file.Substring(file.Length - 3, 3).ToLower() == "avs")
        editvid = AVSAudio(editvid, track);
    else
    {
        shell.StartInfo.Arguments = "/c mediainfo \"" + file + "\" \"--Language=file
        shell.Start();
        shell.WaitForExit();
    }
    editvid.audioinfo = tempDir + "\\" + editvid.key + ".audioinfo";
    return editvid;
}


public static bool EVO_Check(string format)
{
    bool container = false, codec = false;
    int len = format.Length;
    if (len > 2)
    {
        if (format.Substring(len - 3, 3) == "AVC")
            codec = true;
        if (len > 3)
        {
            if (format.Substring(len - 4, 4) == "VC-1")
                codec = true;
            if (len > 6)
                if (format.Substring(0, 7) == "MPEG-PS")
                    container = true;
        }
    }
    return container && codec;
}


public static video Read_AudioInfo(video editvid, int track)
{
    edittracklist = editvid.tracks;
    track newtrack;
    if (track > -1)
        newtrack = edittracklist[track];
    else
    {
        newtrack = new track();
        newtrack.forcesample = editvid.forcesample;
        newtrack.outputsample = editvid.outputsample;
    }
    if (File.Exists(editvid.audioinfo))
    {
        newtrack.format = "";
        newtrack.inputbitrate = "";
        newtrack.inputchannels = 0;
        newtrack.trackcount = 0;

        //Read audio file, display contents
        StreamReader aread = File.OpenText(editvid.audioinfo);
        int intAudio = -1;
        string strInfo;
        string sub;
        while (aread.Peek() > -1)
        {
            strInfo = aread.ReadLine();
            if (strInfo.Length > 4)
            {
                if (strInfo.Substring(0, 5) == "Video" && intAudio == -1)
                    intAudio = 0;
                if (strInfo.Substring(0, 5) == "Audio" && intAudio == 0)
                    intAudio = 1;
                if (strInfo.Length > 34)
                {
                    int inum;
                    double dnum;
                    sub = strInfo.Substring(35, strInfo.Length - 35);
                    int isub = Int32.TryParse(sub, out inum) ? inum 
                    double dsub = Double.TryParse(sub, out dnum) ? dnum 
                    string psub = sub.Length > 6 ? sub.Substring(0, sub.Length - 6) 
                    int ipsub = Int32.TryParse(psub, out inum) ? inum 
                    switch (strInfo.Substring(0, 20))
                    {
                        case "Format              "
                            if (editvid.avsaudio != 1)
                            {
                                if (intAudio == 1)
                                    if (newtrack.format == "")
                                        newtrack.format = sub;
                            }
                            else
                                newtrack.format = "Avisynth Script";
                            break;
                        case "Codec               "
                            if (intAudio == 1)
                                if (newtrack.trackcount == 0)
                                    newtrack.format = sub;
                            break;
                        case "Bit rate            "
                            if (intAudio == 1)
                                if (newtrack.trackcount == 0)
                                    newtrack.inputbitrate = sub;
                            break;
                        case "Channel(s)          "
                            if (newtrack.trackcount == 0)
                            {
                                newtrack.outputchannels = isub;
                                newtrack.inputchannels = isub;
                            }
                            break;
                        case "Sampling rate       "
                            if (newtrack.trackcount == 0)
                                newtrack.inputsample = isub * 1000;
                            newtrack.trackcount++;
                            intAudio++;
                            break;
                    }
                }
            }
        }
        while (aread.Peek() > -1)
        {
            strInfo = aread.ReadLine();
            if (strInfo.Length > 4)
            {
                if (strInfo.Substring(0, 5) == "Video" && intAudio == -1)
                    intAudio = 0;
                if (strInfo.Substring(0, 5) == "Audio" && intAudio == 0)
                    intAudio = 1;
                if (strInfo.Length > 34)
                {
                    int inum;
                    double dnum;
                    sub = strInfo.Substring(35, strInfo.Length - 35);
                    int isub = Int32.TryParse(sub, out inum) ? inum 
                    double dsub = Double.TryParse(sub, out dnum) ? dnum 
                    string psub = sub.Length > 6 ? sub.Substring(0, sub.Length - 6) 
                    int ipsub = Int32.TryParse(psub, out inum) ? inum 
                    switch (strInfo.Substring(0, 20))
                    {
                        case "Format              "
                            if (editvid.avsaudio != 1)
                            {
                                if (intAudio == 1)
                                    if (newtrack.format == "")
                                        newtrack.format = sub;
                            }
                            else
                                newtrack.format = "Avisynth Script";
                            break;
                        case "Codec               "
                            if (intAudio == 1)
                                if (newtrack.trackcount == 0)
                                    newtrack.format = sub;
                            break;
                        case "Bit rate            "
                            if (intAudio == 1)
                                if (newtrack.trackcount == 0)
                                    newtrack.inputbitrate = sub;
                            break;
                        case "Channel(s)          "
                            if (newtrack.trackcount == 0)
                            {
                                newtrack.outputchannels = isub;
                                newtrack.inputchannels = isub;
                            }
                            break;
                        case "Sampling rate       "
                            if (newtrack.trackcount == 0)
                                newtrack.inputsample = isub * 1000;
                            newtrack.trackcount++;
                            intAudio++;
                            break;
                    }
                }
            }
        }
        aread.Close();

        if (!newtrack.forcesample)
        {
            List<int> rates = new List<int>();
            double isa, min;
            int rate;
            isa = newtrack.inputsample;
            rates.Add(0);
            rates.Add(8000);
            rates.Add(11025);
            rates.Add(16000);
            rates.Add(22050);
            rates.Add(24000);
            rates.Add(32000);
            rates.Add(44100);
            rates.Add(48000);
            rates.Add(88200);
            rates.Add(96000);
            bool common = false;
            foreach (int i in rates)
                if (i == isa)
                    common = true;
            foreach (int i in rates)
                if (i == isa)
                    common = true;
            if (!common)
            {
                rate = 8000;
                min = Math.Abs(isa - 8000);
                foreach (int i in rates)
                    if (Math.Abs(isa - i) < min)
                    {
                        min = Math.Abs(isa - i);
                        rate = i;
                    }
                foreach (int i in rates)
                    if (Math.Abs(isa - i) < min)
                    {
                        min = Math.Abs(isa - i);
                        rate = i;
                    }
                MessageBoxResult result;
                if (strSample == "Always Ask")
                    result = MessageBox.Show("The audio sample rate is not a common value (" + isa + " Hz), would you like to force it to the nearest compatible sample rate? (" + rate + " Hz)", "Change Sample Rate?", MessageBoxButton.YesNo);
                else
                    result = (strSample == "Always Convert") ? MessageBoxResult.Yes 
                if (result == MessageBoxResult.Yes)
                {
                    newtrack.forcesample = true;
                    newtrack.outputsample = rate;
                }
            }
        }
    }
    if (track > -1)
    {
        edittracklist[track] = newtrack;
        editvid.tracks = edittracklist;
    }
    else
    {
        editvid.audioinfo = newtrack.format;
        editvid.inputabitrate = newtrack.inputbitrate;
        editvid.inputChannels = newtrack.inputchannels;
        editvid.outputChannels = newtrack.outputchannels;
        editvid.inputsample = newtrack.inputsample;
        editvid.forcesample = newtrack.forcesample;
        editvid.outputsample = newtrack.outputsample;
        editvid.trackcount = newtrack.trackcount;
    }
    return editvid;
}


public bool Remux_Check(string format)
{
    bool container = false, codec = false;
    int len = format.Length;
    string sub;
    if (len > 2)
    {
        sub = format.Substring(0, 3);
        if (sub == "AVI" || sub == "AVC" || sub == "OGG")
            container = true;
        if (format.Substring(len - 3, 3) == "AVC")
            codec = true;
        if (len > 3)
        {
            sub = format.Substring(0, 4);
            if (sub == "H264" || (!editvid.MKV && sub == "H263"))
                container = true;
            sub = format.Substring(len - 4, 4);
            if (sub == "H263" || sub == "H264")
                codec = true;
            if (len > 4)
            {
                if (format.Substring(0, 5) == "H263+")
                    container = true;
                if (format.Substring(len - 5, 5) == "H263+")
                    codec = true;
                if (len > 5)
                {
                    if (format.Substring(0, 6) == "MPEG-4")
                        container = true;
                    if (len > 6)
                    {
                        sub = format.Substring(0, 7);
                        if (sub == "MPEG-PS" || (!editvid.MKV && sub == "MPEG-TS"))
                            container = true;
                        if (len > 7)
                        {
                            if (editvid.MKV && !editvid.bluray && format.Substring(0, 8) == "Matroska")
                                container = true;
                            if (len > 8)
                            {
                                if (format.Substring(0, 9) == "Quicktime")
                                    container = true;
                                if (format.Substring(len - 9, 9) == "DVD-Video")
                                    codec = true;
                                if (len > 9)
                                {
                                    if (format.Substring(0, 10) == "MPEG Video")
                                        container = true;
                                    if (format.Substring(len - 10, 10) == "MPEG Video")
                                        codec = true;
                                    if (len > 12)
                                    {
                                        if (format.Substring(0, 13) == "MPEG-4 Visual")
                                            container = true;
                                        if (format.Substring(len - 13, 13) == "MPEG-4 Visual")
                                            codec = true;
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }
    return container && codec;
}

    if (cmdClose.IsMouseOver) stop = true;
    if (cmdExport.IsMouseOver) stop = true;

    if (!stop) this.WindowState = (this.WindowState == WindowState.Normal) ? WindowState.Maximized 
}

