{
    string pf = appdir + "\\profiles.ini", data, prof, profs = "", of = txtFile.Text, output = "";
    bool read = false;
    if (File.Exists(pf))


private void MainForm_Drop(object sender, DragEventArgs e)
{
    if (e.Data.GetDataPresent(DataFormats.FileDrop))
    {
        string[] myfiles;
        video tmpvid = editVid;
        myfiles = (string[])e.Data.GetData(DataFormats.FileDrop);

        foreach (string path in myfiles)
        {
            editVid = load_file(path);
            videolist.Add(editVid);
            string[] q = new string[2];
            q[0] = editVid.jobname;
            q[1] = "Waiting...";
            VideoItem v = new VideoItem();
            v.FileName = q[0];
            v.Status = q[1];
           lstVideos.Items.Add(v);
        }
        foreach (string path in myfiles)
        {
            editVid = load_file(path);
            videolist.Add(editVid);
            string[] q = new string[2];
            q[0] = editVid.jobname;
            q[1] = "Waiting...";
            VideoItem v = new VideoItem();
            v.FileName = q[0];
            v.Status = q[1];
           lstVideos.Items.Add(v);
        }
        editVid = tmpvid;
    }
}


public static bool MPEG_Check(string format)
{
    int len = format.Length;
    bool container = false, codec = false;
    string sub = "";
    if (len > 3)
    {
        if (format.Substring(0, 4) == "BDAV")
            container = true;
        if (len > 6)
        {
            sub = format.Substring(0, 7);
            if (sub == "MPEG-TS" || sub == "MPEG-PS")
                container = true;
            if (len > 8)
            {
                if (format.Substring(format.Length - 9, 9) == "DVD-Video")
                    codec = true;
                if (len > 9)
                {
                    if (format.Substring(format.Length - 10, 10) == "MPEG Video")
                        codec = true;
                    if (format.Substring(0, 10) == "MPEG Video")
                        container = true;
                }
            }
        }
    }
    return container && codec;
}


private void cmdEdit_Click(object sender, RoutedEventArgs e)
{
    edit = true;

        int i = 0;
        foreach (VideoItem v in lstVideos.Items)
        {
            if (v.IsSelected)
            {
                Edit frmEdit = new Edit();
                editVid = videolist[i];
                edittracklist = editVid.tracks;
                setBlur(true);
                if ((bool)frmEdit.ShowDialog())
                    videolist[i] = editVid;
                setBlur(false);
            }
            i++;
        }
        foreach (VideoItem v in lstVideos.Items)
        {
            if (v.IsSelected)
            {
                Edit frmEdit = new Edit();
                editVid = videolist[i];
                edittracklist = editVid.tracks;
                setBlur(true);
                if ((bool)frmEdit.ShowDialog())
                    videolist[i] = editVid;
                setBlur(false);
            }
            i++;
        }
}


private void cmdDown_Click(object sender, RoutedEventArgs e)
{
    for (int i = lstVideos.Items.Count - 1; i >= 0; i--)
    {
        VideoItem v = (VideoItem)lstVideos.Items[i];
        if (i < videolist.Count - 1 && v.IsSelected)
        {
            if (strcmdgo == "Stop Encoding" && i == currentjob)
                currentjob++;
            if (strcmdgo == "Stop Encoding" && i == currentjob + 1)
                currentjob--;
            VideoItem itm1 = (VideoItem)lstVideos.Items[i];
            VideoItem itm2 = (VideoItem)lstVideos.Items[i + 1];
            video v1 = videolist[i];
            video v2 = videolist[i + 1];
            lstVideos.Items.RemoveAt(i + 1);
            lstVideos.Items.RemoveAt(i);
            lstVideos.Items.Insert(i, itm2);
            lstVideos.Items.Insert(i + 1, itm1);
            videolist[i] = v2;
            videolist[i + 1] = v1;
        }
    }
    for (int i = lstVideos.Items.Count - 1; i >= 0; i--)
    {
        VideoItem v = (VideoItem)lstVideos.Items[i];
        if (i < videolist.Count - 1 && v.IsSelected)
        {
            if (strcmdgo == "Stop Encoding" && i == currentjob)
                currentjob++;
            if (strcmdgo == "Stop Encoding" && i == currentjob + 1)
                currentjob--;
            VideoItem itm1 = (VideoItem)lstVideos.Items[i];
            VideoItem itm2 = (VideoItem)lstVideos.Items[i + 1];
            video v1 = videolist[i];
            video v2 = videolist[i + 1];
            lstVideos.Items.RemoveAt(i + 1);
            lstVideos.Items.RemoveAt(i);
            lstVideos.Items.Insert(i, itm2);
            lstVideos.Items.Insert(i + 1, itm1);
            videolist[i] = v2;
            videolist[i + 1] = v1;
        }
    }
    lstVideos.Focus();
}


public static bool AVC_Check(string format)
{
    bool avc = false;
    int len = format.Length;
    if (len > 8)
    {
        if (format == "AVC / AVC")
            avc = true;
        if (len > 9)
        {
            if (format == "BDAV / AVC")
                avc = true;
            if (len > 12)
                if (format == "MPEG-TS / AVC" || format == "MPEG-PS / AVC")
                    avc = true;
        }
    }
    return avc;
}


public static video Read_VideoInfo(video editvid)
{
    if (File.Exists(editvid.videoinfo))
    {
        editvid.inputformat = "";
        editvid.audioformat = "";
        editvid.inputvbitrate = "";
        editvid.inputabitrate = "";
        editvid.inputHeight = 0;
        editvid.inputWidth = 0;
        editvid.inputChannels = 0;
        editvid.inputFPS = 0;
        editvid.inputfilesize = "";
        editvid.fieldorder = "";
        editvid.length = 0;
        editvid.inputAspect = 0;
        editvid.inputChannels = 0;
        editvid.trackcount = 0;
        editvid.videoID = -1;
        editvid.audioID = -1;

        //Read videoinfo file, display contents
        StreamReader vread = File.OpenText(editvid.videoinfo);
        int intAudio = -1;
        string strInfo;
        string evov = "", evoa = "";
        string sub;
        while (vread.Peek() > -1)
        {
            strInfo = vread.ReadLine();
            if (strInfo.Length > 4)
            {
                if (strInfo.Substring(0, 5) == "Video" && intAudio == -1)
                    intAudio = 0;
                if (strInfo.Substring(0, 5) == "Audio" && intAudio == 0)
                    intAudio = 1;
                if (strInfo.Length > 34)
                {
                    int inum;
                    double dnum;
                    sub = strInfo.Substring(35, strInfo.Length - 35);
                    int isub = Int32.TryParse(sub, out inum) ? inum 
                    double dsub = Double.TryParse(sub, out dnum) ? dnum 
                    string psub = sub.Length > 6 ? sub.Substring(0, sub.Length - 6) 
                    int ipsub = Int32.TryParse(psub, out inum) ? inum 
                    switch (strInfo.Substring(0, 20))
                    {
                        case "Format              "
                            if (!editvid.avs)
                            {
                                if (editvid.inputformat == "" || editvid.inputformat == null)
                                    editvid.inputformat = sub;
                                switch (intAudio)
                                {
                                    case 0
                                        evov = sub;
                                        editvid.inputformat = editvid.inputformat + " / " + sub;
                                        break;
                                    case 1
                                        if (editvid.audioformat == "" || editvid.audioformat == null)
                                        {
                                            evoa = sub;
                                            editvid.audioformat = sub;
                                        }
                                        break;
                                }
                            }
                            else
                                editvid.inputformat = "Avisynth Script";
                            break;
                        case "Codec               "
                            switch (intAudio)
                            {
                                case 0
                                    if (!editvid.avs)
                                        editvid.inputformat = editvid.inputformat + " / " + sub;
                                    break;
                                case 1
                                    if (editvid.trackcount == 0)
                                        editvid.audioformat = sub;
                                    break;
                            }
                            break;
                        case "Bit rate            "
                            switch (intAudio)
                            {
                                case 0
                                    editvid.inputvbitrate = sub;
                                    break;
                                case 1
                                    if (editvid.trackcount == 0)
                                        editvid.inputabitrate = sub;
                                    break;
                            }
                            break;
                        case "Width               "
                            editvid.inputWidth = ipsub;
                            break;
                        case "Height              "
                            editvid.inputHeight = ipsub;
                            break;
                        case "Channel(s)          "
                            if (editvid.trackcount == 0)
                            {
                                editvid.outputChannels = isub;
                                editvid.inputChannels = isub;
                            }
                            break;
                        case "Display aspect ratio"
                            editvid.inputAspect = 0;
                            for (int i = 1; i < sub.Length; i++)
                                if ((sub.Substring(i, 1) == "/" || sub.Substring(i, 1) == "
                                    editvid.inputAspect = Convert.ToDouble(sub.Substring(0, i)) / Convert.ToDouble(sub.Substring(i + 1, sub.Length - i - 1));
                            for (int i = 1; i < sub.Length; i++)
                                if ((sub.Substring(i, 1) == "/" || sub.Substring(i, 1) == "
                                    editvid.inputAspect = Convert.ToDouble(sub.Substring(0, i)) / Convert.ToDouble(sub.Substring(i + 1, sub.Length - i - 1));
                            if (editvid.inputAspect == 0)
                                editvid.inputAspect = dsub;
                            if (!editvid.autocropped)
                                editvid.autocropAspect = editvid.inputAspect;
                            break;
                        case "Frame rate          "
                            editvid.outputFPS = dsub;
                            editvid.inputFPS = dsub;
                            break;
                        case "Duration            "
                            double hours = 0, minutes = 0, seconds = 0;
                            int cursor = 0;
                            if (intAudio == -1)
                            {
                                int i;
                                for (i = 1; i < sub.Length; i++)
                                    if (sub.Substring(i, 1) == "h")
                                    {
                                        hours = Convert.ToDouble(sub.Substring(0, i));
                                        cursor = i + 1;
                                    }
                                for (i = 1; i < sub.Length; i++)
                                    if (sub.Substring(i, 1) == "h")
                                    {
                                        hours = Convert.ToDouble(sub.Substring(0, i));
                                        cursor = i + 1;
                                    }
                                for (i = cursor + 1; i < (sub.Length - 1); i++)
                                    if (sub.Substring(i, 2) == "mn")
                                    {
                                        minutes = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                        cursor = i + 3;
                                    }
                                for (i = cursor + 1; i < (sub.Length - 1); i++)
                                    if (sub.Substring(i, 2) == "mn")
                                    {
                                        minutes = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                        cursor = i + 3;
                                    }
                                for (i = cursor + 1; i < sub.Length; i++)
                                    if (sub.Substring(i, 1) == "s" && sub.Substring(i - 1, 2) != "ms")
                                    {
                                        seconds = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                        cursor = i + 1;
                                    }
                                for (i = cursor + 1; i < sub.Length; i++)
                                    if (sub.Substring(i, 1) == "s" && sub.Substring(i - 1, 2) != "ms")
                                    {
                                        seconds = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                        cursor = i + 1;
                                    }
                                editvid.length = hours * 3600 + minutes * 60 + seconds;
                            }
                            break;
                        case "File size           "
                            editvid.inputfilesize = sub;
                            break;
                        case "Scan type           "
                            if (sub == "Progressive")
                                editvid.isinterlaced = false;
                            else
                                editvid.isinterlaced = true;
                            break;
                        case "Scan order          "
                            editvid.isinterlaced = true;
                            editvid.film = false;
                            if (sub == "Bottom Field First")
                                editvid.fieldorder = "BFF";
                            else
                            {
                                editvid.fieldorder = "TFF";
                                if (sub == "2
                                {
                                    editvid.film = true;
                                    if (Math.Abs(editvid.inputFPS - 23.976) < 0.02)
                                        editvid.inputFPS = 29.97;
                                }
                            }
                            break;
                        case "ID                  "
                            string sd = sub;
                            for (int i = 0; i < sd.Length; i++)
                                if (sd.Substring(i, 1) == "(")
                                    sd = sd.Substring(0, i - 1);
                            for (int i = 0; i < sd.Length; i++)
                                if (sd.Substring(i, 1) == "(")
                                    sd = sd.Substring(0, i - 1);
                            int id = Int32.TryParse(sd, out inum) ? inum 
                            switch (intAudio)
                            {
                                case 0
                                    editvid.videoID = id;
                                    break;
                                case 1
                                    if (editvid.trackcount == 0)
                                        editvid.audioID = id;
                                    break;
                            }
                            break;
                        case "Sampling rate       "
                            if (editvid.trackcount == 0 && sub.Length > 4)
                            {
                                string sr = sub.Substring(0, sub.Length - 4);
                                double srd = double.TryParse(sr, out dnum) ? dnum 
                                editvid.inputsample = Convert.ToInt32(srd * 1000);
                            }
                            editvid.trackcount++;
                            intAudio++;
                            break;
                    }
                }
            }
        }
        while (vread.Peek() > -1)
        {
            strInfo = vread.ReadLine();
            if (strInfo.Length > 4)
            {
                if (strInfo.Substring(0, 5) == "Video" && intAudio == -1)
                    intAudio = 0;
                if (strInfo.Substring(0, 5) == "Audio" && intAudio == 0)
                    intAudio = 1;
                if (strInfo.Length > 34)
                {
                    int inum;
                    double dnum;
                    sub = strInfo.Substring(35, strInfo.Length - 35);
                    int isub = Int32.TryParse(sub, out inum) ? inum 
                    double dsub = Double.TryParse(sub, out dnum) ? dnum 
                    string psub = sub.Length > 6 ? sub.Substring(0, sub.Length - 6) 
                    int ipsub = Int32.TryParse(psub, out inum) ? inum 
                    switch (strInfo.Substring(0, 20))
                    {
                        case "Format              "
                            if (!editvid.avs)
                            {
                                if (editvid.inputformat == "" || editvid.inputformat == null)
                                    editvid.inputformat = sub;
                                switch (intAudio)
                                {
                                    case 0
                                        evov = sub;
                                        editvid.inputformat = editvid.inputformat + " / " + sub;
                                        break;
                                    case 1
                                        if (editvid.audioformat == "" || editvid.audioformat == null)
                                        {
                                            evoa = sub;
                                            editvid.audioformat = sub;
                                        }
                                        break;
                                }
                            }
                            else
                                editvid.inputformat = "Avisynth Script";
                            break;
                        case "Codec               "
                            switch (intAudio)
                            {
                                case 0
                                    if (!editvid.avs)
                                        editvid.inputformat = editvid.inputformat + " / " + sub;
                                    break;
                                case 1
                                    if (editvid.trackcount == 0)
                                        editvid.audioformat = sub;
                                    break;
                            }
                            break;
                        case "Bit rate            "
                            switch (intAudio)
                            {
                                case 0
                                    editvid.inputvbitrate = sub;
                                    break;
                                case 1
                                    if (editvid.trackcount == 0)
                                        editvid.inputabitrate = sub;
                                    break;
                            }
                            break;
                        case "Width               "
                            editvid.inputWidth = ipsub;
                            break;
                        case "Height              "
                            editvid.inputHeight = ipsub;
                            break;
                        case "Channel(s)          "
                            if (editvid.trackcount == 0)
                            {
                                editvid.outputChannels = isub;
                                editvid.inputChannels = isub;
                            }
                            break;
                        case "Display aspect ratio"
                            editvid.inputAspect = 0;
                            for (int i = 1; i < sub.Length; i++)
                                if ((sub.Substring(i, 1) == "/" || sub.Substring(i, 1) == "
                                    editvid.inputAspect = Convert.ToDouble(sub.Substring(0, i)) / Convert.ToDouble(sub.Substring(i + 1, sub.Length - i - 1));
                            if (editvid.inputAspect == 0)
                                editvid.inputAspect = dsub;
                            if (!editvid.autocropped)
                                editvid.autocropAspect = editvid.inputAspect;
                            break;
                        case "Frame rate          "
                            editvid.outputFPS = dsub;
                            editvid.inputFPS = dsub;
                            break;
                        case "Duration            "
                            double hours = 0, minutes = 0, seconds = 0;
                            int cursor = 0;
                            if (intAudio == -1)
                            {
                                int i;
                                for (i = 1; i < sub.Length; i++)
                                    if (sub.Substring(i, 1) == "h")
                                    {
                                        hours = Convert.ToDouble(sub.Substring(0, i));
                                        cursor = i + 1;
                                    }
                                for (i = cursor + 1; i < (sub.Length - 1); i++)
                                    if (sub.Substring(i, 2) == "mn")
                                    {
                                        minutes = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                        cursor = i + 3;
                                    }
                                for (i = cursor + 1; i < sub.Length; i++)
                                    if (sub.Substring(i, 1) == "s" && sub.Substring(i - 1, 2) != "ms")
                                    {
                                        seconds = Convert.ToDouble(sub.Substring(cursor, i - cursor));
                                        cursor = i + 1;
                                    }
                                editvid.length = hours * 3600 + minutes * 60 + seconds;
                            }
                            break;
                        case "File size           "
                            editvid.inputfilesize = sub;
                            break;
                        case "Scan type           "
                            if (sub == "Progressive")
                                editvid.isinterlaced = false;
                            else
                                editvid.isinterlaced = true;
                            break;
                        case "Scan order          "
                            editvid.isinterlaced = true;
                            editvid.film = false;
                            if (sub == "Bottom Field First")
                                editvid.fieldorder = "BFF";
                            else
                            {
                                editvid.fieldorder = "TFF";
                                if (sub == "2
                                {
                                    editvid.film = true;
                                    if (Math.Abs(editvid.inputFPS - 23.976) < 0.02)
                                        editvid.inputFPS = 29.97;
                                }
                            }
                            break;
                        case "ID                  "
                            string sd = sub;
                            for (int i = 0; i < sd.Length; i++)
                                if (sd.Substring(i, 1) == "(")
                                    sd = sd.Substring(0, i - 1);
                            int id = Int32.TryParse(sd, out inum) ? inum 
                            switch (intAudio)
                            {
                                case 0
                                    editvid.videoID = id;
                                    break;
                                case 1
                                    if (editvid.trackcount == 0)
                                        editvid.audioID = id;
                                    break;
                            }
                            break;
                        case "Sampling rate       "
                            if (editvid.trackcount == 0 && sub.Length > 4)
                            {
                                string sr = sub.Substring(0, sub.Length - 4);
                                double srd = double.TryParse(sr, out dnum) ? dnum 
                                editvid.inputsample = Convert.ToInt32(srd * 1000);
                            }
                            editvid.trackcount++;
                            intAudio++;
                            break;
                    }
                }
            }
        }
        vread.Close();
        string path = editvid.path;
        int len = path.Length;

        string ext = len > 3 ? path.Substring(len - 3, 3).ToLower() 

        //Check if input file is EVO
        string format = editvid.inputformat;
        bool evo = (ext == "evo" && EVO_Check(format));
        if (evo)
            switch (strEVO)
            {
                case "Always Ask"
                    if (MessageBox.Show("Source has been detected as EVO. Would you lie to remux to M2TS for better compatibility?", "Remux EVO?", MessageBoxButton.YesNo) == MessageBoxResult.Yes)
                        editvid = Remux_EVO(editvid, evov, evoa, editvid.videoID, editvid.audioID);
                    return editvid;
                case "Always Convert to M2TS"
                    editvid = Remux_EVO(editvid, evov, evoa, editvid.videoID, editvid.audioID);
                    return editvid;
            }

        //Check DGIndex & DGAVCIndex compatibility
        bool mpeg = MPEG_Check(format), avc = AVC_Check(format);
        string jname = editvid.jobname;
        if (mpeg && !File.Exists(editvid.mpeginfo) && jname.Length > 2)
        {
            bool vob = jname.Substring(jname.Length - 3, 3).ToUpper() == "VOB";

            if ((vob && preloadVOB) || (!vob && preloadMPG))
                editvid = MPEGInfo(editvid);
        }
        if (avc && preloadAVC)
            editvid = AVCInfo(editvid);


        //Check for interlace
        if (editvid.isinterlaced != editvid.Deinterlace)
            if (editvid.isinterlaced)
                switch (strDeinterlace)
                {
                    case "Always Deinterlace"
                        editvid.interlaced = false;
                        editvid.Deinterlace = true;
                        break;
                    case "Never Deinterlace"
                        editvid.interlaced = true;
                        editvid.Deinterlace = false;
                        break;
                    default
                        if (MessageBox.Show("Interlace was detected, would you like to deinterlace?", "Deinterlace?", MessageBoxButton.YesNo) == MessageBoxResult.Yes)
                        {
                            editvid.interlaced = false;
                            editvid.Deinterlace = true;
                        }
                        else
                        {
                            editvid.interlaced = true;
                            editvid.Deinterlace = false;
                        }
                        break;
                }
            else
                if (MessageBox.Show("Interlace wasn't detected, disable deinterlace? (recommended)", "Disable Deinterlace?", MessageBoxButton.YesNo) == MessageBoxResult.Yes)
                {
                    editvid.Deinterlace = false;
                    editvid.interlaced = true;
                }

        //Check for film content
        double fps = 24;
        switch (strFilmMode)
        {
            case "Based on Input FPS"
                if (Math.Abs(editvid.inputFPS - 29.97) < 0.02 || Math.Abs(editvid.inputFPS - 59.94) < 0.02)
                    fps = 23.976;
                if (Math.Abs(editvid.inputFPS - 25) < 0.02 || Math.Abs(editvid.inputFPS - 50) < 0.02)
                    fps = 25;
                break;
            case "NTSC (23.976 FPS)"
                fps = 23.976;
                break;
            case "PAL (25 FPS)"
                fps = 25;
                break;
        }

        if (editvid.film && !editvid.changefps)
            switch (strFilm)
            {
                case "Always Ask"
                    if (editvid.percentfilm == "" || editvid.percentfilm == null)
                        editvid.percentfilm = " FILM";
                    if (MessageBox.Show("Source was detected as" + editvid.percentfilm.ToLower() + ", would you like the frame rate set at " + fps + " fps? (recommended)", "Convert FPS?", MessageBoxButton.YesNo) == MessageBoxResult.Yes)
                    {
                        editvid.changefps = true;
                        editvid.customFPS = fps;
                    }
                    break;
                case "Always Convert FPS"
                    editvid.changefps = true;
                    editvid.customFPS = fps;
                    break;
            }

        //Check sample rate
        if (!editvid.forcesample)
        {
            List<int> rates = new List<int>();
            double isa, min;
            int rate;
            isa = editvid.inputsample;
            rates.Add(0);
            rates.Add(8000);
            rates.Add(11025);
            rates.Add(16000);
            rates.Add(22050);
            rates.Add(24000);
            rates.Add(32000);
            rates.Add(44100);
            rates.Add(48000);
            rates.Add(88200);
            rates.Add(96000);
            bool common = false;
            foreach (int i in rates)
                if (i == isa)
                    common = true;
            foreach (int i in rates)
                if (i == isa)
                    common = true;
            if (!common)
            {
                rate = 8000;
                min = Math.Abs(isa - 8000);
                foreach (int i in rates)
                    if (Math.Abs(isa - i) < min)
                    {
                        min = Math.Abs(isa - i);
                        rate = i;
                    }
                foreach (int i in rates)
                    if (Math.Abs(isa - i) < min)
                    {
                        min = Math.Abs(isa - i);
                        rate = i;
                    }
                MessageBoxResult result;
                if (strSample == "Always Ask")
                    result = MessageBox.Show("The audio sample rate is not a common value (" + isa + " Hz), would you like to force it to the nearest compatible sample rate? (" + rate + " Hz)", "Change Sample Rate?", MessageBoxButton.YesNo);
                else
                    result = (strSample == "Always Convert") ? MessageBoxResult.Yes 
                if (result == MessageBoxResult.Yes)
                {
                    editvid.forcesample = true;
                    editvid.outputsample = rate;
                }
            }
        }
    }

    return editvid;
}

private IntPtr WndProc(IntPtr hwnd, int msg, IntPtr wParam,
  IntPtr lParam, ref bool handled)
{
    if (msg == 0x031E)
    {
        int aero = 0;
        DwmIsCompositionEnabled(ref aero);
        win6 = (aero > 0);
        if (win6) this.Background = Brushes.Transparent; else this.Background = SystemColors.ControlBrush;
        if (win6) setGlass(this);
        handled = true;
    }
    return IntPtr.Zero;
}


private void cmdUp_Click(object sender, RoutedEventArgs e)
{
    for (int i = 0; i < lstVideos.Items.Count; i++)
    {
        VideoItem v = (VideoItem)lstVideos.Items[i];
        if (i > 0 && v.IsSelected)
        {
            if (strcmdgo == "Stop Encoding" && i == currentjob)
                currentjob--;
            if (strcmdgo == "Stop Encoding" && i == currentjob + 1)
                currentjob++;
            VideoItem itm1 = (VideoItem)lstVideos.Items[i - 1];
            VideoItem itm2 = (VideoItem)lstVideos.Items[i];
            video v1 = videolist[i - 1];
            video v2 = videolist[i];
            lstVideos.Items.RemoveAt(i);
            lstVideos.Items.RemoveAt(i - 1);
            lstVideos.Items.Insert(i - 1, itm2);
            lstVideos.Items.Insert(i, itm1);
            videolist[i - 1] = v2;
            videolist[i] = v1;
        }
    }
    for (int i = 0; i < lstVideos.Items.Count; i++)
    {
        VideoItem v = (VideoItem)lstVideos.Items[i];
        if (i > 0 && v.IsSelected)
        {
            if (strcmdgo == "Stop Encoding" && i == currentjob)
                currentjob--;
            if (strcmdgo == "Stop Encoding" && i == currentjob + 1)
                currentjob++;
            VideoItem itm1 = (VideoItem)lstVideos.Items[i - 1];
            VideoItem itm2 = (VideoItem)lstVideos.Items[i];
            video v1 = videolist[i - 1];
            video v2 = videolist[i];
            lstVideos.Items.RemoveAt(i);
            lstVideos.Items.RemoveAt(i - 1);
            lstVideos.Items.Insert(i - 1, itm2);
            lstVideos.Items.Insert(i, itm1);
            videolist[i - 1] = v2;
            videolist[i] = v1;
        }
    }
    lstVideos.Focus();
}


public static string randomstring(int size)
{
    StringBuilder builder = new StringBuilder();
    Random rnd = new Random();
    Char ch;
    for (int i = 0; i < size; i++)
    {
        ch = Convert.ToChar(Convert.ToInt32(26 * rnd.NextDouble() + 64));
        builder.Append(ch);
    }
    for (int i = 0; i < size; i++)
    {
        ch = Convert.ToChar(Convert.ToInt32(26 * rnd.NextDouble() + 64));
        builder.Append(ch);
    }
    return builder.ToString().ToLower();
}


public string Display_Aspect(double aspect)
{
    string label = Math.Round(aspect, 2, MidpointRounding.AwayFromZero).ToString(); ;
    if (Math.Abs(aspect - 2.39) < .05)
        label = "2.39
    if ((aspect >= 1.85 && Math.Abs(aspect - 1.85) < 0.05) || Math.Abs(aspect - 1.85) < 0.035)
        label = "1.85
    if ((aspect <= 1.78 && Math.Abs(aspect - 1.78) < 0.05) || Math.Abs(aspect - 1.78) < 0.035)
        label = "16
    if (Math.Abs(aspect - 1.33) < 0.05)
        label = "4
    return label;
}


public string Display_Length(double length)
{
    int hour, min, sec;
    hour = (int)(length / 3600);
    length = length - (hour * 3600);
    min = (int)(length / 60);
    length = length - (min * 60);
    sec = (int)(length);
    return hour > 0 ? hour + "h " 
}


private void Update_Jobs()
{
    lstVideos.Items.Clear();
    string[] q = new string[1];
    ListViewItem itm;
    foreach (video v in videolist)
    {
        itm = new ListViewItem();
        q[0]= v.jobname;
        if (v.encoded)
            q[1] = "Done";
        else
            q[1] = "Waiting...";
        itm.Content = q;
        lstVideos.Items.Add(itm);
    }
    foreach (video v in videolist)
    {
        itm = new ListViewItem();
        q[0]= v.jobname;
        if (v.encoded)
            q[1] = "Done";
        else
            q[1] = "Waiting...";
        itm.Content = q;
        lstVideos.Items.Add(itm);
    }
}


