private void cmdOK_Click(object sender, RoutedEventArgs e)
{
    DialogResult = true;
    tempdir = txtTemp.Text;
    outputdir = txtOutput.Text;
    logdir = txtLog.Text;
    if (tempdir != MainForm.tempDir)
        if (System.Windows.MessageBox.Show("Clear files in current temporary folder?\n\nWARNING! This will remove all items from the job queue!", "Clear Temp?", MessageBoxButton.YesNo) == MessageBoxResult.Yes)
            clear_temp();
    MainForm.tempDir = tempdir;
    MainForm.strResize = cboResize.Text;
    MainForm.strShutdown = cboShutdown.Text;
    MainForm.boolUpdate = (bool)optOn.IsChecked;
    MainForm.strAVSAudio = cboAVSAudio.Text;
    MainForm.strDeinterlace = cboDeinterlace.Text;
    MainForm.strFilm = cboFilm.Text;
    MainForm.strEVO = cboEVO.Text;
    MainForm.useiTunes = (bool)optiTunesOn.IsChecked;
    MainForm.customoutput = (bool)optCustomOutput.IsChecked;
    MainForm.outputdir = outputdir;
    MainForm.logDir = logdir;
    MainForm.strDefaultProfile = cboSettings.Text;
    MainForm.bit64 = (bool)opt64bit.IsChecked;
    MainForm.manual32bit = manual32bit;
    MainForm.manual64bit = manual64bit;
    MainForm.strFilmMode = cboFilmMode.Text;
    MainForm.MPGDec = (bool)optMPGOn.IsChecked;
    MainForm.preloadVOB = (bool)chkVOB.IsChecked;
    MainForm.AVCDec = (bool)optAVCOn.IsChecked;
    MainForm.FFDec = (bool)optAVCOn.IsChecked;
    MainForm.preloadMPG = (bool)chkMPG.IsChecked;
    MainForm.preloadAVC = (bool)chkAVC.IsChecked;
    MainForm.strSample = cboSample.Text;
    MainForm.strAutoSize = strAutoSize;
    if (!Directory.Exists(tempdir))
        Directory.CreateDirectory(tempdir);
    if (!Directory.Exists(outputdir))
        Directory.CreateDirectory(outputdir);
    if (!Directory.Exists(logdir))
        Directory.CreateDirectory(logdir);
    this.Close();
}


private void Read_VideoInfo()
{
    //Clear input labels
    lblVFormat.Content = "Loading...";
    lblAFormat.Content = "";
    lblVBitrate.Content = "";
    lblABitrate.Content = "";
    lblResolution.Content = "";
    lblChannels.Content = "";
    lblAspect.Content = "";
    lblFrameRate.Content = "";
    lblLength.Content = "";
    lblFileSize.Content = "";

    //Read videoinfo file
    editvid = MainForm.Read_VideoInfo(editvid);

    //Display Results
    lblVFormat.Content = editvid.inputformat;
    lblAFormat.Content = editvid.audioformat;
    lblVBitrate.Content = editvid.inputvbitrate;
    lblABitrate.Content = editvid.inputabitrate;
    lblResolution.Content = editvid.inputWidth + " x " + editvid.inputHeight;
    lblChannels.Content = editvid.inputChannels > 0 ? editvid.inputChannels + " channel" + (editvid.inputChannels > 1 ? "s" 
    lblFrameRate.Content = editvid.inputFPS + " fps";
    lblFileSize.Content = editvid.inputfilesize;
    cboFieldOrder.Text = editvid.fieldorder;
    lblLength.Content = Display_Length(editvid.length);
    lblAspect.Content = Display_Aspect(editvid.inputAspect);
    chkDownmix.IsEnabled = editvid.inputChannels > 2;
    //trackbar.Maximum = Convert.ToInt32(editvid.length);

    //Check ffmpeg compatibility
    int len = editvid.path.Length;
    string ext = len > 3 ? editvid.path.Substring(len - 3, 3).ToLower() 
    bool ffmpeg = MainForm.ffmpeg_check(ext);
    chkDirectshow.Visibility = ffmpeg ? Visibility.Visible 

    //Check if video can be remuxed
    chkRemuxVideo.Visibility = Remux_Check(editvid.inputformat) ? Visibility.Visible 

    //Check video for demuxed audio tracks (mpeg or avc)
    string format = editvid.inputformat;
    bool mpeg = MainForm.MPEG_Check(format), avc = MainForm.AVC_Check(format);
    if (mpeg || avc)
    {
        cboSourceTrack.Items.Clear();
        cboSourceTrack.Items.Add("Default");
        cboSourceTrack.SelectedIndex = 0;
        for (int i = 0; i < editvid.trackcount; i++)
            cboAudio.Items.Add("Track " + (i + 1));
        for (int i = 0; i < editvid.trackcount; i++)
            cboAudio.Items.Add("Track " + (i + 1));
        lblSourceTrack.Visibility = visible(cboAudio.Items.Count > 2);
        cboSourceTrack.Visibility = lblSourceTrack.Visibility;
        txtDelay.Text = Convert.ToString(editvid.delay * 1000);
    }

    //Set default resolution options
    if (!editvid.CustomSize)
    {
        editvid.customX = editvid.inputWidth;
        editvid.customY = editvid.inputHeight;
    }
    if (!editvid.MaxSize)
    {
        editvid.maxX = editvid.inputWidth;
        editvid.maxY = editvid.inputHeight;
    }

    //Create Input AVS file for Video preview
    //editvid = MainForm.inputavs(editvid, ffmpeg, mpeg, avc);

    //double iaspect = editvid.inputAspect;

    videoPreview.Source = new Uri(editvid.path);
    videoPreview.Position = TimeSpan.FromSeconds(editvid.length * 0.1);
    videoPreview.Play();
    videoPreview.Pause();
    //videoPreview.Position = TimeSpan.FromSeconds(0);
    load = true;
}


public static video Set_defaults(video editvid)
{
    editvid.MKV = false;
    editvid.iPod = false;
    editvid.remuxaudio = false;
    editvid.passes = 2;
