source_elements.source:
	cb.setColorStroke(Color.black);
	cb.setColorFill(Color.red);
	cb.rectangle(x, y, 100, 200);
	cb.fill();
	cb.setLineWidth(2);
	cb.rectangle(x, y, 200, 200);
	cb.stroke();
}


	/**
	 * Prints 3 circles in different colors that intersect with eachother.
	 * @param x
	 * @param y
	 * @param cb
	 * @throws Exception
	 */
	public static void pictureCircles(float x, float y, PdfContentByte cb) throws Exception {
		PdfGState gs = new PdfGState();
		gs.setBlendMode(PdfGState.BM_SOFTLIGHT);
		gs.setFillOpacity(0.7f);
		cb.setGState(gs);
		cb.setColorFill(Color.gray);
		cb.circle(x + 70, y + 70, 50);
		cb.fill();
		cb.circle(x + 100, y + 130, 50);
		cb.fill();
		cb.circle(x + 130, y + 70, 50);
		cb.fill();
	}


	/**
	 * Demonstrates the Transparency functionality.
	 * @param args no arguments needed
	 */
	public static void main(String[] args) throws Exception {
		System.out.println("Transparency Groups");
		// step 1
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("groups.pdf"));
		// step 3
		document.open();
		// step 4
		BaseFont bf = BaseFont.createFont("Helvetica", "Cp1252", false);
		PdfContentByte cb = writer.getDirectContent();
		float gap = (document.getPageSize().width() - 400) / 3;
            
		pictureBackdrop(gap, 500, cb);
		pictureBackdrop(200 + 2 * gap, 500, cb);
		pictureBackdrop(gap, 500 - 200 - gap, cb);
		pictureBackdrop(200 + 2 * gap, 500 - 200 - gap, cb);

		PdfTemplate tp;
		PdfTransparencyGroup group;
            
		tp = cb.createTemplate(200, 200);
		pictureCircles(0, 0, tp);
		group = new PdfTransparencyGroup();
		group.setIsolated(true);
		group.setKnockout(true);
		tp.setGroup(group);
		cb.addTemplate(tp, gap, 500);
            
		tp = cb.createTemplate(200, 200);
		pictureCircles(0, 0, tp);
		group = new PdfTransparencyGroup();
		group.setIsolated(true);
		group.setKnockout(false);
		tp.setGroup(group);
		cb.addTemplate(tp, 200 + 2 * gap, 500);
            
		tp = cb.createTemplate(200, 200);
		pictureCircles(0, 0, tp);
		group = new PdfTransparencyGroup();
		group.setIsolated(false);
		group.setKnockout(true);
		tp.setGroup(group);
		cb.addTemplate(tp, gap, 500 - 200 - gap);

		tp = cb.createTemplate(200, 200);
		pictureCircles(0, 0, tp);
		group = new PdfTransparencyGroup();
		group.setIsolated(false);
		group.setKnockout(false);
		tp.setGroup(group);
		cb.addTemplate(tp, 200 + 2 * gap, 500 - 200 - gap);

		// step 5
		document.close();
	}

		System.out.println("Stencil");
        
		// step 1
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);

		// step 2
		// we create a writer that listens to the document
		// and directs a PDF-stream to a file
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("pattern.pdf"));
            
		// step 3
		document.open();
            
		// step 4
		PdfContentByte cb = writer.getDirectContent();
		PdfTemplate tp = cb.createTemplate(400, 300);
		PdfPatternPainter pat = cb.createPattern(15, 15, null);
		pat.rectangle(5, 5, 5, 5);
		pat.fill();
		PdfSpotColor spc_cmyk = new PdfSpotColor("PANTONE 280 CV", 0.25f, new CMYKColor(0.9f, .2f, .3f, .1f));
		SpotColor spot = new SpotColor(spc_cmyk);
		tp.setPatternFill(pat, spot, .9f);
		tp.rectangle(0, 0, 400, 300);
		tp.fill();
		cb.addTemplate(tp, 50, 50);
		PdfPatternPainter pat2 = cb.createPattern(10, 10, null);
		pat2.setLineWidth(2);
		pat2.moveTo(-5, 0);
		pat2.lineTo(10, 15);
		pat2.stroke();
		pat2.moveTo(0, -5);
		pat2.lineTo(15, 10);
		pat2.stroke();
		cb.setLineWidth(1);
		cb.setColorStroke(Color.black);
		cb.setPatternFill(pat2, Color.red);
		cb.rectangle(100, 400, 30, 210);
		cb.fillStroke();
		cb.setPatternFill(pat2, Color.green);
		cb.rectangle(150, 400, 30, 100);
		cb.fillStroke();
		cb.setPatternFill(pat2, Color.blue);
		cb.rectangle(200, 400, 30, 130);
		cb.fillStroke();
		cb.setPatternFill(pat2, new GrayColor(0.5f));
		cb.rectangle(250, 400, 30, 80);
		cb.fillStroke();
		cb.setPatternFill(pat2, new GrayColor(0.7f));
		cb.rectangle(300, 400, 30, 170);
		cb.fillStroke();
		cb.setPatternFill(pat2, new GrayColor(0.9f));
		cb.rectangle(350, 400, 30, 40);
		cb.fillStroke();

		// step 5
		document.close();
	}

		System.out.println("Painting Patterns");
        
		// step 1
		Document document = new Document();
        
		// step 2
		// we create a writer that listens to the document
		// and directs a PDF-stream to a file
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("patterns.pdf"));
            
		// step 3
		document.open();
            
		// step 4
		PdfContentByte cb = writer.getDirectContent();
            
		BaseFont bf = BaseFont.createFont("Times-Roman", "winansi", false);
            
		// step 5
            
		// Image instance to be placed in PdfPatternPainter canvas. Any nice one?
		Image img = Image.getInstance("pngnow.png");

		PdfPatternPainter p = cb.createPattern(60f, 60f, 60f, 60f);
		PdfPatternPainter p1 = cb.createPattern(60f, 60f, 60f, 60f);
		PdfPatternPainter p2 = cb.createPattern(img.scaledWidth(), img.scaledHeight(), img.scaledWidth(), img.scaledHeight());
            
            
		// step 6
            
		// A star pattern taken from Adobe PDF Reference Book p.207
		String star = "0.3 g\n15.000 27.000 m\n"
			+ "7.947 5.292 l\n26.413 18.708 l\n"
			+ "3.587 18.708 l\n22.053 5.292 l\nf\n"
			+ "45.000 57.000 m\n37.947 35.292 l\n"
			+ "56.413 48.708 l\n33.587 48.708 l\n"
			+ "52.053 35.292 l\nf\n"
			+ "0.7 g\n15.000 57.000 m\n"
			+ "7.947 35.292 l\n26.413 48.708 l\n"
			+ "3.587 48.708 l\n22.053 35.292 l\nf\n"
			+ "45.000 27.000 m\n37.947 5.292 l\n"
			+ "56.413 18.708 l\n33.587 18.708 l\n"
			+ "52.053 5.292 l\nf";
            
		p.setLiteral(star);
            
		// A Pattern with some text drawing
		p1.setGrayFill(0.3f);
		p1.setFontAndSize(bf, 12);
		p1.beginText();
		p1.setTextMatrix(1f, 0f, 0f, 1f, 0f, 0f);
		p1.showText("A B C D");
		p1.endText();
		p1.moveTo(0f, 0f);
		p1.lineTo(60f, 60f);
		p1.stroke();
            
		// A pattern with an image and position
		p2.addImage(img, img.scaledWidth(), 0f, 0f, img.scaledHeight(), 0f, 0f);
		p2.setPatternMatrix(1f, 0f, 0f, 1f, 60f, 60f);
            
		// See if we can apply the pattern color to chunk, phrase or paragraph
		PatternColor pat = new PatternColor(p);
		PatternColor pat1 = new PatternColor(p1);
		PatternColor pat2 = new PatternColor(p2);
		String text = "Text with pattern";
		document.add(new Paragraph(text, FontFactory.getFont(FontFactory.HELVETICA, 60, Font.BOLD, new GrayColor(0.3f))));
		document.add(new Paragraph(text, FontFactory.getFont(FontFactory.HELVETICA, 60, Font.BOLD, pat)));
            
		// draw a rectangle filled with star pattern
		cb.setPatternFill(p);
		cb.setGrayStroke(0.0f);
		cb.rectangle(20, 20, 284, 120);
		cb.fillStroke();
            
		// draw some characters filled with star.
		// Note
		// otherwise, you will not be able to see the character glyph
		// since the glyph path is filled by pattern
		cb.beginText();
		cb.setFontAndSize(bf, 1);
		cb.setTextMatrix(270f, 0f, 0f, 270f, 20f, 100f);
		cb.setGrayFill(0.9f);
		cb.showText("ABC");
		cb.setPatternFill(p);
		cb.moveTextWithLeading(0.0f, 0.0f);
		cb.showText("ABC");
		cb.endText();
		cb.setPatternFill(p);
            
		// draw a circle. Similar to rectangle
		cb.setGrayStroke(0.0f);
		cb.circle(150f, 400f, 150f);
		cb.fillStroke();
            
		// New Page to draw text in the pattern painter's canvas
		document.newPage();
            
		document.add(new Paragraph(text, FontFactory.getFont(FontFactory.HELVETICA, 60, Font.BOLD, new GrayColor(0.3f))));
		document.add(new Paragraph(text, FontFactory.getFont(FontFactory.HELVETICA, 60, Font.BOLD, pat1)));
		// draw a rectangle
		cb.setPatternFill(p1);
		cb.setGrayStroke(0.0f);
		cb.rectangle(0, 0, 284, 120);
		cb.fillStroke();
            
		// draw some characters
		cb.beginText();
		cb.setFontAndSize(bf, 1);
		cb.setTextMatrix(270f, 0f, 0f, 270f, 20f, 100f);
		cb.setGrayFill(0.9f);
		cb.showText("ABC");
		cb.setPatternFill(p1);
		cb.moveTextWithLeading(0.0f, 0.0f);
		cb.showText("ABC");
		cb.endText();
            
		// draw a circle
		cb.setPatternFill(p1);
		cb.setGrayStroke(0.0f);
		cb.circle(150f, 400f, 150f);
		cb.fillStroke();
            
		// New page to place image in the pattern painter's canvas
		document.newPage();
		document.add(new Paragraph(text, FontFactory.getFont(FontFactory.HELVETICA, 60, Font.BOLD, new GrayColor(0.3f))));
		document.add(new Paragraph(text, FontFactory.getFont(FontFactory.HELVETICA, 60, Font.BOLD, pat2)));

		// The original Image for comparison reason.
		// Note
		cb.addImage(img, img.scaledWidth(), 0f, 0f, img.scaledHeight(), 350f, 400f);
            
		// draw a rectangle
		cb.setPatternFill(p2);
		cb.setGrayStroke(0.0f);
		cb.rectangle(60, 60, 300, 120);
		cb.fillStroke();
            
		// draw some characters.
		// Note
		// since colors in image will be clipped to character glyph path
		cb.beginText();
		cb.setFontAndSize(bf, 1);
		cb.setTextMatrix(270f, 0f, 0f, 270f, 60f, 120f);
		cb.setPatternFill(p2);
		cb.showText("ABC");
		cb.endText();
            
		// draw a circle
		cb.setPatternFill(p2);
		cb.setGrayStroke(0.0f);
		cb.circle(150f, 400f, 150f);
		cb.fillStroke();
        
		// finally, we close the document
		document.close();
	}

		System.out.println("Shading");
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("shading.pdf"));
		document.open();

		PdfFunction function1 = PdfFunction.type2(writer, new float[]{0, 1}, null,
			new float[]{.929f, .357f, 1, .298f}, new float[]{.631f, .278f, 1, .027f}, 1.048f);
		PdfFunction function2 = PdfFunction.type2(writer, new float[]{0, 1}, null,
			new float[]{.929f, .357f, 1, .298f}, new float[]{.941f, .4f, 1, .102f}, 1.374f);
		PdfFunction function3 = PdfFunction.type3(writer, new float[]{0, 1}, null,
			new PdfFunction[]{function1, function2}, new float[]{.708f}, new float[]{1, 0, 0, 1});
		PdfShading shading = PdfShading.type3(writer, new CMYKColor(0, 0, 0, 0),
			new float[]{0, 0, .096f, 0, 0, 1}, null, function3, new boolean[]{true, true});
		PdfContentByte cb = writer.getDirectContent();
		cb.moveTo(316.789f, 140.311f);
		cb.curveTo(303.222f, 146.388f, 282.966f, 136.518f, 279.122f, 121.983f);
		cb.lineTo(277.322f, 120.182f);
		cb.curveTo(285.125f, 122.688f, 291.441f, 121.716f, 298.156f, 119.386f);
		cb.lineTo(336.448f, 119.386f);
		cb.curveTo(331.072f, 128.643f, 323.346f, 137.376f, 316.789f, 140.311f);
		cb.clip();
		cb.newPath();
		cb.saveState();
		cb.concatCTM(27.7843f, 0, 0, -27.7843f, 310.2461f, 121.1521f);
		cb.paintShading(shading);
		cb.restoreState();
            
		document.close();
	}

		System.out.println("Shading pattern");
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("shading_pattern.pdf"));
		document.open();

		PdfShading shading = PdfShading.simpleAxial(writer, 100, 100, 400, 100, Color.red, Color.cyan);
		PdfShadingPattern shadingPattern = new PdfShadingPattern(shading);
		PdfContentByte cb = writer.getDirectContent();
		BaseFont bf = BaseFont.createFont(BaseFont.TIMES_BOLD, BaseFont.WINANSI, false);
		cb.setShadingFill(shadingPattern);
		cb.beginText();
		cb.setTextMatrix(100, 100);
		cb.setFontAndSize(bf, 40);
		cb.showText("Look at this text!");
		cb.endText();
		PdfShading shadingR = PdfShading.simpleRadial(writer, 200, 500, 50, 300, 500, 100, new Color(255, 247, 148), new Color(247, 138, 107), false, false);
		cb.paintShading(shadingR);
		document.close();
	}

