		// step 1
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("softmask.pdf"));
		// step 3
		document.open();
		// step 4
		PdfContentByte cb = writer.getDirectContent();
		String text = "text ";
		text += text;
		text += text;
		text += text;
		text += text;
		text += text;
		text += text;
		text += text;
		text += text;
		document.add(new Paragraph(text));
		Image img = Image.getInstance("otsoe.jpg");
		img.setAbsolutePosition(100, 550);
		byte gradient[] = new byte[256];
		for (int k = 0; k < 256; ++k)
			gradient[k] = (byte)k;
		for (int k = 0; k < 256; ++k)
			gradient[k] = (byte)k;
		Image smask = Image.getInstance(256, 1, 1, 8, gradient);
		smask.makeMask();
		img.setImageMask(smask);
		cb.addImage(img);

		// step 5
		document.close();
	}

		System.out.println("Pantone example 
        
		// step 1
		Document document = new Document();
            
		// step 2
		// we create a writer that listens to the document
		// and directs a PDF-stream to a file
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("spotcolor.pdf"));
		BaseFont bf = BaseFont.createFont("Helvetica", "winansi", BaseFont.NOT_EMBEDDED);
            
		// step 3
		document.open();
            
		// step 4
		PdfContentByte cb = writer.getDirectContent();
            
		// step 5
            
		// Note
		PdfSpotColor spc_cmyk = new PdfSpotColor("PANTONE 280 CV", 0.25f, new CMYKColor(0.9f, .2f, .3f, .1f));
		PdfSpotColor spc_rgb = new PdfSpotColor("PANTONE 147", 0.9f, new Color(114, 94, 38));
		PdfSpotColor spc_g = new PdfSpotColor("PANTONE 100 CV", 0.5f, new GrayColor(0.9f));
            
		// Stroke a rectangle with CMYK alternate
		cb.setColorStroke(spc_cmyk, .5f);
		cb.setLineWidth(10f);
		// draw a rectangle
		cb.rectangle(100, 700, 100, 100);
		// add the diagonal
		cb.moveTo(100, 700);
		cb.lineTo(200, 800);
		// stroke the lines
		cb.stroke();
            
		// Fill a rectangle with CMYK alternate
		cb.setColorFill(spc_cmyk, spc_cmyk.getTint());
		cb.rectangle(250, 700, 100, 100);
		cb.fill();
            
		// Stroke a circle with RGB alternate
		cb.setColorStroke(spc_rgb, spc_rgb.getTint());
		cb.setLineWidth(5f);
		cb.circle(150f, 500f, 100f);
		cb.stroke();
            
		// Fill the circle with RGB alternate
		cb.setColorFill(spc_rgb, spc_rgb.getTint());
		cb.circle(150f, 500f, 50f);
		cb.fill();
            
		// example with colorfill
		cb.setColorFill(spc_g, spc_g.getTint());
		cb.moveTo(100f, 200f);
		cb.lineTo(200f, 250f);
		cb.lineTo(400f, 150f);
		cb.fill();
		document.newPage();
		String text = "Some text to show";
		document.add(new Paragraph(text, new Font(Font.HELVETICA, 24, Font.NORMAL, new SpotColor(spc_cmyk))));
		document.add(new Paragraph(text, new Font(Font.HELVETICA, 24, Font.NORMAL, new SpotColor(spc_cmyk, 0.5f))));
            
		// example with template
		PdfTemplate t = cb.createTemplate(500f, 500f);
		// Stroke a rectangle with CMYK alternate
		t.setColorStroke(new SpotColor(spc_cmyk, .5f));
		t.setLineWidth(10f);
		// draw a rectangle
		t.rectangle(100, 10, 100, 100);
		// add the diagonal
		t.moveTo(100, 10);
		t.lineTo(200, 100);
		// stroke the lines
		t.stroke();
            
		// Fill a rectangle with CMYK alternate
		t.setColorFill(spc_g, spc_g.getTint());
		t.rectangle(100, 125, 100, 100);
		t.fill();
		t.beginText();
		t.setFontAndSize(bf, 20f);
		t.setTextMatrix(1f, 0f, 0f, 1f, 10f, 10f);
		t.showText("Template text upside down");
		t.endText();
		t.rectangle(0, 0, 499, 499);
		t.stroke();
		cb.addTemplate(t, -1.0f, 0.00f, 0.00f, -1.0f, 550f, 550f);

		// step 5
		document.close();
	}

	cb.setColorStroke(Color.black);
	cb.setColorFill(Color.gray);
	cb.rectangle(x, y, 100, 200);
	cb.fill();
	cb.setLineWidth(2);
	cb.rectangle(x, y, 200, 200);
	cb.stroke();
}


	/**
	 * Prints 3 circles in different colors that intersect with eachother.
	 * @param x
	 * @param y
	 * @param cb
	 * @throws Exception
	 */
	public static void pictureCircles(float x, float y, PdfContentByte cb) throws Exception {
		cb.setColorFill(Color.red);
		cb.circle(x + 70, y + 70, 50);
		cb.fill();
		cb.setColorFill(Color.yellow);
		cb.circle(x + 100, y + 130, 50);
		cb.fill();
		cb.setColorFill(Color.blue);
		cb.circle(x + 130, y + 70, 50);
		cb.fill();
	}


	/**
	 * Demonstrates the Transparency functionality.
	 * @param args no arguments needed
	 */
	public static void main(String[] args) throws Exception {
		System.out.println("Transparency");
		// step 1
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("transparency.pdf"));
		// step 3
		document.open();
		// step 4
		BaseFont bf = BaseFont.createFont("Helvetica", "Cp1252", false);
		PdfContentByte cb = writer.getDirectContent();
		float gap = (document.getPageSize().width() - 400) / 3;
            
		pictureBackdrop(gap, 500, cb);
		pictureBackdrop(200 + 2 * gap, 500, cb);
		pictureBackdrop(gap, 500 - 200 - gap, cb);
		pictureBackdrop(200 + 2 * gap, 500 - 200 - gap, cb);
            
		pictureCircles(gap, 500, cb);
		cb.saveState();
		PdfGState gs1 = new PdfGState();
		gs1.setFillOpacity(0.5f);
		cb.setGState(gs1);
		pictureCircles(200 + 2 * gap, 500, cb);
		cb.restoreState();

		PdfTemplate tp = cb.createTemplate(200, 200);
		cb.saveState();
		pictureCircles(0, 0, tp);
		PdfTransparencyGroup group = new PdfTransparencyGroup();
		tp.setGroup(group);
		cb.setGState(gs1);
		cb.addTemplate(tp, gap, 500 - 200 - gap);
		cb.restoreState();

		tp = cb.createTemplate(200, 200);
		cb.saveState();
		PdfGState gs2 = new PdfGState();
		gs2.setFillOpacity(0.5f);
		gs2.setBlendMode(PdfGState.BM_SOFTLIGHT);
		tp.setGState(gs2);
		pictureCircles(0, 0, tp);
		tp.setGroup(group);
		cb.addTemplate(tp, 200 + 2 * gap, 500 - 200 - gap);
		cb.restoreState();

		cb.resetRGBColorFill();
		ColumnText ct = new ColumnText(cb);
		Phrase ph = new Phrase("Ungrouped objects\nObject opacity = 1.0");
		ct.setSimpleColumn(ph, gap, 0, gap + 200, 500, 18, Element.ALIGN_CENTER);
		ct.go();
            
		ph = new Phrase("Ungrouped objects\nObject opacity = 0.5");
		ct.setSimpleColumn(ph, 200 + 2 * gap, 0, 200 + 2 * gap + 200, 500, 18, Element.ALIGN_CENTER);
		ct.go();
            
		ph = new Phrase("Transparency group\nObject opacity = 1.0\nGroup opacity = 0.5\nBlend mode = Normal");
		ct.setSimpleColumn(ph, gap, 0, gap + 200, 500 - 200 - gap, 18, Element.ALIGN_CENTER);
		ct.go();
            
		ph = new Phrase("Transparency group\nObject opacity = 0.5\nGroup opacity = 1.0\nBlend mode = SoftLight");
		ct.setSimpleColumn(ph, 200 + 2 * gap, 0, 200 + 2 * gap + 200, 500 - 200 - gap, 18, Element.ALIGN_CENTER);
		ct.go();

		// step 5
		document.close();
	}

		System.out.println("Affine Transformation");        
		// step 1
		Document document = new Document(PageSize.A4);
        
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("affinetransformation.pdf"));
            
		// step 3
		document.open();
            
		// step 4
		PdfContentByte cb = writer.getDirectContent();
		cb.transform(AffineTransform.getScaleInstance(1.2, 0.75));
            
		// we create a PdfTemplate
		PdfTemplate template = cb.createTemplate(25, 25);
            
		// we add some crosses to visualize the coordinates
		template.moveTo(13, 0);
		template.lineTo(13, 25);
		template.moveTo(0, 13);
		template.lineTo(50, 13);
		template.stroke();
            
		// we add the template on different positions
		cb.addTemplate(template, 216 - 13, 720 - 13);
		cb.addTemplate(template, 360 - 13, 360 - 13);
		cb.addTemplate(template, 360 - 13, 504 - 13);
		cb.addTemplate(template, 72 - 13, 144 - 13);
		cb.addTemplate(template, 144 - 13, 288 - 13);

		cb.moveTo(216, 720);
		cb.lineTo(360, 360);
		cb.lineTo(360, 504);
		cb.lineTo(72, 144);
		cb.lineTo(144, 288);
		cb.stroke();
            
		BaseFont bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
		cb.beginText();
		cb.setFontAndSize(bf, 12);
		cb.showTextAligned(PdfContentByte.ALIGN_CENTER, "(3\" * 1.2, 10\" * .75)", 216 + 25, 720 + 5, 0);
		cb.showTextAligned(PdfContentByte.ALIGN_CENTER, "(5\" * 1.2, 5\" * .75)", 360 + 25, 360 + 5, 0);
		cb.showTextAligned(PdfContentByte.ALIGN_CENTER, "(5\" * 1.2, 7\" * .75)", 360 + 25, 504 + 5, 0);
		cb.showTextAligned(PdfContentByte.ALIGN_CENTER, "(1\" * 1.2, 2\" * .75)", 72 + 25, 144 + 5, 0);
		cb.showTextAligned(PdfContentByte.ALIGN_CENTER, "(2\" * 1.2, 4\" * .75)", 144 + 25, 288 + 5, 0);
		cb.endText(); 
        
		// step 5
		document.close();
	}

