		System.out.println("Drawing circles");
        
		// step 1
		Document document = new Document();
        
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("circles.pdf"));
            
		// step 3
		document.open();
            
		// step 4
		PdfContentByte cb = writer.getDirectContent();

		cb.circle(250.0f, 500.0f, 200.0f);
		cb.circle(250.0f, 500.0f, 150.0f);
		cb.stroke();
		cb.setRGBColorFill(0xFF, 0x00, 0x00);
		cb.circle(250.0f, 500.0f, 100.0f);
		cb.fillStroke();
		cb.setRGBColorFill(0xFF, 0xFF, 0xFF);
		cb.circle(250.0f, 500.0f, 50.0f);
		cb.fill();
        
		// step 5
		document.close();
	}

		System.out.println("Changing the Graphics State with PdfGState");

		// step 1
		Document document = new Document();

		// step 2
		// we create a writer that listens to the document
		// and directs a PDF-stream to a file
		PdfWriter writer = PdfWriter.getInstance(document,
			new FileOutputStream("gstate.pdf"));

		// step 3
		document.open();

		// step 4
		PdfContentByte cb = writer.getDirectContent();

		PdfGState gs = new PdfGState();
		gs.setFillOpacity(0.5f);
		cb.setGState(gs);
		cb.setColorFill(Color.red);
		cb.circle(260.0f, 500.0f, 250.0f);
		cb.fill();
		cb.circle(260.0f, 500.0f, 200.0f);
		cb.fill();
		cb.circle(260.0f, 500.0f, 150.0f);
		cb.fill();
		gs.setFillOpacity(0.2f);
		cb.setGState(gs);
		cb.setColorFill(Color.blue);
		cb.circle(260.0f, 500.0f, 100.0f);
		cb.fill();
		cb.circle(260.0f, 500.0f, 50.0f);
		cb.fill();

		// step 5
		document.close();
	}

		System.out.println("Literal PDF Syntax");
		Document.compress = false;
        
		// step 1
		Document document = new Document();
        
		// step 2
		// we create a writer that listens to the document
		// and directs a PDF-stream to a file
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("literal.pdf"));
            
		// step 3
		document.open();
            
		// step 4
		PdfContentByte cb = writer.getDirectContent();
		String star = "0.3 g\n15.000 27.000 m\n"
			+ "7.947 5.292 l\n26.413 18.708 l\n"
			+ "3.587 18.708 l\n22.053 5.292 l\nf\n"
			+ "45.000 57.000 m\n37.947 35.292 l\n"
			+ "56.413 48.708 l\n33.587 48.708 l\n"
			+ "52.053 35.292 l\nf\n"
			+ "0.7 g\n15.000 57.000 m\n"
			+ "7.947 35.292 l\n26.413 48.708 l\n"
			+ "3.587 48.708 l\n22.053 35.292 l\nf\n"
			+ "45.000 27.000 m\n37.947 5.292 l\n"
			+ "56.413 18.708 l\n33.587 18.708 l\n"
			+ "52.053 5.292 l\nf";
		cb.setLiteral(star);
        
		// step 5
		document.close();
	}

		System.out.println("Drawing some shapes");
        
		// step 1
		Document document = new Document();

		// step 2
		// we create a writer that listens to the document
		// and directs a PDF-stream to a file
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("shapes.pdf"));
            
		// step 3
		document.open();
            
		// step 4
		PdfContentByte cb = writer.getDirectContent();
            
		// an example of a rectangle with a diagonal in very thick lines
		cb.setLineWidth(10f);
		// draw a rectangle
		cb.rectangle(100, 700, 100, 100);
		// add the diagonal
		cb.moveTo(100, 700);
		cb.lineTo(200, 800);
		// stroke the lines
		cb.stroke();
            
		// an example of some circles
		cb.setLineDash(3, 3, 0);
		cb.setRGBColorStrokeF(0f, 255f, 0f);
		cb.circle(150f, 500f, 100f);
		cb.stroke();
            
		cb.setLineWidth(5f);
		cb.resetRGBColorStroke();
		cb.circle(150f, 500f, 50f);
		cb.stroke();
            
		// example with colorfill
		cb.setRGBColorFillF(0f, 255f, 0f);
		cb.moveTo(100f, 200f);
		cb.lineTo(200f, 250f);
		cb.lineTo(400f, 150f);
		// because we change the fill color BEFORE we stroke the triangle
		// the color of the triangle will be red instead of green
		cb.setRGBColorFillF(255f, 0f, 0f);
		cb.closePathFillStroke();
        
		// step 5
		document.close();
	}

		System.out.println("Changing the Graphics State with save and restore");

		// step 1
		Document document = new Document();

		// step 2
		// we create a writer that listens to the document
		// and directs a PDF-stream to a file
		PdfWriter writer = PdfWriter.getInstance(document,
			new FileOutputStream("state.pdf"));

		// step 3
		document.open();

		// step 4
		PdfContentByte cb = writer.getDirectContent();

		cb.circle(260.0f, 500.0f, 250.0f);
		cb.fill();
		cb.saveState();
		cb.setColorFill(Color.red);
		cb.circle(260.0f, 500.0f, 200.0f);
		cb.fill();
		cb.saveState();
		cb.setColorFill(Color.blue);
		cb.circle(260.0f, 500.0f, 150.0f);
		cb.fill();
		cb.restoreState();
		cb.circle(260.0f, 500.0f, 100.0f);
		cb.fill();
		cb.restoreState();
		cb.circle(260.0f, 500.0f, 50.0f);
		cb.fill();

		// step 5
		document.close();
	}

		System.out.println("Layers");
        
		// step 1
		Document document = new Document();
            
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("layers.pdf"));
            
		// step 3
		document.open();
            
		// step 4
            
		// high level
		Paragraph p = new Paragraph();
		for (int i = 0; i < 100; i++) p.add(new Chunk("Blah blah blah blah blah. "));
		document.add(p);
		Image img = Image.getInstance("hitchcock.png");
		img.setAbsolutePosition(100, 500);
		document.add(img);
            
		// low level
		PdfContentByte cb = writer.getDirectContent();
		PdfContentByte cbu = writer.getDirectContentUnder();
		cb.setRGBColorFill(0xFF, 0xFF, 0xFF);
		cb.circle(250.0f, 500.0f, 50.0f);
		cb.fill();
		cbu.setRGBColorFill(0xFF, 0x00, 0x00);
		cbu.circle(250.0f, 500.0f, 100.0f);
		cbu.fill();
        
		// step 5
		document.close();
	}

		System.out.println("Automatic grouping and nesting");

		// step 1
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("automatic.pdf"));
		writer.setPdfVersion(PdfWriter.VERSION_1_5);
		writer.setViewerPreferences(PdfWriter.PageModeUseOC);
		// step 3
		document.open();
		// step 4
		PdfContentByte cb = writer.getDirectContent();
		Phrase explanation = new Phrase("Automatic layer grouping and nesting", new Font(Font.HELVETICA, 20, Font.BOLD, Color.red));
		ColumnText.showTextAligned(cb, Element.ALIGN_LEFT, explanation, 50, 650, 0);
		PdfLayer l12 = new PdfLayer("Layer nesting", writer);
		PdfLayer l1 = new PdfLayer("Layer 1", writer);
		PdfLayer l2 = new PdfLayer("Layer 2", writer);
		PdfLayer title = PdfLayer.createTitle("Layer grouping", writer);
		PdfLayer l3 = new PdfLayer("Layer 3", writer);
		PdfLayer l4 = new PdfLayer("Layer 4", writer);
		l12.addChild(l1);
		l12.addChild(l2);
		title.addChild(l3);
		title.addChild(l4);
		Phrase p1 = new Phrase("Text in layer 1");
		Phrase p2 = new Phrase("Text in layer 2");
		Phrase p3 = new Phrase("Text in layer 3");
		Phrase p4 = new Phrase("Text in layer 4");
		cb.beginLayer(l1);
		ColumnText.showTextAligned(cb, Element.ALIGN_LEFT, p1, 50, 600, 0);
		cb.endLayer();
		cb.beginLayer(l2);
		ColumnText.showTextAligned(cb, Element.ALIGN_LEFT, p2, 50, 550, 0);
		cb.endLayer();
		cb.beginLayer(l3);
		ColumnText.showTextAligned(cb, Element.ALIGN_LEFT, p3, 50, 500, 0);
		cb.endLayer();
		cb.beginLayer(l4);
		ColumnText.showTextAligned(cb, Element.ALIGN_LEFT, p4, 50, 450, 0);
		cb.endLayer();
		// step 5
		document.close();
	}

	System.out.println("Grouping optional content");
	// step 1
	Document document = new Document(PageSize.A4, 50, 50, 50, 50);
	// step 2
	PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("contentgroups.pdf"));
	writer.setPdfVersion(PdfWriter.VERSION_1_5);
	writer.setViewerPreferences(PdfWriter.PageModeUseOC);
	// step 3
	document.open();
	// step 4
	PdfContentByte cb = writer.getDirectContent();
	Phrase explanation = new Phrase("Layer grouping", new Font(Font.HELVETICA, 20, Font.BOLD, Color.red));
	ColumnText.showTextAligned(cb, Element.ALIGN_LEFT, explanation, 50, 650, 0);
	PdfLayer l1 = new PdfLayer("Layer 1", writer);
	PdfLayer l2 = new PdfLayer("Layer 2", writer);
	PdfLayer l3 = new PdfLayer("Layer 3", writer);
	PdfLayerMembership m1 = new PdfLayerMembership(writer);
	m1.addMember(l2);
	m1.addMember(l3);
	Phrase p1 = new Phrase("Text in layer 1");
	Phrase p2 = new Phrase("Text in layer 2 or layer 3");
	Phrase p3 = new Phrase("Text in layer 3");
	cb.beginLayer(l1);
	ColumnText.showTextAligned(cb, Element.ALIGN_LEFT, p1, 50, 600, 0);
	cb.endLayer();
	cb.beginLayer(m1);
	ColumnText.showTextAligned(cb, Element.ALIGN_LEFT, p2, 50, 550, 0);
	cb.endLayer();
	cb.beginLayer(l3);
	ColumnText.showTextAligned(cb, Element.ALIGN_LEFT, p3, 50, 500, 0);
	cb.endLayer();
	PdfOCProperties p = writer.getOCProperties();
	PdfArray order = new PdfArray();
	order.add(l1.getRef());
	PdfArray group = new PdfArray();
	group.add(new PdfString("A group of two", PdfObject.TEXT_UNICODE));
	group.add(l2.getRef());
	group.add(l3.getRef());
	order.add(group);
	PdfDictionary d = new PdfDictionary();
	d.put(PdfName.ORDER, order);
	p.put(PdfName.D, d);
	// step 5
	document.close();
}

