public class Bookmarks extends PdfPageEventHelper {
    
	/** Keeps the number of the current paragraph. */
	private int n = 0;

	Document document = new Document(PageSize.A4, 50, 50, 70, 70);
	PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("endpage.pdf"));
	writer.setPageEvent(new EndPage());
	document.open();
	String text = "Lots of text. ";
	for (int k = 0; k < 10; ++k)
		text += text;
	for (int k = 0; k < 10; ++k)
		text += text;
	document.add(new Paragraph(text));
	document.close();
}

    
	/**
	 * @see com.lowagie.text.pdf.PdfPageEventHelper#onEndPage(com.lowagie.text.pdf.PdfWriter, com.lowagie.text.Document)
	 */
	public void onEndPage(PdfWriter writer, Document document) {
		try {
			Rectangle page = document.getPageSize();
			PdfPTable head = new PdfPTable(3);
			for (int k = 1; k <= 6; ++k)
				head.addCell("head " + k);
			for (int k = 1; k <= 6; ++k)
				head.addCell("head " + k);
			head.setTotalWidth(page.width() - document.leftMargin() - document.rightMargin());
			head.writeSelectedRows(0, -1, document.leftMargin(), page.height() - document.topMargin() + head.getTotalHeight(),
				writer.getDirectContent());
			PdfPTable foot = new PdfPTable(3);
			for (int k = 1; k <= 6; ++k)
				foot.addCell("foot " + k);
			for (int k = 1; k <= 6; ++k)
				foot.addCell("foot " + k);
			foot.setTotalWidth(page.width() - document.leftMargin() - document.rightMargin());
			foot.writeSelectedRows(0, -1, document.leftMargin(), document.bottomMargin(),
				writer.getDirectContent());
		}
		catch (Exception e) {
			throw new ExceptionConverter(e);
		}
	}

/**
 * Gets a Handler object.
 * @param document  the document on which the handler operates
 * @return a Handler object
 * @throws IOException
 */
public MyHandler getXmlHandler(Document document) throws IOException, DocumentException
{
	return new MyHandler(document, new RomeoJulietMap());
}

	
