	System.out.println("Add watermarks and pagenumbers");
	// we create a reader for a certain document
	PdfReader reader = new PdfReader("ChapterSection.pdf");
	int n = reader.getNumberOfPages();
	// we create a stamper that will copy the document to a new file
	PdfStamper stamp = new PdfStamper(reader, new FileOutputStream("watermark_pagenumbers.pdf"));
	// adding some metadata
	HashMap moreInfo = new HashMap();
	moreInfo.put("Author", "Bruno Lowagie");
	stamp.setMoreInfo(moreInfo);
	// adding content to each page
	int i = 0;
	PdfContentByte under;
	PdfContentByte over;
	Image img = Image.getInstance("watermark.jpg");
	BaseFont bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.WINANSI, BaseFont.EMBEDDED);
	img.setAbsolutePosition(200, 400);
	while (i < n) {
		i++;
		// watermark under the existing page
		under = stamp.getUnderContent(i);
		under.addImage(img);
		// text over the existing page
		over = stamp.getOverContent(i);
		over.beginText();
		over.setFontAndSize(bf, 18);
		over.setTextMatrix(30, 30);
		over.showText("page " + i);
		over.setFontAndSize(bf, 32);
		over.showTextAligned(Element.ALIGN_LEFT, "DUPLICATE", 230, 430, 45);
		over.endText();
	while (i < n) {
		i++;
		// watermark under the existing page
		under = stamp.getUnderContent(i);
		under.addImage(img);
		// text over the existing page
		over = stamp.getOverContent(i);
		over.beginText();
		over.setFontAndSize(bf, 18);
		over.setTextMatrix(30, 30);
		over.showText("page " + i);
		over.setFontAndSize(bf, 32);
		over.showTextAligned(Element.ALIGN_LEFT, "DUPLICATE", 230, 430, 45);
		over.endText();
	}
	// adding an extra page
	stamp.insertPage(1, PageSize.A4);
	over = stamp.getOverContent(1);
	over.beginText();
	over.setFontAndSize(bf, 18);
	over.showTextAligned(Element.ALIGN_LEFT, "DUPLICATE OF AN EXISTING PDF DOCUMENT", 30, 600, 0);
	over.endText();
	// adding a page from another document
	PdfReader reader2 = new PdfReader("SimpleAnnotations1.pdf");
	under = stamp.getUnderContent(1);
	under.addTemplate(stamp.getImportedPage(reader2, 3), 1, 0, 0, 1, 0, 0);
	// closing PdfStamper will generate the new PDF file
	stamp.close();
}

    if (args.length < 2) {
        System.err.println("arguments
    }
    else {
    	System.out.println("PdfCopy example");
        try {
            int pageOffset = 0;
            ArrayList master = new ArrayList();
            int f = 0;
            String outFile = args[args.length-1];
            Document document = null;
            PdfCopy  writer = null;
            while (f < args.length-1) {
                // we create a reader for a certain document
                PdfReader reader = new PdfReader(args[f]);
                reader.consolidateNamedDestinations();
                // we retrieve the total number of pages
                int n = reader.getNumberOfPages();
                List bookmarks = SimpleBookmark.getBookmark(reader);
                if (bookmarks != null) {
                    if (pageOffset != 0)
                        SimpleBookmark.shiftPageNumbers(bookmarks, pageOffset, null);
                    master.addAll(bookmarks);
                }
                pageOffset += n;
                
                if (f == 0) {
                    // step 1
                    document = new Document(reader.getPageSizeWithRotation(1));
                    // step 2
                    writer = new PdfCopy(document, new FileOutputStream(outFile));
                    // step 3
                    document.open();
                }
                // step 4
                PdfImportedPage page;
                for (int i = 0; i < n; ) {
                    ++i;
                    page = writer.getImportedPage(reader, i);
                    writer.addPage(page);
                for (int i = 0; i < n; ) {
                    ++i;
                    page = writer.getImportedPage(reader, i);
                    writer.addPage(page);
                }
                PRAcroForm form = reader.getAcroForm();
                if (form != null)
                    writer.copyAcroForm(reader);
                f++;
            while (f < args.length-1) {
                // we create a reader for a certain document
                PdfReader reader = new PdfReader(args[f]);
                reader.consolidateNamedDestinations();
                // we retrieve the total number of pages
                int n = reader.getNumberOfPages();
                List bookmarks = SimpleBookmark.getBookmark(reader);
                if (bookmarks != null) {
                    if (pageOffset != 0)
                        SimpleBookmark.shiftPageNumbers(bookmarks, pageOffset, null);
                    master.addAll(bookmarks);
                }
                pageOffset += n;
                
                if (f == 0) {
                    // step 1
                    document = new Document(reader.getPageSizeWithRotation(1));
                    // step 2
                    writer = new PdfCopy(document, new FileOutputStream(outFile));
                    // step 3
                    document.open();
                }
                // step 4
                PdfImportedPage page;
                for (int i = 0; i < n; ) {
                    ++i;
                    page = writer.getImportedPage(reader, i);
                    writer.addPage(page);
                }
                PRAcroForm form = reader.getAcroForm();
                if (form != null)
                    writer.copyAcroForm(reader);
                f++;
            }
            if (master.size() > 0)
                writer.setOutlines(master);
            // step 5
            document.close();
        }
        catch(Exception e) {
            e.printStackTrace();
        }
    }
}

	PdfReader reader1 = new PdfReader("SimpleRegistrationForm.pdf");
	PdfReader reader2 = new PdfReader("TextFields.pdf");
	PdfCopyFields copy = new PdfCopyFields(new FileOutputStream("concatenatedforms.pdf"));
	copy.addDocument(reader1);
	copy.addDocument(reader2);
	copy.close();
