	}

		System.out.println("JavaScript");

		// step 1
		Document document = new Document();
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream(
			"JavaScriptAction.pdf"));
		// step 3
		document.open();
		// step 4
		Paragraph p = new Paragraph(new Chunk("Click to say Hello")
			.setAction(PdfAction.javaScript("app.alert('Hello');\r", writer)));
		document.add(p);
		// step 5
		document.close();
	}

		System.out.println("local destination");
        
		// step 1
		Document document = new Document();
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("LocalDestination.pdf"));
		// step 3
		document.open();
		// step 4
		document.add(new Paragraph("Page 1"));
		document.newPage();
		document.add(new Paragraph("This PDF file jumps directly to page 2 when opened"));
		PdfContentByte cb = writer.getDirectContent();
		cb.localDestination("page2", new PdfDestination(PdfDestination.XYZ, -1, 10000, 0));
		writer.setOpenAction("page2");
        
		// step 5
		document.close();
	}

		System.out.println("local goto");
        
		// step 1
		Document document = new Document();
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("LocalGoto.pdf"));
            
		// step 3
		document.open();
            
		// step 4
            
		// we make some content
            
		// a paragraph with a local goto
		Paragraph p1 = new Paragraph("We will do something special with this paragraph. If you click on ", FontFactory.getFont(FontFactory.HELVETICA, 12));
		p1.add(new Chunk("this word", FontFactory.getFont(FontFactory.HELVETICA, 12, Font.NORMAL, new Color(0, 0, 255))).setLocalGoto("test"));
		p1.add(" you will automatically jump to another location in this document.");
            
		// some paragraph
		Paragraph p2 = new Paragraph("blah, blah, blah");
            
		// a paragraph with a local destination
		Paragraph p3 = new Paragraph("This paragraph contains a ");
		p3.add(new Chunk("local destination", FontFactory.getFont(FontFactory.HELVETICA, 12, Font.NORMAL, new Color(0, 255, 0))).setLocalDestination("test"));
            
		// we add the content
		document.add(p1);
		document.add(p2);
		document.add(p2);
		document.add(p2);
		document.add(p2);
		document.add(p2);
		document.add(p2);
		document.add(p2);
		document.add(p3);
        
		// step 5
		document.close();
	}

		System.out.println("Named Actions");

		// step 1
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document,
			new FileOutputStream("NamedActions.pdf"));
		// step 3
		document.open();
		// step 4
		Paragraph p = new Paragraph(new Chunk("Click to print")
			.setAction(new PdfAction(PdfAction.PRINTDIALOG)));
		PdfPTable table = new PdfPTable(4);
		table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_CENTER);
		table.addCell(new Phrase(new Chunk("First Page")
			.setAction(new PdfAction(PdfAction.FIRSTPAGE))));
		table.addCell(new Phrase(new Chunk("Prev Page")
			.setAction(new PdfAction(PdfAction.PREVPAGE))));
		table.addCell(new Phrase(new Chunk("Next Page")
			.setAction(new PdfAction(PdfAction.NEXTPAGE))));
		table.addCell(new Phrase(new Chunk("Last Page")
			.setAction(new PdfAction(PdfAction.LASTPAGE))));
		for (int k = 1; k <= 10; ++k) {
			document.add(new Paragraph("This is page " + k));
			document.add(Chunk.NEWLINE);
			document.add(table);
			document.add(p);
			document.newPage();
		for (int k = 1; k <= 10; ++k) {
			document.add(new Paragraph("This is page " + k));
			document.add(Chunk.NEWLINE);
			document.add(table);
			document.add(p);
			document.newPage();
		}
		// step 5
		document.close();
	}

		System.out.println("Open Application");

		// step 1
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document,
			new FileOutputStream("OpenApplication.pdf"));
