	}

		System.out.println("Remote goto (URLs and local destinations in another document)");
        
		// step 1
		Document document = new Document();
            
		// step 2
		PdfWriter writerA = PdfWriter.getInstance(document, new FileOutputStream("DocumentA.pdf"));
		PdfWriter writerB = PdfWriter.getInstance(document, new FileOutputStream("DocumentB.pdf"));
            
		// step 3
		document.open();
            
		// step 4
            
		// we create some content
            
		// a paragraph with a link to an external url
		Paragraph p1 = new Paragraph("You can turn a Chunk into an ", FontFactory.getFont(FontFactory.HELVETICA, 12));
		p1.add(new Chunk("anchor", FontFactory.getFont(FontFactory.HELVETICA, 12, Font.UNDERLINE, new Color(0, 0, 255))).setAnchor(new URL("http
		p1.add(", for instance to the iText site.");
            
		// some paragraph
		Paragraph p2 = new Paragraph("blah, blah, blah");
            
		// two paragraphs with a local destination
		Paragraph p3a = new Paragraph("This paragraph contains a ");
		p3a.add(new Chunk("local destination in document A", FontFactory.getFont(FontFactory.HELVETICA, 12, Font.NORMAL, new Color(0, 255, 0))).setLocalDestination("test"));
		Paragraph p3b = new Paragraph("This paragraph contains a ");
		p3b.add(new Chunk("local destination in document B", FontFactory.getFont(FontFactory.HELVETICA, 12, Font.NORMAL, new Color(0, 255, 0))).setLocalDestination("test"));
            
		// two paragraphs with a remote goto
		Paragraph p4a = new Paragraph(new Chunk("Click this paragraph to go to a certain destination on document B").setRemoteGoto("DocumentB.pdf", "test"));
		Paragraph p4b = new Paragraph(new Chunk("Click this paragraph to go to a certain destination on document A").setRemoteGoto("DocumentA.pdf", "test"));
            
		// a special remote goto
		Paragraph p5a = new Paragraph("you can also jump to a ");
		p5a.add(new Chunk("specific page on another document", FontFactory.getFont(FontFactory.HELVETICA, 12, Font.ITALIC)).setRemoteGoto("DocumentB.pdf", 3));
            
		// we add all the content
		document.add(p1);
		document.add(p2);
		document.add(p2);
		document.add(p2);
		document.add(p2);
		document.add(p2);
		document.add(p2);
		document.add(p2);
		// only for DocumentB.pdf
		writerA.pause();
		document.add(p4b);
		writerA.resume();
		// only for DocumentA.pdf
		writerB.pause();
		document.add(p4a);
		document.add(p5a);
		writerB.resume();
		// for both documents
		document.add(p2);
		document.add(p2);
		document.add(p2);
		document.add(p2);
		// only for DocumentB.pdf
		writerA.pause();
		document.add(p3b);
		document.add(p2);
		document.add(p2);
		document.newPage();
		document.add(p2);
		document.add(p2);
		document.newPage();
		writerA.resume();
		// only for documentA.pdf
		writerB.pause();
		document.add(p3a);
		writerB.resume();
		// for both documents
		document.add(p2);
		document.add(p2);
		// step 5
		document.close();
	}

		System.out.println("Simple Annotations");

		// step 1
		Document document1 = new Document(PageSize.A4, 10, 10, 10, 10);
		Document document2 = new Document(PageSize.A4, 10, 10, 10, 10);
		// step 2
		PdfWriter writer1 = PdfWriter.getInstance(document1,
			new FileOutputStream("SimpleAnnotations1.pdf"));
		PdfWriter writer2 = PdfWriter.getInstance(document2,
			new FileOutputStream("SimpleAnnotations2.pdf"));
		// step 3
		writer2.setPdfVersion(PdfWriter.VERSION_1_5);
		document1.open();
		document2.open();
		// step 4
		document1.add(new Paragraph("Each square on this page represents an annotation."));
		// document1
		PdfContentByte cb1 = writer1.getDirectContent();
		Annotation a1 = new Annotation(
			"authors", "Maybe it's because I wanted to be an author myself that I wrote iText.",
			250f, 700f, 350f, 800f);
		document1.add(a1);
		Annotation a2 = new Annotation(250f, 550f, 350f, 650f,
			new URL("http
		document1.add(a2);
		Annotation a3 = new Annotation(250f, 400f, 350f, 500f,
			"http
		document1.add(a3);
		Image image = Image.getInstance("iText.gif");
		image.setAnnotation(a3);
		document1.add(image);
		Annotation a4 = new Annotation(
			250f, 250f, 350f, 350f, PdfAction.LASTPAGE);
		document1.add(a4);
		// draw rectangles to show where the annotations were added
		cb1.rectangle(250, 700, 100, 100);
		cb1.rectangle(250, 550, 100, 100);
		cb1.rectangle(250, 400, 100, 100);
		cb1.rectangle(250, 250, 100, 100);
		cb1.stroke();
		// more content
		document1.newPage();
		for (int i = 0; i < 5; i++) {
			document1.add(new Paragraph("blahblahblah"));
		for (int i = 0; i < 5; i++) {
			document1.add(new Paragraph("blahblahblah"));
		}
		document1.add(new Annotation("blahblah", "Adding an annotation without specifying coordinates"));
		for (int i = 0; i < 3; i++) {
			document1.add(new Paragraph("blahblahblah"));	
		for (int i = 0; i < 3; i++) {
			document1.add(new Paragraph("blahblahblah"));	
		}
		document1.newPage();
		document1.add(new Chunk("marked chunk").setLocalDestination("mark"));
            
		// document2
		document2.add(new Paragraph("Each square on this page represents an annotation."));
		PdfContentByte cb2 = writer2.getDirectContent();
		Annotation a5 = new Annotation(100f, 700f, 200f, 800f,
			"cards.mpg", "video/mpeg", true);
		document2.add(a5);
		Annotation a6 = new Annotation(100f, 550f, 200f, 650f,
			"SimpleAnnotations1.pdf", "mark");
		document2.add(a6);
		Annotation a7 = new Annotation(100f, 400f, 200f, 500f,
			"SimpleAnnotations1.pdf", 2);
		document2.add(a7);
		Annotation a8 = new Annotation(	100f, 250f, 200f, 350f,
			"C
		document2.add(a8);
		// draw rectangles to show where the annotations were added
		cb2.rectangle(100, 700, 100, 100);
		cb2.rectangle(100, 550, 100, 100);
		cb2.rectangle(100, 400, 100, 100);
		cb2.rectangle(100, 250, 100, 100);
		cb2.stroke();
		// step 5
		document1.close();
		document2.close();
	}

	n++;
	PdfContentByte cb = writer.getDirectContent();
	PdfDestination destination = new PdfDestination(PdfDestination.FITH, position);
	PdfOutline outline = new PdfOutline(cb.getRootOutline(), destination, "paragraph " + n);
}


	/**
	 * Creates a document with outlines.
	 * 
	 * @param args
	 *            no arguments needed
	 */
	public static void main(String[] args) throws Exception {

		System.out.println("Bookmarks");

		// step 1
		Document document = new Document(PageSize.A6);
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("Bookmarks.pdf"));
		// step 3
		writer.setViewerPreferences(PdfWriter.PageModeUseOutlines);
		document.open();
		// step 4
		writer.setPageEvent(new Bookmarks());
            
		document.add(new Paragraph("GALLIA est omnis divisa in partes tres, quarum unam incolunt Belgae, aliam Aquitani, tertiam qui ipsorum lingua Celtae, nostra Galli appellantur.  Hi omnes lingua, institutis, legibus inter se differunt. Gallos ab Aquitanis Garumna flumen, a Belgis Matrona et Sequana dividit. Horum omnium fortissimi sunt Belgae, propterea quod a cultu atque humanitate provinciae longissime absunt, minimeque ad eos mercatores saepe commeant atque ea quae ad effeminandos animos pertinent important, proximique sunt Germanis, qui trans Rhenum incolunt, quibuscum continenter bellum gerunt.  Qua de causa Helvetii quoque reliquos Gallos virtute praecedunt, quod fere cotidianis proeliis cum Germanis contendunt, cum aut suis finibus eos prohibent aut ipsi in eorum finibus bellum gerunt.", new Font(Font.HELVETICA, 12)));
		document.add(new Paragraph("[Eorum una, pars, quam Gallos obtinere dictum est, initium capit a flumine Rhodano, continetur Garumna flumine, Oceano, finibus Belgarum, attingit etiam ab Sequanis et Helvetiis flumen Rhenum, vergit ad septentriones. Belgae ab extremis Galliae finibus oriuntur, pertinent ad inferiorem partem fluminis Rheni, spectant in septentrionem et orientem solem. Aquitania a Garumna flumine ad Pyrenaeos montes et eam partem Oceani quae est ad Hispaniam pertinet; spectat inter occasum solis et septentriones.]", new Font(Font.HELVETICA, 12)));
		document.add(new Paragraph("Apud Helvetios longe nobilissimus fuit et ditissimus Orgetorix.  Is M. Messala, [et P.] M.  Pisone consulibus regni cupiditate inductus coniurationem nobilitatis fecit et civitati persuasit ut de finibus suis cum omnibus copiis exirent
", new Font(Font.HELVETICA, 12)));
		document.add(new Paragraph("His rebus adducti et auctoritate Orgetorigis permoti constituerunt ea quae ad proficiscendum pertinerent comparare, iumentorum et carrorum quam maximum numerum coemere, sementes quam maximas facere, ut in itinere copia frumenti suppeteret, cum proximis civitatibus pacem et amicitiam confirmare.  Ad eas res conficiendas biennium sibi satis esse duxerunt; in tertium annum profectionem lege confirmant.  Ad eas res conficiendas Orgetorix deligitur.  Is sibi legationem ad civitates suscipit.  In eo itinere persuadet Castico, Catamantaloedis filio, Sequano, cuius pater regnum in Sequanis multos annos obtinuerat et a senatu populi Romani amicus appellatus erat, ut regnum in civitate sua occuparet, quod pater ante habuerit; itemque Dumnorigi Haeduo, fratri Diviciaci, qui eo tempore principatum in civitate obtinebat ac maxime plebi acceptus erat, ut idem conaretur persuadet eique filiam suam in matrimonium dat.  Perfacile factu esse illis probat conata perficere, propterea quod ipse suae
 civitatis imperium obtenturus esset
		document.add(new Paragraph("Ea res est Helvetiis per indicium enuntiata.  Moribus suis Orgetoricem ex vinculis causam dicere coegerunt; damnatum poenam sequi oportebat, ut igni cremaretur.  Die constituta causae dictionis Orgetorix ad iudicium omnem suam familiam, ad hominum milia decem, undique coegit, et omnes clientes obaeratosque suos, quorum magnum numerum habebat, eodem conduxit; per eos ne causam diceret se eripuit.  Cum civitas ob eam rem incitata armis ius suum exequi conaretur multitudinemque hominum ex agris magistratus cogerent, Orgetorix mortuus est; neque abest suspicio, ut Helvetii arbitrantur, quin ipse sibi mortem consciverit.", new Font(Font.HELVETICA, 12)));
		// step 5

public class Bookmarks extends PdfPageEventHelper {
    
	/** Keeps the number of the current paragraph. */
	private int n = 0;

		System.out.println("Chapters and Sections");
        
		// step 1
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);
		// step 2
		PdfWriter writer=PdfWriter.getInstance(document, new FileOutputStream("ChapterSection.pdf"));
		// step 3
		writer.setViewerPreferences(PdfWriter.PageModeUseOutlines);
		document.open();
		// step 4
		// we define some fonts
		Font chapterFont = FontFactory.getFont(FontFactory.HELVETICA, 24, Font.NORMAL, new Color(255, 0, 0));
		Font sectionFont = FontFactory.getFont(FontFactory.HELVETICA, 20, Font.NORMAL, new Color(0, 0, 255));
		Font subsectionFont = FontFactory.getFont(FontFactory.HELVETICA, 18, Font.BOLD, new Color(0, 64, 64));
		// we create some paragraphs
		Paragraph blahblah = new Paragraph("blah blah blah blah blah blah blaah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah");
		Paragraph blahblahblah = new Paragraph("blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blaah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah");
		// this loop will create 7 chapters
		for (int i = 1; i < 8; i++) {
			Paragraph cTitle = new Paragraph("This is chapter " + i, chapterFont);
			Chapter chapter = new Chapter(cTitle, i);
			// in chapter 4 we change the alignment to ALIGN_JUSTIFIED
			if (i == 4) {
				blahblahblah.setAlignment(Element.ALIGN_JUSTIFIED);
				blahblah.setAlignment(Element.ALIGN_JUSTIFIED);
				chapter.add(blahblah);
			}
			// in chapter 5, the alignment is changed again
			if (i == 5) {
				blahblahblah.setAlignment(Element.ALIGN_CENTER);
				blahblah.setAlignment(Element.ALIGN_RIGHT);
				chapter.add(blahblah);
			}
			// the alignment is changed to ALIGN_JUSTIFIED again
			if (i == 6) {
				blahblahblah.setAlignment(Element.ALIGN_JUSTIFIED);
				blahblah.setAlignment(Element.ALIGN_JUSTIFIED);
			}
			// in every chapter 3 sections will be added
			for (int j = 1; j < 4; j++) {
				Paragraph sTitle = new Paragraph("This is section " + j + " in chapter " + i, sectionFont);
				Section section = chapter.addSection(sTitle, 1);
				// for chapters > 2, the outine isn't open by default
				if (i > 2) section.setBookmarkOpen(false);
				// in all chapters except the 1st one, some extra text is added to section 3
				if (j == 3 && i > 1) {
					section.add(blahblah);
				}
				// in every section 3 subsections are added
				for (int k = 1; k < 4; k++) {
					Paragraph subTitle = new Paragraph("This is subsection " + k + " of section " + j, subsectionFont);
					Section subsection = section.addSection(subTitle, 3);
					// in the first subsection of section 3, extra text is added
					if (k == 1 && j == 3) {
						subsection.add(blahblahblah);
					}
					subsection.add(blahblah);
				for (int k = 1; k < 4; k++) {
					Paragraph subTitle = new Paragraph("This is subsection " + k + " of section " + j, subsectionFont);
					Section subsection = section.addSection(subTitle, 3);
					// in the first subsection of section 3, extra text is added
					if (k == 1 && j == 3) {
						subsection.add(blahblahblah);
					}
					subsection.add(blahblah);
				}
				// in the section section of every chapter > 2 extra text is added
				if (j == 2 && i > 2) {
					section.add(blahblahblah);
				}
				// a new page is added after the second section in Chapter 1
				if (j == 2 && i == 1) {
					section.add(Chunk.NEXTPAGE);
			for (int j = 1; j < 4; j++) {
				Paragraph sTitle = new Paragraph("This is section " + j + " in chapter " + i, sectionFont);
				Section section = chapter.addSection(sTitle, 1);
				// for chapters > 2, the outine isn't open by default
				if (i > 2) section.setBookmarkOpen(false);
				// in all chapters except the 1st one, some extra text is added to section 3
				if (j == 3 && i > 1) {
					section.add(blahblah);
				}
				// in every section 3 subsections are added
				for (int k = 1; k < 4; k++) {
					Paragraph subTitle = new Paragraph("This is subsection " + k + " of section " + j, subsectionFont);
					Section subsection = section.addSection(subTitle, 3);
					// in the first subsection of section 3, extra text is added
					if (k == 1 && j == 3) {
						subsection.add(blahblahblah);
					}
					subsection.add(blahblah);
				}
				// in the section section of every chapter > 2 extra text is added
				if (j == 2 && i > 2) {
					section.add(blahblahblah);
				}
				// a new page is added after the second section in Chapter 1
				if (j == 2 && i == 1) {
					section.add(Chunk.NEXTPAGE);
				}
			}
			document.add(chapter);
		for (int i = 1; i < 8; i++) {
			Paragraph cTitle = new Paragraph("This is chapter " + i, chapterFont);
			Chapter chapter = new Chapter(cTitle, i);
			// in chapter 4 we change the alignment to ALIGN_JUSTIFIED
			if (i == 4) {
				blahblahblah.setAlignment(Element.ALIGN_JUSTIFIED);
				blahblah.setAlignment(Element.ALIGN_JUSTIFIED);
				chapter.add(blahblah);
			}
			// in chapter 5, the alignment is changed again
			if (i == 5) {
				blahblahblah.setAlignment(Element.ALIGN_CENTER);
				blahblah.setAlignment(Element.ALIGN_RIGHT);
				chapter.add(blahblah);
			}
			// the alignment is changed to ALIGN_JUSTIFIED again
			if (i == 6) {
				blahblahblah.setAlignment(Element.ALIGN_JUSTIFIED);
				blahblah.setAlignment(Element.ALIGN_JUSTIFIED);
			}
			// in every chapter 3 sections will be added
			for (int j = 1; j < 4; j++) {
				Paragraph sTitle = new Paragraph("This is section " + j + " in chapter " + i, sectionFont);
				Section section = chapter.addSection(sTitle, 1);
				// for chapters > 2, the outine isn't open by default
				if (i > 2) section.setBookmarkOpen(false);
				// in all chapters except the 1st one, some extra text is added to section 3
				if (j == 3 && i > 1) {
					section.add(blahblah);
				}
				// in every section 3 subsections are added
				for (int k = 1; k < 4; k++) {
					Paragraph subTitle = new Paragraph("This is subsection " + k + " of section " + j, subsectionFont);
					Section subsection = section.addSection(subTitle, 3);
					// in the first subsection of section 3, extra text is added
					if (k == 1 && j == 3) {
						subsection.add(blahblahblah);
					}
					subsection.add(blahblah);
				}
				// in the section section of every chapter > 2 extra text is added
				if (j == 2 && i > 2) {
					section.add(blahblahblah);
				}
				// a new page is added after the second section in Chapter 1
				if (j == 2 && i == 1) {
					section.add(Chunk.NEXTPAGE);
				}
			}
			document.add(chapter);
		}
		// step 5
		document.close();
	}

		System.out.println("Destinations");

		// step 1
		Document document = new Document();
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("Destinations.pdf"));
		// step 3
		writer.setViewerPreferences(PdfWriter.PageModeUseOutlines);
		document.open();
		// step 4
		PdfContentByte cb = writer.getDirectContent();
            
		// we create a PdfTemplate
		PdfTemplate template = cb.createTemplate(25, 25);
            
		// we add some crosses to visualize the destinations
		template.moveTo(13, 0);
		template.lineTo(13, 25);
		template.moveTo(0, 13);
		template.lineTo(50, 13);
		template.stroke();
            
		// we add the template on different positions
		cb.addTemplate(template, 287, 787);
		cb.addTemplate(template, 187, 487);
		cb.addTemplate(template, 487, 287);
		cb.addTemplate(template, 87, 87);
            
		// we define the destinations
		PdfDestination d1 = new PdfDestination(PdfDestination.XYZ, 300, 800, 0);
		PdfDestination d2 = new PdfDestination(PdfDestination.FITH, 500);
		PdfDestination d3 = new PdfDestination(PdfDestination.FITR, 200, 300, 400, 500);
		PdfDestination d4 = new PdfDestination(PdfDestination.FITBV, 100);
		PdfDestination d5 = new PdfDestination(PdfDestination.FIT);
            
		// we define the outlines
		PdfOutline out1 = new PdfOutline(cb.getRootOutline(), d1, "root");
		PdfOutline out2 = new PdfOutline(out1, d2, "sub 1");
		PdfOutline out3 = new PdfOutline(out1, d3, "sub 2");
		PdfOutline out4 = new PdfOutline(out2, d4, "sub 2.1");
		PdfOutline out5 = new PdfOutline(out2, d5, "sub 2.2");
		// step 5
		document.close();
	}

		System.out.println("layers");

		// step 1
		Document document = new Document();
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document,
			new FileOutputStream("Layers.pdf"));
		writer.setPdfVersion(PdfWriter.VERSION_1_5);
		// step 3
		writer.setViewerPreferences(PdfWriter.PageModeUseOC);
		document.open();
		// step 4
		PdfContentByte cb = writer.getDirectContent();
		Phrase explanation = new Phrase("Layer grouping", new Font(
			Font.HELVETICA, 20, Font.BOLD, Color.red));
		ColumnText.showTextAligned(cb, Element.ALIGN_LEFT, explanation, 50,
			650, 0);
		PdfLayer l1 = new PdfLayer("Layer 1", writer);
		PdfLayer l2 = new PdfLayer("Layer 2", writer);
		PdfLayer l3 = new PdfLayer("Layer 3", writer);
		PdfLayerMembership m1 = new PdfLayerMembership(writer);
		m1.addMember(l2);
		m1.addMember(l3);
		Phrase p1 = new Phrase("Text in layer 1");
		Phrase p2 = new Phrase("Text in layer 2 or layer 3");
		Phrase p3 = new Phrase("Text in layer 3");
		cb.beginLayer(l1);
		ColumnText.showTextAligned(cb, Element.ALIGN_LEFT, p1, 50, 600, 0);
		cb.endLayer();
		cb.beginLayer(m1);
		ColumnText.showTextAligned(cb, Element.ALIGN_LEFT, p2, 50, 550, 0);
		cb.endLayer();
		cb.beginLayer(l3);
		ColumnText.showTextAligned(cb, Element.ALIGN_LEFT, p3, 50, 500, 0);
		cb.endLayer();
		PdfOCProperties p = writer.getOCProperties();
		PdfArray order = new PdfArray();
		order.add(l1.getRef());
		PdfArray group = new PdfArray();
		group.add(new PdfString("A group of two", PdfObject.TEXT_UNICODE));
		group.add(l2.getRef());
		group.add(l3.getRef());
		order.add(group);
		PdfDictionary d = new PdfDictionary();
		d.put(PdfName.ORDER, order);
		p.put(PdfName.D, d);
		// step 5
		document.close();
	}

