		System.out.println("End of Line");

		// step 1
		Document document = new Document();
		// step 2
		// we create a writer that listens to the document
		PdfWriter.getInstance(document,
			new FileOutputStream("EndOfLine.pdf"));

		// step 3
		document.open();
		// step 4
		Chunk chunk = new Chunk("quick brown fox jumps over the lazy dog ");
		for (int i = 0; i < 5; i++) {
			chunk.setTextRenderMode(PdfContentByte.TEXT_RENDER_MODE_STROKE, 0.3f, new Color(i * 30, i * 30, i * 30));
			document.add(chunk);
		for (int i = 0; i < 5; i++) {
			chunk.setTextRenderMode(PdfContentByte.TEXT_RENDER_MODE_STROKE, 0.3f, new Color(i * 30, i * 30, i * 30));
			document.add(chunk);
		}
		document.newPage();
		Phrase p = new Phrase(16f);
		for (int i = 0; i < 5; i++) {
			chunk = new Chunk("quick brown fox jumps over the lazy dog ");
			chunk.setTextRenderMode(PdfContentByte.TEXT_RENDER_MODE_STROKE, 0.3f, new Color(i * 30, i * 30, i * 30));
			p.add(chunk);
		for (int i = 0; i < 5; i++) {
			chunk = new Chunk("quick brown fox jumps over the lazy dog ");
			chunk.setTextRenderMode(PdfContentByte.TEXT_RENDER_MODE_STROKE, 0.3f, new Color(i * 30, i * 30, i * 30));
			p.add(chunk);
		}
		document.add(p);
		// step 5
		document.close();
	}

	if ("ellipse".equals(text)) {
		PdfContentByte cb = writer.getDirectContent();
		cb.setRGBColorStroke(0xFF, 0x00, 0x00);
		cb.ellipse(rect.left(), rect.bottom() - 5f, rect.right(), rect.top());
		cb.stroke();
		cb.resetRGBColorStroke();
	}
	else if ("box".equals(text)) {
		PdfContentByte cb = writer.getDirectContentUnder();
		rect.setGrayFill(0.5f);
		cb.rectangle(rect);
	}
}

	/**
	 * Generic page event.
	 * 
	 * @param args no arguments needed here
	 */
	public static void main(String[] args) throws Exception {

		System.out.println("Generic");

		// step 1
		Document document = new Document();
		// step 2
		// we create a writer that listens to the document
		PdfWriter writer = PdfWriter.getInstance(document,
			new FileOutputStream("Generic.pdf"));
		writer.setPageEvent(new Generic());
			
		// step 3
		document.open();
		// step 4
		Paragraph p = new Paragraph("Generic page event");
		document.add(p);
		Chunk box = new Chunk("box");
		box.setGenericTag("box");
		Chunk ellipse = new Chunk("ellipse");
		ellipse.setGenericTag("ellipse");
		p = new Paragraph("In this example, we will add chunks that are tagged as an ");
		p.add(ellipse);
		p.add(" and chunks that are tagged as a ");
		p.add(box);
		p.add(". Can you see the difference between ");
		Chunk c1 = new Chunk("this");
		c1.setGenericTag("box");
		Chunk c2 = new Chunk("that");
		c2.setGenericTag("ellipse");
		p.add(c1);
		p.add(" and ");
		p.add(c2);
		p.add("? One is a ");
		p.add(box);
		p.add("; the other an ");
		p.add(ellipse);
		document.add(p);
		// step 5
		document.close();
	}

    
	/**
	 * Generic page event.
	 * 
	 * @param args no arguments needed here
	 */
	public static void main(String[] args) throws Exception {

		System.out.println("Glossary");

		// step 1
		Document document = new Document();
		// step 2
		// we create a writer that listens to the document
		PdfWriter writer = PdfWriter.getInstance(document,
			new FileOutputStream("Glossary.pdf"));
		Glossary generic = new Glossary();
		writer.setPageEvent(generic);
			
		// step 3
		document.open();
		// step 4
		String[] f = new String[14];
		f[0] = "Courier";
		f[1] = "Courier Bold";
		f[2] = "Courier Italic";
		f[3] = "Courier Bold Italic";
		f[4] = "Helvetica";
		f[5] = "Helvetica bold";
		f[6] = "Helvetica italic";
		f[7] = "Helvetica bold italic";
		f[8] = "Times New Roman";
		f[9] = "Times New Roman bold";
		f[10] = "Times New Roman italic";
		f[11] = "Times New Roman bold italic";
		f[12] = "Symbol";
		f[13] = "Zapfdingbats";
		Font[] fonts = new Font[14];
		fonts[0] = FontFactory.getFont(FontFactory.COURIER, 12, Font.NORMAL);
		fonts[1] = FontFactory.getFont(FontFactory.COURIER, 12, Font.BOLD);
		fonts[2] = FontFactory.getFont(FontFactory.COURIER, 12, Font.ITALIC);
		fonts[3] = FontFactory.getFont(FontFactory.COURIER, 12, Font.BOLD | Font.ITALIC);
		fonts[4] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.NORMAL);
		fonts[5] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.BOLD);
		fonts[6] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.ITALIC);
		fonts[7] = FontFactory.getFont(FontFactory.HELVETICA, 12, Font.BOLD | Font.ITALIC);
		fonts[8] = FontFactory.getFont(FontFactory.TIMES_ROMAN, 12, Font.NORMAL);
		fonts[9] = FontFactory.getFont(FontFactory.TIMES_ROMAN, 12, Font.BOLD);
		fonts[10] = FontFactory.getFont(FontFactory.TIMES_ROMAN, 12, Font.ITALIC);
		fonts[11] = FontFactory.getFont(FontFactory.TIMES_ROMAN, 12, Font.BOLD | Font.ITALIC);
		fonts[12] = FontFactory.getFont(FontFactory.SYMBOL, 12, Font.NORMAL);
		fonts[13] = FontFactory.getFont(FontFactory.ZAPFDINGBATS, 12, Font.NORMAL);
		for (int i = 0; i < 14; i++) {
			Chunk chunk = new Chunk("This is font ", fonts[i]);
			Paragraph p = new Paragraph(chunk);
			p.add(new Phrase(new Chunk(f[i], fonts[i]).setGenericTag(f[i])));
			document.add(p);
			if (i % 4 == 3) {
				document.newPage();
		for (int i = 0; i < 14; i++) {
			Chunk chunk = new Chunk("This is font ", fonts[i]);
			Paragraph p = new Paragraph(chunk);
			p.add(new Phrase(new Chunk(f[i], fonts[i]).setGenericTag(f[i])));
			document.add(p);
			if (i % 4 == 3) {
				document.newPage();
			}
		}
            
		// we add the glossary
		document.newPage();
		for (Iterator i = generic.glossary.keySet().iterator(); i.hasNext(); ) {
			String key = (String) i.next();
			int page = ((Integer) generic.glossary.get(key)).intValue();
			Paragraph g = new Paragraph(key);
			g.add(" 
			g.add(String.valueOf(page));
			document.add(g);
		for (Iterator i = generic.glossary.keySet().iterator(); i.hasNext(); ) {
			String key = (String) i.next();
			int page = ((Integer) generic.glossary.get(key)).intValue();
			Paragraph g = new Paragraph(key);
			g.add(" 
			g.add(String.valueOf(page));
			document.add(g);
		}

		// step 5
		document.close();
	}

public class Glossary extends PdfPageEventHelper {
    
	/** keeps a glossary of words and the pages they appear on */
	public TreeMap glossary = new TreeMap();

		System.out.println("Hyphenation");

		// step 1
		Document document = new Document(PageSize.A6);
		// step 2
		// we create a writer that listens to the document
		PdfWriter.getInstance(document, new FileOutputStream(
			"Hyphenation.pdf"));

		// step 3
		document.open();
		// step 4
		String text = "It was the best of times, it was the worst of times, "
			+ "it was the age of wisdom, it was the age of foolishness, "
			+ "it was the epoch of belief, it was the epoch of incredulity, "
			+ "it was the season of Light, it was the season of Darkness, "
			+ "it was the spring of hope, it was the winter of despair, "
			+ "we had everything before us, we had nothing before us, "
			+ "we were all going direct to Heaven, we were all going direct "
			+ "the other way\u2014in short, the period was so far like the present "
			+ "period, that some of its noisiest authorities insisted on its "
			+ "being received, for good or for evil, in the superlative degree "
			+ "of comparison only.";
		Chunk ck = new Chunk(text);
		HyphenationAuto auto = new HyphenationAuto("en", "GB", 2, 2);
		ck.setHyphenation(auto);
		Paragraph p = new Paragraph(ck);
		p.setAlignment(Paragraph.ALIGN_JUSTIFIED);
		document.add(p);
		document.newPage();
		ck = new Chunk(text);
		auto = new HyphenationAuto("en", "US", 2, 2);
		ck.setHyphenation(auto);
		p = new Paragraph(ck);
		p.setAlignment(Paragraph.ALIGN_JUSTIFIED);
		document.add(p);

		// step 5
		document.close();
	}

		System.out.println("Underline, Strike through,...");

		// step 1
		Document document = new Document();
		// step 2
		// we create a writer that listens to the document
		PdfWriter.getInstance(document,
			new FileOutputStream("Lines.pdf"));

		// step 3
		document.open();
		// step 4
		Chunk underlined = new Chunk("underlined");
		underlined.setUnderline(0.2f, -2f);
		Paragraph p = new Paragraph("The following chunk is ");
		p.add(underlined);
		document.add(p);
		Chunk strikethru = new Chunk("strike through example");
		strikethru.setUnderline(0.5f, 3f);	
		document.add(strikethru);
		document.add(Chunk.NEWLINE);
		document.add(Chunk.NEWLINE);
		document.add(Chunk.NEWLINE);
		Chunk c;
		c = new Chunk("Multiple lines");
		c.setUnderline(new Color(0xFF, 0x00, 0x00), 0.0f, 0.3f, 0.0f, 0.4f, PdfContentByte.LINE_CAP_ROUND);
		c.setUnderline(new Color(0x00, 0xFF, 0x00), 5.0f, 0.0f, 0.0f, -0.5f, PdfContentByte.LINE_CAP_PROJECTING_SQUARE);
		c.setUnderline(new Color(0x00, 0x00, 0xFF), 0.0f, 0.2f, 15.0f, 0.0f, PdfContentByte.LINE_CAP_BUTT);
		document.add(c);
		document.add(Chunk.NEWLINE);
		document.add(Chunk.NEWLINE);
		document.add(Chunk.NEWLINE);
		c = new Chunk("Multiple lines", FontFactory.getFont(FontFactory.HELVETICA, 24));
		c.setUnderline(new Color(0xFF, 0x00, 0x00), 0.0f, 0.3f, 0.0f, 0.4f, PdfContentByte.LINE_CAP_ROUND);
		c.setUnderline(new Color(0x00, 0xFF, 0x00), 5.0f, 0.0f, 0.0f, -0.5f, PdfContentByte.LINE_CAP_PROJECTING_SQUARE);
		c.setUnderline(new Color(0x00, 0x00, 0xFF), 0.0f, 0.2f, 15.0f, 0.0f, PdfContentByte.LINE_CAP_BUTT);
		document.add(c);
		// step 5
		document.close();
	}

