		System.out.println("SplitCharacter");

		// step 1
		Document document = new Document();

		// step 2
		// we create a writer that listens to the document
		PdfWriter.getInstance(document,
			new FileOutputStream("SplitChar.pdf"));

		// step 3
		document.open();
		// step 4
		Chunk chunk;
		Paragraph p;
		String text = "Some.text.to.show.the.splitting.action.of.the.interface.";
		Font font = FontFactory.getFont(FontFactory.HELVETICA, 24);
            
		document.add(new Paragraph("Normal split."));
		chunk = new Chunk(text, font);
		p = new Paragraph(24, chunk);
		document.add(p);
            
		document.add(new Paragraph("The dot '.' is the split character."));
		chunk = new Chunk(text, font);
		chunk.setSplitCharacter(new SplitChar());
		p = new Paragraph(24, chunk);
		document.add(p);

		// step 5
		document.close();
	}


	/**
	 * @see com.lowagie.text.SplitCharacter#isSplitCharacter(int, int, int, char[], com.lowagie.text.pdf.PdfChunk[])
	 */
	public boolean isSplitCharacter(int start, int current, int end, char[] cc, PdfChunk[] ck) {
		char c;
		if (ck == null)
			c = cc[current];
		else
			c = ck[Math.min(current, ck.length - 1)].getUnicodeEquivalent(cc[current]);
		return (c == '.');
	}

		System.out.println("Sub- and Superscript");

		// step 1
		Document document = new Document();

		// step 2
		// we create a writer that listens to the document
		PdfWriter.getInstance(document,
			new FileOutputStream("SubSupScript.pdf"));

		// step 3
		document.open();
		// step 4
		String s = "quick brown fox jumps over the lazy dog";
		StringTokenizer st = new StringTokenizer(s, " ");
		float textrise = 6.0f;
		Chunk c;
		while (st.hasMoreTokens()) {
			c = new Chunk(st.nextToken());
			c.setTextRise(textrise);
			c.setUnderline(new Color(0xC0, 0xC0, 0xC0), 0.2f, 0.0f, 0.0f, 0.0f, PdfContentByte.LINE_CAP_BUTT);
			document.add(c);
			textrise -= 2.0f;
		while (st.hasMoreTokens()) {
			c = new Chunk(st.nextToken());
			c.setTextRise(textrise);
			c.setUnderline(new Color(0xC0, 0xC0, 0xC0), 0.2f, 0.0f, 0.0f, 0.0f, PdfContentByte.LINE_CAP_BUTT);
			document.add(c);
			textrise -= 2.0f;
		}

		// step 5
		document.close();
	}

		System.out.println("Width");

		// step 1
		Document document = new Document();

		// step 2
