		PdfWriter.getInstance(document,
			new FileOutputStream("Width.pdf"));

		// step 3
		document.open();
		// step 4
		Chunk c = new Chunk("quick brown fox jumps over the lazy dog");
		float w = c.getWidthPoint();
		Paragraph p = new Paragraph("The width of the chunk
		p.add(c);
		p.add("' is ");
		p.add(String.valueOf(w));
		p.add(" points or ");
		p.add(String.valueOf(w / 72f));
		p.add(" inches.");
		document.add(p);
		document.add(c);
		document.add(Chunk.NEWLINE);
		c.setHorizontalScaling(0.5f);
		document.add(c);
		document.add(c);

		// step 5
		document.close();
	}

		System.out.println("Simple single object columns");
        
		// step 1
		Document document = new Document();
        
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("column.pdf"));
            
		// step 3
		document.open();
            
		// step 4
            
		// we create some content
		BaseFont bf = BaseFont.createFont(BaseFont.COURIER, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
		Font font = new Font(bf, 11, Font.NORMAL);
            
		Phrase unicodes = new Phrase(15, "UNI\n", font);
		Phrase characters = new Phrase(15, "\n", font);
		Phrase names = new Phrase(15, "NAME\n", font);
            
		for (int i = 0; i < 27; i++) {
			unicodes.add(uni[i] + "\n");
			characters.add(code[i] + "\n");
			names.add(name[i] + "\n");
		for (int i = 0; i < 27; i++) {
			unicodes.add(uni[i] + "\n");
			characters.add(code[i] + "\n");
			names.add(name[i] + "\n");
		}
            
		// we grab the ContentByte and do some stuff with it
		PdfContentByte cb = writer.getDirectContent();
            
		ColumnText ct = new ColumnText(cb);
		ct.setSimpleColumn(unicodes, 60, 300, 100, 300 + 28 * 15, 15, Element.ALIGN_CENTER);
		ct.go();
		cb.rectangle(103, 295, 52, 8 + 28 * 15);
		cb.stroke();
		ct.setSimpleColumn(characters, 105, 300, 150, 300 + 28 * 15, 15, Element.ALIGN_RIGHT);
		ct.go();
		ct.setSimpleColumn(names, 160, 300, 500, 300 + 28 * 15, 15, Element.ALIGN_LEFT);
		ct.go();
        
		// step 5
		document.close();
	}

    
	/**
	 * Demonstrating the use of ColumnText
	 * @param args no arguments needed
	 */
	public static void main(String[] args) throws Exception {
        
		System.out.println("Simple single object columns");
        
		// step 1
		Document document = new Document();
        
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("column.pdf"));
            
		// step 3
		document.open();
            
		// step 4
            
		// we create some content
		BaseFont bf = BaseFont.createFont(BaseFont.COURIER, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
		Font font = new Font(bf, 11, Font.NORMAL);
            
		Phrase unicodes = new Phrase(15, "UNI\n", font);
		Phrase characters = new Phrase(15, "\n", font);
		Phrase names = new Phrase(15, "NAME\n", font);
            
		for (int i = 0; i < 27; i++) {
			unicodes.add(uni[i] + "\n");
			characters.add(code[i] + "\n");
			names.add(name[i] + "\n");
		}
            
		// we grab the ContentByte and do some stuff with it
		PdfContentByte cb = writer.getDirectContent();
            
		ColumnText ct = new ColumnText(cb);
		ct.setSimpleColumn(unicodes, 60, 300, 100, 300 + 28 * 15, 15, Element.ALIGN_CENTER);
		ct.go();
		cb.rectangle(103, 295, 52, 8 + 28 * 15);
		cb.stroke();
		ct.setSimpleColumn(characters, 105, 300, 150, 300 + 28 * 15, 15, Element.ALIGN_RIGHT);
		ct.go();
		ct.setSimpleColumn(names, 160, 300, 500, 300 + 28 * 15, 15, Element.ALIGN_LEFT);
		ct.go();
        
		// step 5
		document.close();
	}
    
