		}
		do {
			cb.setLineWidth(5);
			cb.setColorStroke(Color.GRAY);
			cb.moveTo(centerX , document.top());
			cb.lineTo(centerX, document.bottom());
			cb.stroke();
			cb.moveTo(centerX, diamondTop);
			cb.lineTo(centerX - (diamondWidth/2), diamondTop - (diamondHeight / 2));
