		System.out.println("Images using java.awt.image");
        
		// step 1
		Document document = new Document();
            
		// step 2
		// we create a writer that listens to the document
		// and directs a PDF-stream to a file
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("awt_image.pdf"));
            
		// step 3
		document.open();
            
		// step 4
		for (int i = 0; i < 300; i++) {
			document.add(new Phrase("Who is this? "));
		for (int i = 0; i < 300; i++) {
			document.add(new Phrase("Who is this? "));
		}
		PdfContentByte cb = writer.getDirectContent();
		java.awt.Image awtImage = Toolkit.getDefaultToolkit().getImage("H.gif"); //.createImage("H.gif");
		Image image = Image.getInstance(awtImage, null);
		image.setAbsolutePosition(100, 500);
		cb.addImage(image);
		Image gif = Image.getInstance(awtImage, new Color(0x00, 0xFF, 0xFF), true);
		gif.setAbsolutePosition(300, 500);
		cb.addImage(gif);
		Image img1 = Image.getInstance(awtImage, null, true);
		img1.setAbsolutePosition(100, 200);
		cb.addImage(img1);
		Image img2 = Image.getInstance(awtImage, new Color(0xFF, 0xFF, 0x00), false);
		img2.setAbsolutePosition(300, 200);
		cb.addImage(img2);
        
		// step 5
		document.close();
	}

/**
 * Sets the backgroundcolor of the cover.
 * @param backgroundcolor a Color object.
 */
public void setBackgroundcolor(Color backgroundcolor) {
	this.backgroundcolor = backgroundcolor;
}

/**
 * Sets the Image that has to be on the front of the cover.
 * @param front an Image object.
 */
public void setFront(Image front) {
	this.front = front;
}

/**
 * Sets the Image that has to be on the back of the cover.
 * @param side an Image object.
 */
public void setSide(Image side) {
	this.side = side;
}

/**
 * Sets the title of the movie (will be printed on the side if there is no side image)
 * @param title The title to set.
 */
public void setTitle(String title) {
	this.title = title;
}

/**
 * Sets the filename of the resulting PDF
 * @param filename The filename to set.
 */
public void setFilename(String filename) {
	this.filename = filename;
}


/** the name of the file that has to be generated. */
protected String filename;
/** the title that has to be on the side of the cover. */
protected String title;
/** the backgroundcolor of the cover. */
protected Color backgroundcolor;
/** the front image of the cover. */
protected Image front;
/** the side image of the cover. */
protected Image side;
/** the back image of the cover. */
protected Image back;

/**
 * Creates a DVD Cover object.
 */
public DvdCover() {
}


/**
 * Example that generates a DVD Cover in PDF.
 * @param args	an array containing [0] a filename [1] a title [2] a backgroundcolor [3] a front image [4] a back image [5] a side image
 */
public static void main(String[] args) throws Exception {
	System.out.println("DVD Cover");
	DvdCover cover = new DvdCover();
	if (args.length > 0) {
		cover.setFilename(args[0]);
	}
	if (args.length > 1) {
		cover.setTitle(args[1]);
	}
	if (args.length > 2) {
		cover.setBackgroundcolor(Color.decode(args[2]));
	}
	if (args.length > 3) {
		cover.setFront(Image.getInstance(args[3]));
	}
	if (args.length > 4) {
		cover.setBack(Image.getInstance(args[4]));
	}
	if (args.length > 5) {
		cover.setSide(Image.getInstance(args[5]));
	}
	cover.generatePdf();
}

    
	/**
	 * Method that generates the actual PDF file.
	 */
	public void generatePdf() throws Exception {   
        
		// step 1
		Rectangle pageSize = new Rectangle(780, 525);
		if (backgroundcolor != null) pageSize.setBackgroundColor(backgroundcolor);
		Document document = new Document(pageSize);
        
		// step 2
		// we create a writer that listens to the document
		// and directs a PDF-stream to a file
		if (filename == null) { filename = "dvdcover.pdf"; }
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream(filename));
                        
		// step 3
		document.open();
            
		// step 4
		PdfContentByte cb = writer.getDirectContent();
		if (title != null) {
			cb.setFontAndSize(BaseFont.createFont(BaseFont.HELVETICA, BaseFont.WINANSI, false), 24);
			cb.beginText();
			if (front == null) {
				cb.showTextAligned(Element.ALIGN_CENTER, title, 595f, 262f, 0f);
			}
			if (side == null) {
				cb.showTextAligned(Element.ALIGN_CENTER, title, 385f, 262f, 270f);
			}
			cb.endText(); 
		}
		cb.moveTo(370, 0);
		cb.lineTo(370, 525);
		cb.moveTo(410, 525);
		cb.lineTo(410, 0);
		cb.stroke();
		if (front != null) {
			front.scaleToFit(370, 525);
			front.setAbsolutePosition(410f + (370f - front.scaledWidth()) / 2f, (525f - front.scaledHeight()) / 2f);
			document.add(front);
		}
		if (back != null) {
			back.scaleToFit(370, 525);
			back.setAbsolutePosition((370f - back.scaledWidth()) / 2f, (525f - back.scaledHeight()) / 2f);
			document.add(back);
		}
		if (side != null) {
			side.scaleToFit(40, 525);
			side.setAbsolutePosition(370 + (40f - side.scaledWidth()) / 2f, (525f - side.scaledHeight()) / 2f);
			document.add(side);
		}
        
		// step 5
		document.close();
	}

