		System.out.println("masked images");
        
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);

		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("maskedImages.pdf"));
            
		document.open();
		Paragraph p = new Paragraph("Some text behind a masked image.");
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
            
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		document.add(p);
		PdfContentByte cb = writer.getDirectContent();
		byte maskr[] = {(byte)0x3c, (byte)0x7e, (byte)0xe7, (byte)0xc3, (byte)0xc3, (byte)0xe7, (byte)0x7e, (byte)0x3c};
		Image mask = Image.getInstance(8, 8, 1, 1, maskr);
		mask.makeMask();
		mask.setInvertMask(true);
		Image image = Image.getInstance("otsoe.jpg");
		image.setImageMask(mask);
		image.setAbsolutePosition(60, 550);
		// explicit masking
		cb.addImage(image);
		// stencil masking
		cb.setRGBColorFill(255, 0, 0);
		cb.addImage(mask, mask.scaledWidth() * 8, 0, 0, mask.scaledHeight() * 8, 100, 450);
		cb.setRGBColorFill(0, 255, 0);
		cb.addImage(mask, mask.scaledWidth() * 8, 0, 0, mask.scaledHeight() * 8, 100, 400);
		cb.setRGBColorFill(0, 0, 255);
		cb.addImage(mask, mask.scaledWidth() * 8, 0, 0, mask.scaledHeight() * 8, 100, 350);
		document.close();
	}

