		Document document = new Document();
		// step 2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("ean128.pdf"));
		// step 3
		document.open();
		// step 4
		PdfContentByte cb = writer.getDirectContent();
		PdfPTable pageTot = new PdfPTable(1);
		pageTot.getDefaultCell().setPadding(0f);
		pageTot.getDefaultCell().setBorder(Rectangle.NO_BORDER);
		pageTot.getDefaultCell().setHorizontalAlignment(Element.ALIGN_LEFT);
		pageTot.setWidthPercentage(100f);
		//Data for the barcode 
		// The blocks whith the type 402 and 90 are of variable size so you must put a FNC1
		// to delimitate the block
		String code402 = "24132399420058289"+Barcode128.FNC1;
		String code90 = "3700000050"+Barcode128.FNC1;
		String code421 = "422356";
		String data = code402 + code90 + code421;
            
		PdfPTable cell = new PdfPTable(1);
		cell.getDefaultCell().setBorder(Rectangle.NO_BORDER);
		cell.getDefaultCell().setPadding(0f);
            
		PdfPCell info = new PdfPCell(new Phrase("Barcode EAN 128"));
		info.setBorder(Rectangle.NO_BORDER);
		pageTot.addCell(info);
            
		Barcode128 shipBarCode = new Barcode128();
		shipBarCode.setX(0.75f);
		shipBarCode.setN(1.5f);
		shipBarCode.setChecksumText(true);
		shipBarCode.setGenerateChecksum(true);
		shipBarCode.setSize(10f);
		shipBarCode.setTextAlignment(Element.ALIGN_CENTER);
		shipBarCode.setBaseline(10f);
		shipBarCode.setCode(data);
		shipBarCode.setBarHeight(50f);
            
		Image imgShipBarCode = shipBarCode.createImageWithBarcode(cb, Color.black, Color.blue);
		PdfPCell shipment = new PdfPCell(new Phrase(
			new Chunk(imgShipBarCode, 0, 0)));
		shipment.setFixedHeight(shipBarCode.getBarcodeSize().height() + 16f);
		shipment.setPaddingTop(5f);
		shipment.setPaddingBottom(10f);
		shipment.setBorder(Rectangle.BOX);
		shipment.setVerticalAlignment(Element.ALIGN_TOP);
		shipment.setHorizontalAlignment(Element.ALIGN_CENTER);
		cell.addCell(shipment);
		pageTot.addCell(cell);
		document.add(pageTot);
		// step 5
		document.close();
	}

	BarcodePDF417 pdf417 = new BarcodePDF417();
	String text = "It was the best of times, it was the worst of times, " + 
		"it was the age of wisdom, it was the age of foolishness, " +
		"it was the epoch of belief, it was the epoch of incredulity, " +
		"it was the season of Light, it was the season of Darkness, " +
		"it was the spring of hope, it was the winter of despair, " +
		"we had everything before us, we had nothing before us, " +
		"we were all going direct to Heaven, we were all going direct " +
		"the other way - in short, the period was so far like the present " +
		"period, that some of its noisiest authorities insisted on its " +
		"being received, for good or for evil, in the superlative degree " +
		"of comparison only.";
	pdf417.setText(text);
	Document document = new Document(PageSize.A4, 50, 50, 50, 50);
	PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("pdf417.pdf"));
	document.open();
	Image img = pdf417.getImage();
	img.scalePercent(50, 50 * pdf417.getYHeight());
	document.add(img);
	document.close();
}

	if (args.length < 3) {
		System.err.println("OddEven needs 3 Arguments.");
		System.out.println("Usage
		System.exit(1);
	}
	RandomAccessFileOrArray odd = new RandomAccessFileOrArray(args[0]);
	RandomAccessFileOrArray even = new RandomAccessFileOrArray(args[1]);
	Image img = TiffImage.getTiffImage(odd, 1);
	Document document = new Document(new Rectangle(img.scaledWidth(),
		img.scaledHeight()));
	PdfWriter writer = PdfWriter.getInstance(document,
		new FileOutputStream(args[2]));
	document.open();
	PdfContentByte cb = writer.getDirectContent();
	int count = Math.max(TiffImage.getNumberOfPages(odd), TiffImage
		.getNumberOfPages(even));
	System.out.println("Combining 2 TIFF files into 1 PDF");
	for (int c = 0; c < count; ++c) {
		Image imgOdd = TiffImage.getTiffImage(odd, c + 1);
		Image imgEven = TiffImage.getTiffImage(even, count - c);
		document.setPageSize(new Rectangle(imgOdd.scaledWidth(),
			imgOdd.scaledHeight()));
		document.newPage();
		imgOdd.setAbsolutePosition(0, 0);
		cb.addImage(imgOdd);
		document.setPageSize(new Rectangle(imgEven.scaledWidth(),
			imgEven.scaledHeight()));
		document.newPage();
		imgEven.setAbsolutePosition(0, 0);
		cb.addImage(imgEven);
	for (int c = 0; c < count; ++c) {
		Image imgOdd = TiffImage.getTiffImage(odd, c + 1);
		Image imgEven = TiffImage.getTiffImage(even, count - c);
		document.setPageSize(new Rectangle(imgOdd.scaledWidth(),
			imgOdd.scaledHeight()));
		document.newPage();
		imgOdd.setAbsolutePosition(0, 0);
		cb.addImage(imgOdd);
		document.setPageSize(new Rectangle(imgEven.scaledWidth(),
			imgEven.scaledHeight()));
		document.newPage();
		imgEven.setAbsolutePosition(0, 0);
		cb.addImage(imgEven);
	}
	odd.close();
	even.close();
	document.close();
}

	if (args.length < 1) {
		throw new Exception("Tiff2Pdf needs an argument.");
	}
	String tiff_file;
	String pdf_file;
	for (int i = 0; i < args.length; i++) {
		tiff_file = args[i];
		pdf_file = tiff_file.substring(0, tiff_file.lastIndexOf(".") + 1) + "pdf";
		Document document = new Document();
		PdfWriter writer = PdfWriter.getInstance(document,
			new FileOutputStream(pdf_file));
		int pages = 0;
		document.open();
		PdfContentByte cb = writer.getDirectContent();
		RandomAccessFileOrArray ra = null;
		int comps = 0;
		ra = new RandomAccessFileOrArray(tiff_file);
		comps = TiffImage.getNumberOfPages(ra);
		System.out.println("Processing
		for (int c = 0; c < comps; ++c) {
			Image img = TiffImage.getTiffImage(ra, c + 1);
			if (img != null) {
				System.out.println("page " + (c + 1));
				if (img.scaledWidth() > 500 || img.scaledHeight() > 700) {
					img.scaleToFit(500, 700);
				}
				img.setAbsolutePosition(20, 20);
				document.add(new Paragraph(tiff_file + " - page " + (c + 1)));
				cb.addImage(img);
				document.newPage();
				++pages;
		for (int c = 0; c < comps; ++c) {
			Image img = TiffImage.getTiffImage(ra, c + 1);
			if (img != null) {
				System.out.println("page " + (c + 1));
				if (img.scaledWidth() > 500 || img.scaledHeight() > 700) {
					img.scaleToFit(500, 700);
				}
				img.setAbsolutePosition(20, 20);
				document.add(new Paragraph(tiff_file + " - page " + (c + 1)));
				cb.addImage(img);
				document.newPage();
				++pages;
			}
		}
		ra.close();
		document.close();
	for (int i = 0; i < args.length; i++) {
		tiff_file = args[i];
		pdf_file = tiff_file.substring(0, tiff_file.lastIndexOf(".") + 1) + "pdf";
		Document document = new Document();
		PdfWriter writer = PdfWriter.getInstance(document,
			new FileOutputStream(pdf_file));
		int pages = 0;
		document.open();
		PdfContentByte cb = writer.getDirectContent();
		RandomAccessFileOrArray ra = null;
		int comps = 0;
		ra = new RandomAccessFileOrArray(tiff_file);
		comps = TiffImage.getNumberOfPages(ra);
		System.out.println("Processing
		for (int c = 0; c < comps; ++c) {
			Image img = TiffImage.getTiffImage(ra, c + 1);
			if (img != null) {
				System.out.println("page " + (c + 1));
				if (img.scaledWidth() > 500 || img.scaledHeight() > 700) {
					img.scaleToFit(500, 700);
				}
				img.setAbsolutePosition(20, 20);
				document.add(new Paragraph(tiff_file + " - page " + (c + 1)));
				cb.addImage(img);
				document.newPage();
				++pages;
			}
		}
		ra.close();
		document.close();
	}
