		System.out.println("Height");
		// step1
		Document document = new Document(PageSize.A4);

		// step2
		PdfWriter writer = PdfWriter.getInstance(document,
			new FileOutputStream("CellHeights.pdf"));
		// step3
		document.open();
		// step4
		PdfPTable table = new PdfPTable(2);
		table.setExtendLastRow(true);
		PdfPCell cell;
			
		// wrap / nowrap
		cell = new PdfPCell(new Paragraph("blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah blah"));
		table.addCell("wrap");
		cell.setNoWrap(false);
		table.addCell(cell);
		table.addCell("no wrap");
		cell.setNoWrap(true);
		table.addCell(cell);
			
		// height
		cell = new PdfPCell(new Paragraph("1. blah blah\n2. blah blah blah\n3. blah blah\n4. blah blah blah\n5. blah blah\n6. blah blah blah\n7. blah blah\n8. blah blah blah"));
		table.addCell("height");
		table.addCell(cell);
		table.addCell("fixed height");
		cell.setFixedHeight(50f);
		table.addCell(cell);
		table.addCell("minimum height");
		cell = new PdfPCell(new Paragraph("x"));
		cell.setMinimumHeight(50f);
		table.addCell(cell);
		table.addCell("extend last row");
		cell = new PdfPCell(new Paragraph("almost no content, but the row is extended"));
		table.addCell(cell);
		document.add(table);

		// step5
		document.close();
	}

	System.out.println("padding - leading");
	// step1
	Document document = new Document(PageSize.A4.rotate(), 10, 10, 10, 10);
	// step2
	PdfWriter writer = PdfWriter.getInstance(document,
		new FileOutputStream("PaddingLeading.pdf"));
	// step3
	document.open();
	// step4
	PdfPTable table = new PdfPTable(2);
	PdfPCell cell;
	Paragraph p = new Paragraph("Quick brown fox jumps over the lazy dog. Quick brown fox jumps over the lazy dog.");
	table.addCell("default");
	table.addCell(p);
	table.addCell("padding 10");
	cell = new PdfPCell(p);
	cell.setPadding(10f);
	table.addCell(cell);
	table.addCell("no padding at all");
	cell = new PdfPCell(p);
	cell.setPadding(0f);
	table.addCell(cell);
	table.addCell("no padding at the top; large padding at the left");
	cell = new PdfPCell(p);
	cell.setPaddingTop(0f);
	cell.setPaddingLeft(20f);
	table.addCell(cell);
	document.add(table);
		
	document.newPage();
	table = new PdfPTable(2);
	table.addCell("no leading at all");
	table.getDefaultCell().setLeading(0f, 0f);
	table.addCell("blah blah\nblah blah blah\nblah blah\nblah blah blah\nblah blah\nblah blah blah\nblah blah\nblah blah blah\n");
	table.getDefaultCell().setLeading(14f, 0f);
	table.addCell("fixed leading of 14pt");
	table.addCell("blah blah\nblah blah blah\nblah blah\nblah blah blah\nblah blah\nblah blah blah\nblah blah\nblah blah blah\n");
	table.addCell("relative leading of 1.0 times the fontsize");
	table.getDefaultCell().setLeading(0f, 1.0f);
	table.addCell("blah blah\nblah blah blah\nblah blah\nblah blah blah\nblah blah\nblah blah blah\nblah blah\nblah blah blah\n");
	document.add(table);
	// step5
	document.close();
}

