	System.out.println("Nested Tables");
	// step1
	Document document = new Document(PageSize.A4.rotate(), 10, 10, 10, 10);
	// step2
	PdfWriter writer = PdfWriter.getInstance(document,
		new FileOutputStream("NestedTables.pdf"));
	// step3
	document.open();
	// step4
	PdfPTable table = new PdfPTable(4);
	PdfPTable nested1 = new PdfPTable(2);
	nested1.addCell("1.1");
	nested1.addCell("1.2");
	PdfPTable nested2 = new PdfPTable(1);
	nested2.addCell("2.1");
	nested2.addCell("2.2");
	for (int k = 0; k < 24; ++k) {
		if (k == 1) {
			table.addCell(nested1);
		}
		else if (k == 20) {
			table.addCell(nested2);
		}
		else {
			table.addCell("cell " + k);
	for (int k = 0; k < 24; ++k) {
		if (k == 1) {
			table.addCell(nested1);
		}
		else if (k == 20) {
			table.addCell(nested2);
		}
		else {
			table.addCell("cell " + k);
		}
	}
	document.add(table);
	// step 5
	document.close();
	// step5
	document.close();
}

	PdfContentByte cb = canvases[PdfPTable.TEXTCANVAS];
	cb.moveTo(position.left(), position.bottom());
	cb.lineTo(position.right(), position.top());
	cb.stroke();
}


	/**
	 * General example using cell events.
	 * 
	 * @param args
	 *            no arguments needed
	 */
	public static void main(String[] args) throws Exception {

		System.out.println("CellEvents");
		// step1
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);
		// step2
		PdfWriter writer = PdfWriter.getInstance(document,
			new FileOutputStream("CellEvents.pdf"));
		// step3
		document.open();
		// step4
		CellEvents event = new CellEvents();
		Image im = Image.getInstance("otsoe.jpg");
		im.setRotationDegrees(30);
		PdfPTable table = new PdfPTable(4);
		table.addCell("text 1");
		PdfPCell cell = new PdfPCell(im, true);
		cell.setCellEvent(event);
		table.addCell(cell);
		table.addCell("text 3");
		im.setRotationDegrees(0);
		table.addCell(im);
		table.setTotalWidth(300);
		PdfContentByte cb = writer.getDirectContent();
		table.writeSelectedRows(0, -1, 50, 600, cb);
		table.setHeaderRows(3);
		document.add(table);
		// step5
		document.close();
	}

	float widths[] = width[0];
	float x1 = widths[0];
	float x2 = widths[widths.length - 1];
	float y1 = height[0];
	float y2 = height[height.length - 1];
	PdfContentByte canvas = canvases[PdfPTable.LINECANVAS];
	canvas.setRGBColorStroke(0x00, 0x00, 0xFF);
	canvas.rectangle(x1, y1, x2 - x1, y2 - y1);
	canvas.stroke();
	canvas.resetRGBColorStroke();
}


	/**
	 * @see com.lowagie.text.pdf.PdfPCellEvent#cellLayout(com.lowagie.text.pdf.PdfPCell,
	 *      com.lowagie.text.Rectangle, com.lowagie.text.pdf.PdfContentByte[])
	 */
	public void cellLayout(PdfPCell cell, Rectangle position,
		PdfContentByte[] canvases) {
		float x1 = position.left() + 2;
		float x2 = position.right() - 2;
		float y1 = position.top() - 2;
		float y2 = position.bottom() + 2;
		PdfContentByte canvas = canvases[PdfPTable.LINECANVAS];
		canvas.setRGBColorStroke(0xFF, 0x00, 0x00);
		canvas.rectangle(x1, y1, x2 - x1, y2 - y1);
		canvas.stroke();
		canvas.resetRGBColorStroke();
	}


	/**
	 * Example originally written by Wendy Smoak to generate a Table with
	 * 'floating boxes'. Adapted by Bruno Lowagie.
	 * 
	 * @param args
	 */
	public static void main(String[] args) throws Exception {
		FloatingBoxes floatingBoxes = new FloatingBoxes();
		// step 1
		Document document = new Document();
		// step 2
		PdfWriter.getInstance(document, new FileOutputStream(
			"FloatingBoxes.pdf"));
		// step 3
		document.open();
		// step 4
		PdfPTable table = new PdfPTable(2);
		table.setTableEvent(floatingBoxes);
		table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
		table.getDefaultCell().setCellEvent(floatingBoxes);
		table.getDefaultCell().setPadding(5f);
		table.addCell("value");
		table.addCell("name");
		table.addCell(new Paragraph("dog"));
		table.addCell(new Paragraph("cat"));
		table.addCell(new Paragraph("bird"));
		table.addCell(new Paragraph("horse"));
		document.add(table);
		// step 5
		document.close();
	}

		System.out.println("FragmentTable");
		int fragmentsize = Integer.parseInt(args[0]);
		// step1
		Document document = new Document(PageSize.A4.rotate(), 10, 10, 10, 10);
		// step2
		PdfWriter writer = PdfWriter.getInstance(document,
			new FileOutputStream("FragmentTable.pdf"));
		// step3
		document.open();
		// step4
		Font font = FontFactory.getFont("Helvetica", 8, Font.BOLD,
			Color.BLACK);

		PdfPTable table = new PdfPTable(2);
		table.setWidthPercentage(100f);

		PdfPCell h1 = new PdfPCell(new Paragraph("Header 1", font));
		PdfPCell h2 = new PdfPCell(new Paragraph("Header 2", font));
		table.setHeaderRows(1);
		table.addCell(h1);
		table.addCell(h2);
		PdfPCell cell;
		for (int row = 1; row <= 2000; row++) {
			if (row % fragmentsize == fragmentsize - 1) {
				document.add(table);
				table.deleteBodyRows();
				table.setSkipFirstHeader(true);
			}
			cell = new PdfPCell(new Paragraph(String.valueOf(row), font));
			table.addCell(cell);
			cell = new PdfPCell(
				new Paragraph(
				"Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Nulla mauris nibh, ultricies nec, adipiscing eget.",
				font));
			table.addCell(cell);
		for (int row = 1; row <= 2000; row++) {
			if (row % fragmentsize == fragmentsize - 1) {
				document.add(table);
				table.deleteBodyRows();
				table.setSkipFirstHeader(true);
			}
			cell = new PdfPCell(new Paragraph(String.valueOf(row), font));
			table.addCell(cell);
			cell = new PdfPCell(
				new Paragraph(
				"Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Nulla mauris nibh, ultricies nec, adipiscing eget.",
				font));
			table.addCell(cell);
		}
		document.add(table);
		// step5
		document.close();
	}

		System.out.println("Split Table");
		// step1
		Document document = new Document(PageSize.A4, 10, 10, 10, 10);
		// step2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream(
			"SplitTable.pdf"));
		// step3
		document.open();
		// step4

		PdfContentByte cb = writer.getDirectContent();
		PdfPTable table = new PdfPTable(10);
		for (int k = 1; k <= 100; ++k) {
			table.addCell("The number " + k);
		for (int k = 1; k <= 100; ++k) {
			table.addCell("The number " + k);
		}
		table.setTotalWidth(800);
		table.writeSelectedRows(0, 5, 0, -1, 50, 650, cb);
		document.newPage();
		table.writeSelectedRows(5, -1, 0, -1, 50, 650, cb);
		document.close();
		// step5
		document.close();
	}

		float widths[] = width[0];
        
		PdfContentByte cb = canvases[PdfPTable.TEXTCANVAS];
		cb.saveState();
		// border for the complete table
		cb.setLineWidth(2);
		cb.setRGBColorStroke(255, 0, 0);
		cb.rectangle(widths[0], heights[heights.length - 1], widths[widths.length - 1] - widths[0], heights[0] - heights[heights.length - 1]);
		cb.stroke();
        
		// border for the header rows
		if (headerRows > 0) {
			float headerHeight = heights[0];
			for (int k = 0; k < headerRows; ++k)
				headerHeight += heights[k];
			for (int k = 0; k < headerRows; ++k)
				headerHeight += heights[k];
			cb.setRGBColorStroke(0, 0, 255);
			cb.rectangle(widths[0], heights[headerRows], widths[widths.length - 1] - widths[0], heights[0] - heights[headerRows]);
			cb.stroke();
		}
		cb.restoreState();
        
		cb = canvases[PdfPTable.BASECANVAS];
		cb.saveState();
		// border for the cells
		cb.setLineWidth(.5f);
		// loop over the rows
		for (int line = 0; line < heights.length - 1; ++line) {
			// loop over the columns
			for (int col = 0; col < widths.length - 1; ++col) {
				if (line == 0 && col == 0)
					cb.setAction(new PdfAction("http
						widths[col], heights[line + 1], widths[col + 1], heights[line]);
				cb.setRGBColorStrokeF((float)Math.random(), (float)Math.random(), (float)Math.random());
				// horizontal borderline
				cb.moveTo(widths[col], heights[line]);
				cb.lineTo(widths[col + 1], heights[line]);
				cb.stroke();
				// vertical borderline
				cb.setRGBColorStrokeF((float)Math.random(), (float)Math.random(), (float)Math.random());
				cb.moveTo(widths[col], heights[line]);
				cb.lineTo(widths[col], heights[line + 1]);
				cb.stroke();
			for (int col = 0; col < widths.length - 1; ++col) {
		for (int line = 0; line < heights.length - 1; ++line) {
			// loop over the columns
			for (int col = 0; col < widths.length - 1; ++col) {
				if (line == 0 && col == 0)
					cb.setAction(new PdfAction("http
						widths[col], heights[line + 1], widths[col + 1], heights[line]);
				cb.setRGBColorStrokeF((float)Math.random(), (float)Math.random(), (float)Math.random());
				// horizontal borderline
				cb.moveTo(widths[col], heights[line]);
				cb.lineTo(widths[col + 1], heights[line]);
				cb.stroke();
				// vertical borderline
				cb.setRGBColorStrokeF((float)Math.random(), (float)Math.random(), (float)Math.random());
				cb.moveTo(widths[col], heights[line]);
				cb.lineTo(widths[col], heights[line + 1]);
				cb.stroke();
			}
		}
		cb.restoreState();
	}

    
	/**
	 * General example using table events.
	 * @param args
	 * 		no arguments needed
	 */
	public static void main(String[] args) throws Exception {

		System.out.println("TableEvents 1");
		// step1
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);
		// step2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("TableEvents1.pdf"));
		// step3
		document.open();
		// step4
		PdfPTable table = new PdfPTable(4);
		table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
		for (int k = 0; k < 24; ++k) {
			if (k != 0)
				table.addCell(String.valueOf(k));
			else
				table.addCell("This is an URL");
		for (int k = 0; k < 24; ++k) {
			if (k != 0)
				table.addCell(String.valueOf(k));
			else
				table.addCell("This is an URL");
		}
		TableEvents1 event = new TableEvents1();
		table.setTableEvent(event);
            
		// add the table with document add
		document.add(table);
		// add the table at an absolute position
		table.setTotalWidth(300);
		table.writeSelectedRows(0, -1, 100, 600, writer.getDirectContent());
            
		document.newPage();
            
		table = new PdfPTable(4);
		float fontSize = 12;
		BaseFont bf = BaseFont.createFont("Helvetica", "winansi", false);
		table.getDefaultCell().setPaddingTop(bf.getFontDescriptor(BaseFont.ASCENT, fontSize) - fontSize + 2);
		table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
		for (int k = 0; k < 500 * 4; ++k) {
			if (k == 0)
				table.addCell(new Phrase("This is an URL", new Font(bf, fontSize)));
			else
				table.addCell(new Phrase(String.valueOf(k), new Font(bf, fontSize)));
		for (int k = 0; k < 500 * 4; ++k) {
			if (k == 0)
				table.addCell(new Phrase("This is an URL", new Font(bf, fontSize)));
			else
				table.addCell(new Phrase(String.valueOf(k), new Font(bf, fontSize)));
		}
		table.setTableEvent(event);
		table.setHeaderRows(3);
		document.add(table);
		// step5
		document.close();
	}

		float widths[] = width[0];
        
		PdfContentByte cb = canvases[PdfPTable.TEXTCANVAS];
		cb.saveState();
		// border for the complete table
		cb.setLineWidth(2);
		cb.setRGBColorStroke(255, 0, 0);
		cb.rectangle(widths[0], heights[heights.length - 1], widths[widths.length - 1] - widths[0], heights[0] - heights[heights.length - 1]);
		cb.stroke();
        
		// border for the header rows
		if (headerRows > 0) {
			float headerHeight = heights[0];
			for (int k = 0; k < headerRows; ++k)
				headerHeight += heights[k];
			for (int k = 0; k < headerRows; ++k)
				headerHeight += heights[k];
			cb.setRGBColorStroke(0, 0, 255);
			cb.rectangle(widths[0], heights[headerRows], widths[widths.length - 1] - widths[0], heights[0] - heights[headerRows]);
			cb.stroke();
		}
		cb.restoreState();
        
		cb = canvases[PdfPTable.BASECANVAS];
		cb.saveState();
		// border for the cells
		cb.setLineWidth(.5f);
		// loop over the rows
		for (int line = 0; line < heights.length - 1; ++line) {
			widths = width[line];
			// loop over the columns
			for (int col = 0; col < widths.length - 1; ++col) {
				if (line == 0 && col == 0)
					cb.setAction(new PdfAction("http
						widths[col], heights[line + 1], widths[col + 1], heights[line]);
				cb.setRGBColorStrokeF((float)Math.random(), (float)Math.random(), (float)Math.random());
				// horizontal borderline
				cb.moveTo(widths[col], heights[line]);
				cb.lineTo(widths[col + 1], heights[line]);
				cb.stroke();
				// vertical borderline
				cb.setRGBColorStrokeF((float)Math.random(), (float)Math.random(), (float)Math.random());
				cb.moveTo(widths[col], heights[line]);
				cb.lineTo(widths[col], heights[line + 1]);
				cb.stroke();
			for (int col = 0; col < widths.length - 1; ++col) {
		for (int line = 0; line < heights.length - 1; ++line) {
			widths = width[line];
			// loop over the columns
			for (int col = 0; col < widths.length - 1; ++col) {
				if (line == 0 && col == 0)
					cb.setAction(new PdfAction("http
						widths[col], heights[line + 1], widths[col + 1], heights[line]);
				cb.setRGBColorStrokeF((float)Math.random(), (float)Math.random(), (float)Math.random());
				// horizontal borderline
				cb.moveTo(widths[col], heights[line]);
				cb.lineTo(widths[col + 1], heights[line]);
				cb.stroke();
				// vertical borderline
				cb.setRGBColorStrokeF((float)Math.random(), (float)Math.random(), (float)Math.random());
				cb.moveTo(widths[col], heights[line]);
				cb.lineTo(widths[col], heights[line + 1]);
				cb.stroke();
			}
		}
		cb.restoreState();
	}

    
	/**
	 * General example using table events (with colspan).
	 * @param args
	 * 		no arguments needed
	 */
	public static void main(String[] args) throws Exception {

		System.out.println("Table Events 2");
		// step1
		Document document = new Document(PageSize.A4, 50, 50, 50, 50);
		// step2
		PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("TableEvents2.pdf"));
		// step3
		document.open();
		// step4
		PdfPTable table = new PdfPTable(4);
		table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
		for (int k = 0; k < 24; ++k) {
			if (k != 0)
				table.addCell(String.valueOf(k));
			else {
				table.getDefaultCell().setColspan(3);
				table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_CENTER);
				table.addCell("This is a very big URL");
				table.getDefaultCell().setColspan(1);
				table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_LEFT);
				k += 2;
		for (int k = 0; k < 24; ++k) {
			if (k != 0)
				table.addCell(String.valueOf(k));
			else {
				table.getDefaultCell().setColspan(3);
				table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_CENTER);
				table.addCell("This is a very big URL");
				table.getDefaultCell().setColspan(1);
				table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_LEFT);
				k += 2;
			}
		}
		TableEvents2 event = new TableEvents2();
		table.setTableEvent(event);

		// add the table with document add
		document.add(table);
		// add the table at an absolute position
		table.setTotalWidth(300);
		table.writeSelectedRows(0, -1, 100, 600, writer.getDirectContent());
		document.newPage();
            
		table = new PdfPTable(4);
		float fontSize = 12;
		BaseFont bf = BaseFont.createFont("Helvetica", "winansi", false);
		table.getDefaultCell().setPaddingTop(bf.getFontDescriptor(BaseFont.ASCENT, fontSize) - fontSize + 2);
		table.getDefaultCell().setBorder(Rectangle.NO_BORDER);
		for (int k = 0; k < 500 * 4; ++k) {
			if (k == 0) {
				table.getDefaultCell().setColspan(4);
				table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_CENTER);
				table.addCell(new Phrase("This is an URL", new Font(bf, fontSize * 2)));
				table.getDefaultCell().setColspan(1);
				table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_LEFT);
				k += 3;
			}
			else
				table.addCell(new Phrase(String.valueOf(k), new Font(bf, fontSize)));
		for (int k = 0; k < 500 * 4; ++k) {
			if (k == 0) {
				table.getDefaultCell().setColspan(4);
				table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_CENTER);
				table.addCell(new Phrase("This is an URL", new Font(bf, fontSize * 2)));
				table.getDefaultCell().setColspan(1);
				table.getDefaultCell().setHorizontalAlignment(Element.ALIGN_LEFT);
				k += 3;
			}
			else
				table.addCell(new Phrase(String.valueOf(k), new Font(bf, fontSize)));
		}
		table.setTableEvent(event);
		table.setHeaderRows(3);
		document.add(table);
		// step5
		document.close();
	}

