    System.out.println("Chapter 5 example 13
    // creation of the document with a certain size and certain margins
    Document document = new Document(PageSize.A4.rotate(), 50, 50, 50, 50);
    
    try {
        // creation of the different writers
        PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("Chap0513.pdf"));
        
        // we add some meta information to the document
        document.addAuthor("Alan Soukup");
        document.addSubject("This is the result of a Test.");
        
        document.open();
        
        Table datatable = getTable();
        
        for (int i = 1; i < 30; i++) {
            
            datatable.setDefaultHorizontalAlignment(Element.ALIGN_LEFT);
            
            datatable.addCell("myUserId");
            datatable.addCell("Somebody with a very, very, very, very, very, very, very, very, very, very, very, very, very, very, very, very, very long long name");
            datatable.addCell("No Name Company");
            datatable.addCell("D" + i);
            
            datatable.setDefaultHorizontalAlignment(Element.ALIGN_CENTER);
            datatable.addCell("No");
            datatable.addCell("Yes");
            datatable.addCell("No");
            datatable.addCell("Yes");
            datatable.addCell("No");
            datatable.addCell("Yes");
            
            if (!writer.fitsPage(datatable)) {
                datatable.deleteLastRow();
                i--;
                document.add(datatable);
                document.newPage();
                datatable = getTable();
        for (int i = 1; i < 30; i++) {
            
            datatable.setDefaultHorizontalAlignment(Element.ALIGN_LEFT);
            
            datatable.addCell("myUserId");
            datatable.addCell("Somebody with a very, very, very, very, very, very, very, very, very, very, very, very, very, very, very, very, very long long name");
            datatable.addCell("No Name Company");
            datatable.addCell("D" + i);
            
            datatable.setDefaultHorizontalAlignment(Element.ALIGN_CENTER);
            datatable.addCell("No");
            datatable.addCell("Yes");
            datatable.addCell("No");
            datatable.addCell("Yes");
            datatable.addCell("No");
            datatable.addCell("Yes");
            
            if (!writer.fitsPage(datatable)) {
                datatable.deleteLastRow();
                i--;
                document.add(datatable);
                document.newPage();
                datatable = getTable();
            }
            
        }
        
        
        document.add(datatable);
    }
    catch(Exception e) {
        e.printStackTrace();
    }
    
    // we close the document
    document.close();
}


private static Table getTable() throws BadElementException, DocumentException {
    Table datatable = new Table(10);
    
    datatable.setPadding(4);
    datatable.setSpacing(0);
    //datatable.setBorder(Rectangle.NO_BORDER);
    int headerwidths[] = {10, 24, 12, 12, 7, 7, 7, 7, 7, 7};
    datatable.setWidths(headerwidths);
    datatable.setWidth(100);
    
    // the first cell spans 10 columns
    Cell cell = new Cell(new Phrase("Administration -System Users Report", FontFactory.getFont(FontFactory.HELVETICA, 24, Font.BOLD)));
    cell.setHorizontalAlignment(Element.ALIGN_CENTER);
    cell.setLeading(30);
    cell.setColspan(10);
    cell.setBorder(Rectangle.NO_BORDER);
    cell.setBackgroundColor(new Color(0xC0, 0xC0, 0xC0));
    datatable.addCell(cell);
    
    // These cells span 2 rows
    datatable.setDefaultCellBorderWidth(2);
    datatable.setDefaultHorizontalAlignment(1);
    datatable.setDefaultRowspan(2);
    datatable.addCell("User Id");
    datatable.addCell(new Phrase("Name", FontFactory.getFont(FontFactory.HELVETICA, 14, Font.BOLD)));
    datatable.addCell("Company");
    datatable.addCell("Department");
    
    // This cell spans the remaining 6 columns in 1 row
    datatable.setDefaultRowspan(1);
    datatable.setDefaultColspan(6);
    datatable.addCell("Permissions");
    
    // These cells span 1 row and 1 column
    datatable.setDefaultColspan(1);
    datatable.addCell("Admin");
    datatable.addCell("Data");
    datatable.addCell("Expl");
    datatable.addCell("Prod");
    datatable.addCell("Proj");
    datatable.addCell("Online");
    
    datatable.setDefaultCellBorderWidth(1);
    datatable.setDefaultRowspan(1);
    
    return datatable;
}

        System.out.println("Chapter 5 example 14
        // step 1
        Document document = new Document();
        try {
            // step 2
            // we create a writer that listens to the document
            // and directs a PDF-stream to a file
            PdfWriter.getInstance(document, new FileOutputStream("Chap0514.pdf"));
            // step 3
            document.open();
            // step 4
            
            // simple example
            
            Table secondTable = new Table(2);
            secondTable.addCell("2nd table 0.0");
            secondTable.addCell("2nd table 0.1");
            secondTable.addCell("2nd table 1.0");
            secondTable.addCell("2nd table 1.1");
            
            Table aTable = new Table(4,4);    // 4 rows, 4 columns
            aTable.setAutoFillEmptyCells(true);
            aTable.addCell("2.2", new Point(2,2));
            aTable.addCell("3.3", new Point(3,3));
            aTable.addCell("2.1", new Point(2,1));
            aTable.insertTable(secondTable, new Point(1,3));
            document.add(aTable);
            
            // example with 2 nested tables
            
            Table thirdTable = new Table(2);
            thirdTable.addCell("3rd table 0.0");
            thirdTable.addCell("3rd table 0.1");
            thirdTable.addCell("3rd table 1.0");
            thirdTable.addCell("3rd table 1.1");
            
            aTable = new Table(5,5);
            aTable.setAutoFillEmptyCells(true);
            aTable.addCell("2.2", new Point(2,2));
            aTable.addCell("3.3", new Point(3,3));
            aTable.addCell("2.1", new Point(2,1));
            aTable.insertTable(secondTable, new Point(1,3));
            aTable.insertTable(thirdTable, new Point(6,2));
            document.add(aTable);        
            
            // relative column widths are preserved
            
            Table a = new Table( 2 );
            a.setWidths( new float[] { 85, 15 } );
            a.addCell("a-1");
            a.addCell("a-2");
            
            Table b = new Table(5);
            b.setWidths( new float[] { 15, 7, 7, 7, 7 } );
            b.addCell("b-1");
            b.addCell("b-2");
            b.addCell("b-3");
            b.addCell("b-4");
            b.addCell("b-5");
            
            // now, insert these 2 tables into a third for layout purposes
            Table c = new Table( 3, 1 );
            c.setWidth( 100.0f );
            c.setWidths( new float[] { 20, 2, 78 } );
            c.insertTable(a, new Point(0,0) );
            c.insertTable(b, new Point(0,2) );

            document.add(c);
            
            // adding extra cells after adding a table
            
            Table t1 = new Table(3);
            t1.addCell("1.1");
            t1.addCell("1.2");
            t1.addCell("1.3");
            // nested
            Table t2 = new Table(2);
            t2.addCell("2.1");
            t2.addCell("2.2");
            
            // now insert the nested
            t1.insertTable(t2);
            t1.addCell("new cell");    // correct row/column ?
            document.add(t1);       
            
            // deep nesting
            
            t1=new Table(2,2); 
            for (int i = 0; i < 4; i++) {
                t1.addCell("t1");
            for (int i = 0; i < 4; i++) {
                t1.addCell("t1");
            }
            
            t2=new Table(3,3);
            for (int i = 0; i < 9; i++) {
                if (i == 4) t2.insertTable(t1);
                else t2.addCell("t2"); 
            for (int i = 0; i < 9; i++) {
                if (i == 4) t2.insertTable(t1);
                else t2.addCell("t2"); 
            }
            
            Table t3=new Table(4,4);
            for (int i = 0; i < 16; i++) {
                if (i == 10) t3.insertTable(t2);
                else t3.addCell("t3"); 
            for (int i = 0; i < 16; i++) {
                if (i == 10) t3.insertTable(t2);
                else t3.addCell("t3"); 
            }

            document.add(t3); 
        }
        catch(DocumentException de) {
            System.out.println(de.getMessage());
        }
        catch(IOException ioe) {
            System.out.println(ioe.getMessage());
        }
        // step 5
        document.close();
    }

