		System.out.println("Chapter 6 example 12

        // creation of the document with a certain size and certain margins
        Document document = new Document(PageSize.A4, 50, 50, 50, 50);
        //Document.compress = false;
        try {
            // creation of the different writers
            PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("Chap0612.pdf"));

            File file = new File("12.tif");
            SeekableStream s = new FileSeekableStream(file);
            TIFFDirectory dir = new TIFFDirectory(s, 0);
            long IFDOffset = dir.getIFDOffset();
            document.open();
            PdfContentByte cb = writer.getDirectContent();
            while (IFDOffset != 0L) {
                dir = new TIFFDirectory(s, IFDOffset, 0);
                IFDOffset = dir.getNextIFDOffset();
                long h = dir.getFieldAsLong(TIFFImageDecoder.TIFF_IMAGE_LENGTH);
                long w = dir.getFieldAsLong(TIFFImageDecoder.TIFF_IMAGE_WIDTH);
                long offset = dir.getFieldAsLong(TIFFImageDecoder.TIFF_STRIP_OFFSETS);
                long size = dir.getFieldAsLong(TIFFImageDecoder.TIFF_STRIP_BYTE_COUNTS);
                boolean reverse = false;
                if (dir.isTagPresent(TIFFImageDecoder.TIFF_FILL_ORDER))
                    reverse = (dir.getFieldAsLong(TIFFImageDecoder.TIFF_FILL_ORDER) == 2L);
                int compression = (int)dir.getFieldAsLong(TIFFImageDecoder.TIFF_COMPRESSION);
                switch (compression) {
                    case TIFFImage.COMP_FAX_G3_1D
                        compression = Image.CCITTG3_1D;
                        break;
                    case TIFFImage.COMP_FAX_G3_2D
                        compression = Image.CCITTG3_1D;
                        if (dir.isTagPresent(TIFFImageDecoder.TIFF_T4_OPTIONS)) {
                            if (((int)dir.getFieldAsLong(TIFFImageDecoder.TIFF_T4_OPTIONS) & 1) != 0)
                                compression = Image.CCITTG3_2D;
                        }
                        break;
                    case TIFFImage.COMP_FAX_G4_2D
                        compression = Image.CCITTG4;
                        break;
                    default
                        throw new Exception("Compression type " + compression + " not supported");
                }
                byte im[] = new byte[(int)size];
                s.seek(offset);
                s.readFully(im);
                Image img = Image.getInstance((int)w, (int)h, reverse, compression, 0, im);
                img.scalePercent(72f / 200f * 100);
                img.setAbsolutePosition(0, 0);
                cb.addImage(img);
                document.newPage();
            while (IFDOffset != 0L) {
                dir = new TIFFDirectory(s, IFDOffset, 0);
                IFDOffset = dir.getNextIFDOffset();
                long h = dir.getFieldAsLong(TIFFImageDecoder.TIFF_IMAGE_LENGTH);
                long w = dir.getFieldAsLong(TIFFImageDecoder.TIFF_IMAGE_WIDTH);
                long offset = dir.getFieldAsLong(TIFFImageDecoder.TIFF_STRIP_OFFSETS);
                long size = dir.getFieldAsLong(TIFFImageDecoder.TIFF_STRIP_BYTE_COUNTS);
                boolean reverse = false;
                if (dir.isTagPresent(TIFFImageDecoder.TIFF_FILL_ORDER))
                    reverse = (dir.getFieldAsLong(TIFFImageDecoder.TIFF_FILL_ORDER) == 2L);
                int compression = (int)dir.getFieldAsLong(TIFFImageDecoder.TIFF_COMPRESSION);
                switch (compression) {
                    case TIFFImage.COMP_FAX_G3_1D
                        compression = Image.CCITTG3_1D;
                        break;
                    case TIFFImage.COMP_FAX_G3_2D
                        compression = Image.CCITTG3_1D;
                        if (dir.isTagPresent(TIFFImageDecoder.TIFF_T4_OPTIONS)) {
                            if (((int)dir.getFieldAsLong(TIFFImageDecoder.TIFF_T4_OPTIONS) & 1) != 0)
                                compression = Image.CCITTG3_2D;
                        }
                        break;
                    case TIFFImage.COMP_FAX_G4_2D
                        compression = Image.CCITTG4;
                        break;
                    default
                        throw new Exception("Compression type " + compression + " not supported");
                }
                byte im[] = new byte[(int)size];
                s.seek(offset);
                s.readFully(im);
                Image img = Image.getInstance((int)w, (int)h, reverse, compression, 0, im);
                img.scalePercent(72f / 200f * 100);
                img.setAbsolutePosition(0, 0);
                cb.addImage(img);
                document.newPage();
            }
            document.close();
        }
        catch (Exception de) {
            de.printStackTrace();
            //System.out.println(de.getMessage());
        }
    }

    System.out.println("Chapter 6 example 13
    
    Document document = new Document(PageSize.A4, 50, 50, 50, 50);
    try {
        PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("Chap0613.pdf"));
        
        document.open();
        Paragraph p = new Paragraph("Some text behind a masked image.");
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        document.add(p);
        PdfContentByte cb = writer.getDirectContent();
        byte maskr[] = {(byte)0x3c, (byte)0x7e, (byte)0xe7, (byte)0xc3, (byte)0xc3, (byte)0xe7, (byte)0x7e, (byte)0x3c};
        Image mask = Image.getInstance(8, 8, 1, 1, maskr);
        mask.makeMask();
        mask.setInvertMask(true);
        Image image = Image.getInstance("vonnegut.gif");
        image.setImageMask(mask);
        image.setAbsolutePosition(60, 620);
        // explicit masking
        cb.addImage(image);
        // stencil masking
        cb.setRGBColorFill(255, 0, 0);
        cb.addImage(mask, mask.scaledWidth() * 8, 0, 0, mask.scaledHeight() * 8, 100, 400);
