                        subsection.add(table);
                    }
                    subsection.add(blahblah);
                }
                if (j == 2 && i > 2) {
                    section.add(blahblahblah);
                    section.add(table);
                }
            }
            document.add(chapter);
        for (int i = 1; i < 8; i++) {
