    System.out.println("Chapter 10 example 1
    
    // step 1
    Document document = new Document();
    
    try {
        
        // step 2
        // we create a writer that listens to the document
        // and directs a PDF-stream to a file
        PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("Chap1001.pdf"));
        
        // step 3
        document.open();
        
        // step 4
        PdfContentByte cb = writer.getDirectContent();
        
        // an example of a rectangle with a diagonal in very thick lines
        cb.setLineWidth(10f);
        // draw a rectangle
        cb.rectangle(100, 700, 100, 100);
        // add the diagonal
        cb.moveTo(100, 700);
        cb.lineTo(200, 800);
        // stroke the lines
        cb.stroke();
        
        // an example of some circles
        cb.setLineDash(3, 3, 0);
        cb.setRGBColorStrokeF(0f, 255f, 0f);
        cb.circle(150f, 500f, 100f);
        cb.stroke();
        
        cb.setLineWidth(5f);
        cb.resetRGBColorStroke();
        cb.circle(150f, 500f, 50f);
        cb.stroke();
        
        // example with colorfill
        cb.setRGBColorFillF(0f, 255f, 0f);
        cb.moveTo(100f, 200f);
        cb.lineTo(200f, 250f);
        cb.lineTo(400f, 150f);
        // because we change the fill color BEFORE we stroke the triangle
        // the color of the triangle will be red instead of green
        cb.setRGBColorFillF(255f, 0f, 0f);
        cb.closePathFillStroke();
    }
    catch(DocumentException de) {
        System.out.println(de.getMessage());
    }
    catch(IOException ioe) {
        System.out.println(ioe.getMessage());
    }
    
    // step 5
    document.close();
}

    System.out.println("Chapter 10 example 2
    
    // step 1
    Document document = new Document();
    
    try {
        
        // step 2
        // we create a writer that listens to the document
        // and directs a PDF-stream to a file
        PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("Chap1002.pdf"));
        
        // step 3
        document.open();
        
        // step 4
        PdfContentByte cb = writer.getDirectContent();
        
        // first we draw some lines to be able to visualize the text alignment functions
        cb.setLineWidth(0f);
        cb.moveTo(250, 500);
        cb.lineTo(250, 800);
        cb.moveTo(50, 700);
        cb.lineTo(400, 700);
        cb.moveTo(50, 650);
        cb.lineTo(400, 650);
        cb.moveTo(50, 600);
        cb.lineTo(400, 600);
        cb.stroke();
        
        // we tell the ContentByte we're ready to draw text
        cb.beginText();
        
        BaseFont bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
        cb.setFontAndSize(bf, 12);
        String text = "Sample text for alignment";
        // we show some text starting on some absolute position with a given alignment
        cb.showTextAligned(PdfContentByte.ALIGN_CENTER, text + " Center", 250, 700, 0);
        cb.showTextAligned(PdfContentByte.ALIGN_RIGHT, text + " Right", 250, 650, 0);
        cb.showTextAligned(PdfContentByte.ALIGN_LEFT, text + " Left", 250, 600, 0);
        
        // we draw some text on a certain position
        cb.setTextMatrix(100, 400);
        cb.showText("Text at position 100,400.");
        
        // we draw some rotated text on a certain position
        cb.setTextMatrix(0, 1, -1, 0, 100, 300);
        cb.showText("Text at position 100,300, rotated 90 degrees.");
        
        // we draw some mirrored, rotated text on a certain position
        cb.setTextMatrix(0, 1, 1, 0, 200, 200);
        cb.showText("Text at position 200,200, mirrored and rotated 90 degrees.");
        
        // we tell the contentByte, we've finished drawing text
        cb.endText();
    }
    catch(DocumentException de) {
        System.out.println(de.getMessage());
    }
    catch(IOException ioe) {
        System.out.println(ioe.getMessage());
    }
    
    // step 5
    document.close();
}

    System.out.println("Chapter 10 example 3
    
    // step 1
    Document document = new Document();
    
    try {
        
        // step 2
        // we create a writer that listens to the document
        // and directs a PDF-stream to a file
        PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("Chap1003.pdf"));
        
        // step 3
        document.open();
        
        // step 4
        PdfContentByte cb = writer.getDirectContent();
        
        // we create a PdfTemplate
        PdfTemplate template = cb.createTemplate(500, 200);
        
        // we add some graphics
        template.moveTo(0, 200);
        template.lineTo(500, 0);
        template.stroke();
        template.setRGBColorStrokeF(255f, 0f, 0f);
        template.circle(250f, 100f, 80f);
        template.stroke();
        
        // we add some text
        template.beginText();
        BaseFont bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
        template.setFontAndSize(bf, 12);
        template.setTextMatrix(100, 100);
        template.showText("Text at the position 100,100 (relative to the template!)");
        template.endText();
        
        // we add the template on different positions
        cb.addTemplate(template, 0, 0);
        cb.addTemplate(template, 0, 1, -1, 0, 500, 200);
        cb.addTemplate(template, .5f, 0, 0, .5f, 100, 400);
        
        // we go to a new page
        document.newPage();
        cb.addTemplate(template, 0, 400);
        cb.addTemplate(template, 2, 0, 0, 2, -200, 400);
    }
    catch(DocumentException de) {
        System.out.println(de.getMessage());
    }
    catch(IOException ioe) {
        System.out.println(ioe.getMessage());
    }
    
    // step 5
    document.close();
}

    System.out.println("Chapter 10 example 4
    
    // step 1
    Document document = new Document();
    
    try {
        
        // step 2
        // we create a writer that listens to the document
        // and directs a PDF-stream to a file
        PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("Chap1004.pdf"));
        
        // step 3
        document.open();
        
        // step 4
        PdfContentByte cb = writer.getDirectContent();
        
        // we create a PdfTemplate
        PdfTemplate template = cb.createTemplate(50, 50);
        BaseFont bf = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
        // we add a number of pages
        int i;
        for (i = 1; i < 5; i++) {
            String text = "Page " + writer.getPageNumber() + " of ";
            float len = bf.getWidthPoint(text, 12);
            cb.beginText();
            cb.setFontAndSize(bf, 12);
            cb.setTextMatrix(280, 40);
            cb.showText(text);
            cb.endText();
            cb.addTemplate(template, 280 + len, 40);
            document.newPage();
        for (i = 1; i < 5; i++) {
            String text = "Page " + writer.getPageNumber() + " of ";
            float len = bf.getWidthPoint(text, 12);
            cb.beginText();
            cb.setFontAndSize(bf, 12);
            cb.setTextMatrix(280, 40);
            cb.showText(text);
            cb.endText();
            cb.addTemplate(template, 280 + len, 40);
            document.newPage();
        }
        template.beginText();
        template.setFontAndSize(bf, 12);
        template.showText(String.valueOf(writer.getPageNumber() - 1));
        template.endText();
    }
    catch(DocumentException de) {
        System.out.println(de.getMessage());
    }
    catch(IOException ioe) {
        System.out.println(ioe.getMessage());
    }
    
    // step 5
    document.close();
}

    System.out.println("Chapter 10 example 5
    
    // step 1
    Document document = new Document();
    
    try {
        
        // step 2
        // we create a writer that listens to the document
        // and directs a PDF-stream to a file
        PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("Chap1005.pdf"));
        
        // step 3
        document.open();
        
        // step 4
        
        // we create some content
        BaseFont bf = BaseFont.createFont(BaseFont.COURIER, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
        Font font = new Font(bf, 11, Font.NORMAL);
        
        Phrase unicodes = new Phrase(15, "UNI\n", font);
        Phrase characters = new Phrase(15, "\n", font);
        Phrase names = new Phrase(15, "NAME\n", font);
        
        for (int i = 0; i < 27; i++) {
            unicodes.add(uni[i] + "\n");
            characters.add(code[i] + "\n");
            names.add(name[i] + "\n");
        for (int i = 0; i < 27; i++) {
            unicodes.add(uni[i] + "\n");
            characters.add(code[i] + "\n");
            names.add(name[i] + "\n");
        }
        
        // we grab the ContentByte and do some stuff with it
        PdfContentByte cb = writer.getDirectContent();
        
        ColumnText ct = new ColumnText(cb);
        ct.setSimpleColumn(unicodes, 60, 300, 100, 300 + 28 * 15, 15, Element.ALIGN_CENTER);
        ct.go();
        cb.rectangle(103, 295, 52, 8 + 28 * 15);
        cb.stroke();
        ct.setSimpleColumn(characters, 105, 300, 150, 300 + 28 * 15, 15, Element.ALIGN_RIGHT);
        ct.go();
        ct.setSimpleColumn(names, 160, 300, 500, 300 + 28 * 15, 15, Element.ALIGN_LEFT);
        ct.go();
        
    }
    catch(DocumentException de) {
        System.out.println(de.getMessage());
    }
    catch(IOException ioe) {
        System.out.println(ioe.getMessage());
    }
    
    // step 5
    document.close();
}


public static void main(String[] args) throws Exception {
    
    System.out.println("Chapter 10 example 5
    
    // step 1
    Document document = new Document();
    
    try {
        
        // step 2
        // we create a writer that listens to the document
        // and directs a PDF-stream to a file
        PdfWriter writer = PdfWriter.getInstance(document, new FileOutputStream("Chap1005.pdf"));
        
        // step 3
        document.open();
        
        // step 4
        
        // we create some content
        BaseFont bf = BaseFont.createFont(BaseFont.COURIER, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
        Font font = new Font(bf, 11, Font.NORMAL);
        
        Phrase unicodes = new Phrase(15, "UNI\n", font);
        Phrase characters = new Phrase(15, "\n", font);
        Phrase names = new Phrase(15, "NAME\n", font);
        
        for (int i = 0; i < 27; i++) {
            unicodes.add(uni[i] + "\n");
            characters.add(code[i] + "\n");
            names.add(name[i] + "\n");
        }
        
        // we grab the ContentByte and do some stuff with it
        PdfContentByte cb = writer.getDirectContent();
        
        ColumnText ct = new ColumnText(cb);
        ct.setSimpleColumn(unicodes, 60, 300, 100, 300 + 28 * 15, 15, Element.ALIGN_CENTER);
        ct.go();
        cb.rectangle(103, 295, 52, 8 + 28 * 15);
        cb.stroke();
        ct.setSimpleColumn(characters, 105, 300, 150, 300 + 28 * 15, 15, Element.ALIGN_RIGHT);
        ct.go();
        ct.setSimpleColumn(names, 160, 300, 500, 300 + 28 * 15, 15, Element.ALIGN_LEFT);
        ct.go();
        
    }
    catch(DocumentException de) {
        System.out.println(de.getMessage());
    }
    catch(IOException ioe) {
        System.out.println(ioe.getMessage());
    }
    
    // step 5
    document.close();
}

