	static void stampSignature(String filename, String signedFilename, 
		String keyFilename, String password, 
		int signType, String fieldName, boolean append, 
		Rectangle rect, int page,
		String reason, String location) throws Exception 
	{
		KeyStore ks = KeyStore.getInstance(KeyStore.getDefaultType());
		ks.load(new java.io.FileInputStream (keyFilename), password.toCharArray());
		String alias = (String)ks.aliases().nextElement();
		PrivateKey key = (PrivateKey)ks.getKey(alias, password.toCharArray());
		jp.ujihara.java.security.cert.Certificate[] chain = ks.getCertificateChain(alias);
		PdfReader reader = new PdfReader(filename);
		FileOutputStream fout = new FileOutputStream(signedFilename);
		PdfStamper stp = PdfStamper.createSignature(reader, fout, '\0');
		PdfSignatureAppearance sap = stp.getSignatureAppearance();
		sap.setCrypto(key, chain, null, PdfSignatureAppearance.SELF_SIGNED);
		sap.setReason("I'm the author");
		sap.setLocation("Lisbon");
		// comment next line to have an invisible signature
		sap.setVisibleSignature(new Rectangle(100, 100, 200, 200), 1, null);
		stp.close();
	}

    Document doc = new Document();
    PdfWriter writer =PdfWriter.getInstance(doc, new FileOutputStream("SignatureField.pdf"));
    PdfAcroForm acroForm = writer.getAcroForm();
    doc.open();
    doc.add(new com.lowagie.text.Paragraph("This document contains a field to sign."));
    acroForm.addSignature("Signature", 73, 705, 149, 759);
    doc.close();
}

