    stampSignature("HelloWorld.pdf", "InvisibleSigned.pdf", "self-sign-test.pfx", "password", SIGNTYPE_INVISIBLE, null, false, null, 0, "Signed by invisible signature.", "Takarazuka");
    stampSignature("SignatureField.pdf", "SignedOnField.pdf", "self-sign-test.pfx", "password", SIGNTYPE_FIELD, "Signature", false, null, 0, "Signed on a field.", "Japan");
    stampSignature("SignedOnField.pdf", "MultiSigned.pdf", "self-sign-test2.pfx", "password2", SIGNTYPE_VISIBLE, null, true, new Rectangle(300, 100, 400, 200), 1, "Multi-Signed by visible signature.", "Tokyo");
}


    static void stampSignature(String filename, String signedFilename, 
        String keyFilename, String password, 
        int signType, String fieldName, boolean append, 
        Rectangle rect, int page,
        String reason, String location) throws Exception {
        KeyStore ks = KeyStore.getInstance("pkcs12");
		ks.load(new FileInputStream(keyFilename), password.toCharArray());
        String alias = (String)ks.aliases().nextElement();
        PrivateKey key = (PrivateKey)ks.getKey(alias, password.toCharArray());
        Certificate[] chain = ks.getCertificateChain(alias);

        PdfReader reader = new PdfReader(filename);
        FileOutputStream fout = new FileOutputStream(signedFilename);
        PdfStamper stp = PdfStamper.createSignature(reader, fout, '\0', null, append);
        
        PdfSignatureAppearance sap = stp.getSignatureAppearance();
        sap.setCrypto(key, chain, null, PdfSignatureAppearance.SELF_SIGNED);
        sap.setReason(reason);
        sap.setLocation(location);

        switch (signType) {
            case SIGNTYPE_VISIBLE
                sap.setVisibleSignature(rect, page, fieldName);
                break;
            case SIGNTYPE_FIELD
                sap.setVisibleSignature(fieldName);
                break;
            default
                break;
        }

        stp.close();
    }


	private static void deleteNonExecutables(String dir) throws IOException {
		File[] files = new File(dir).listFiles();
		for (int i = 0; i < files.length; i++) 
		{
			File file = files[i];
			if (file.isFile()) 
			{
				String name = file.getName();
				String ext = name.substring(name.lastIndexOf('.')); //get extension
				if (!exeExts.contains(ext)) 
				{
					file.delete();
		for (int i = 0; i < files.length; i++) 
		{
			File file = files[i];
			if (file.isFile()) 
			{
				String name = file.getName();
				String ext = name.substring(name.lastIndexOf('.')); //get extension
				if (!exeExts.contains(ext)) 
				{
					file.delete();
				}
			}
		}
	}


	private static void copyFiles(File[] files, File destDir) throws IOException {
		for (int i = 0; i < files.length; i++) 
		{
			String dstName = destDir.getCanonicalPath() + File.separator + files[i].getName();
			InputStream is = new FileInputStream(files[i]);
			OutputStream os = new FileOutputStream(dstName);
			byte[] buffer = new byte[0x8000];
			int readBytes;
			while ((readBytes = is.read(buffer)) >= 0)
			{
				os.write(buffer, 0, readBytes);
			while ((readBytes = is.read(buffer)) >= 0)
			{
				os.write(buffer, 0, readBytes);
			}
			os.close();
			is.close();
		for (int i = 0; i < files.length; i++) 
		{
			String dstName = destDir.getCanonicalPath() + File.separator + files[i].getName();
			InputStream is = new FileInputStream(files[i]);
			OutputStream os = new FileOutputStream(dstName);
			byte[] buffer = new byte[0x8000];
			int readBytes;
			while ((readBytes = is.read(buffer)) >= 0)
			{
				os.write(buffer, 0, readBytes);
			}
			os.close();
			is.close();
		}
	}


	private static void init() throws IOException {
		File fileInputs = new File(Configuration.inputFilesDirectory);

		if (!fileInputs.exists()) {
			try {
				// If input directory doesn't exist in a current directory,
				// it may be executed from a project.

				File fileInputs2 = new File(fileInputs.getParentFile().getParentFile().getParentFile().getParentFile().getCanonicalPath() + File.separator + Configuration.inputFilesDirectory);
				if (!fileInputs2.exists())
					throw new IOException(fileInputs.toString() + " or " + fileInputs2.toString() + " doesn't exist.");
				fileInputs.mkdir();
				copyFiles(fileInputs2.listFiles(), fileInputs);
			}
			catch (IOException t) {
				try
				{
					fileInputs.delete();
				}
				catch (Exception tt)
				{
				}
				throw t;
			}
		}

		deleteNonExecutables(".");
		copyFiles(fileInputs.listFiles(), new File("."));
	}


