	private static void showUsage() {
		System.out.println("Usage
	}


	public static void main(String[] args) throws Exception {
		try 
		{
			ArrayList exeClasses = new ArrayList();

			for (int index = 0; index < args.length; ) 
			{
				String arg = args[index++];

				if (arg.equals("-?")) 
				{
					showUsage();
					System.exit(0);
				}
				else if (arg.equals("-benchmark")) 
				{
					Configuration.isBenchmark = true;
				}
				else if (arg.equals("-exec-old"))
				{
					Configuration.execOldExamples = true;
				}
				else 
				{
					exeClasses.add(arg);
			for (int index = 0; index < args.length; ) 
			{
				String arg = args[index++];

				if (arg.equals("-?")) 
				{
					showUsage();
					System.exit(0);
				}
				else if (arg.equals("-benchmark")) 
				{
					Configuration.isBenchmark = true;
				}
				else if (arg.equals("-exec-old"))
				{
					Configuration.execOldExamples = true;
				}
				else 
				{
					exeClasses.add(arg);
				}
			}
			if (exeClasses.size() == 0)
				exeClasses.add("jp.ujihara.itext.test.AllTests");

			for (Iterator it = exeClasses.iterator(); it.hasNext(); )
			{
				String className = (String)it.next();
				Class clazz = Class.forName(className);
				Method m = clazz.getMethod("main", new Class[] {String[].class});
				m.invoke(null, new Object[] { new String[] {} });
			for (Iterator it = exeClasses.iterator(); it.hasNext(); )
			{
				String className = (String)it.next();
				Class clazz = Class.forName(className);
				Method m = clazz.getMethod("main", new Class[] {String[].class});
				m.invoke(null, new Object[] { new String[] {} });
			}

			if (Configuration.execOldExamples)
			{
				OldExampleTest.main(new String[] {});
			}
		}
		catch (Exception e)
		{
			String mes;

			if (e instanceof InvocationTargetException)
			{
				InvocationTargetException ite = (InvocationTargetException)e;
				Throwable t = ite.getTargetException();
				mes = t.getMessage();
				if (mes != null)
					System.err.println(mes);
				t.printStackTrace(System.err);
			}
			
			mes = e.getMessage();
			if (mes != null)
				System.err.println(mes);
			e.printStackTrace(System.err);
			showUsage();
			System.exit(1);
		}

		System.exit(0);
	}


