    protected void setUp() throws Exception {
        if (!new File("HelloWorld.pdf").exists())
            HelloWorld.main(null);

        // Loading another securuty provider is required because GNU classpath doesn't support PKCS#12.
        // The next line can be commented out if 'classpath.security' file is stored in \Windows\Java\lib\security.
        // See Gnu.Classpath.Configure project.
        jp.ujihara.java.security.Security.addProvider(new org.bouncycastle.jce.provider.BouncyCastleProvider());
    }


