	short s = v;
	if (v < 0) {
		s += 256;
	}
	return s;
}

public static int ToUnsigned(short v)
{
	int s = v;
	if (v < 0) {
		s += 65536;
	}
	return s;
}

public static long ToUnsigned(int v)
{
	int s = v;
	if (v < 0) {
		s += 4294967296l;
	}
	return s;
}

public FileSeekableStream(File file)  throws IOException
{
	super(file);
}

public FileSeekableStream(String name)   throws IOException
{
	super(name);
}

		if (dir.isTagPresent(tag)) return dir.getFieldAsLong(tag);

		//returns default value.
		switch (tag) 
		{
			case TIFFImageDecoder.TIFF_S_MIN_SAMPLE_VALUE
				//case TIFFImageDecoder.NewSubfileType
				//Orientation
				return 0;
				//case TIFFImageDecoder.TIFF_S_MAX_SAMPLE_VALUE
				//	return 2**(BitsPerSample) - 1.;
			case TIFFImageDecoder.TIFF_BITS_PER_SAMPLE
			case TIFFImageDecoder.TIFF_SAMPLES_PER_PIXEL
			case TIFFImageDecoder.TIFF_COMPRESSION
			case TIFFImageDecoder.TIFF_FILL_ORDER
				//PlanarConfiguration
				//Threshholding
				return 1;
			case TIFFImageDecoder.TIFF_RESOLUTION_UNIT
				return 2;
				//RowsPerStrip	2**32 - 1,
			default
				break;
				
		}
		throw new IOException("PropReader 
	}

