	public StreamReader(RandomAccessFile stream, boolean isBigEndian) 
	boolean isBigEndian = false;
	
	public int read(byte[] bytes) throws IOException
	{
		return stream.read(bytes);
	}
	
	public char readChar() throws IOException
	{
		return stream.readChar();
	}

	
	public void seek(long position) throws IOException
	{
		stream.seek(position);
	}
	
	public int skipBytes(int length) throws IOException
	{
		return stream.skipBytes(length);
	}
	
	public long getFilePointer() throws IOException
	{
		return stream.getFilePointer();
	}
	
	public StreamReader(RandomAccessFile stream, boolean isBigEndian) 
	{
		this.stream = stream;
		this.isBigEndian = isBigEndian;
	}



public byte readByte() throws IOException
{
	return stream.readByte();
}


public short readUnsignedByte() throws IOException
{
	short b = this.readByte();
	if (b < 0) b += 0x100;
	return b;
}


public short readShort() throws IOException 
{
	if (isBigEndian) {
		return stream.readShort();
	}
	short lo = readUnsignedByte();
	short hi = stream.readByte();
	return (short)(lo + hi * 0x100);
}


public int readUnsignedShort() throws IOException
{
	int s = readShort();
	if (s < 0) s += 0x10000;
	return s;
}


public int readInt() throws IOException 
{ 
	if (isBigEndian) {
		return stream.readInt();
	}
	int lo = this.readUnsignedShort();
	int hi = this.readShort();
	return (int)(lo + hi * 0x10000);
}


	public long readUnsignedInt() throws IOException
	{
		long s = readInt();
		if (s < 0) s += 0x100000000L;
		return s;
	}


public float readFloat() throws IOException 
{
	return stream.readFloat();
}

public double readDouble() throws IOException 
{ 
	return stream.readDouble();
}


public int[] readInts(int length)  throws IOException 
{
	int[] a = new int[length];
	for (int i = 0; i < length; i++)
	{
		a[i] = readInt();
	for (int i = 0; i < length; i++)
	{
		a[i] = readInt();
	}
	return a;
}


	public long[] readUnsignedInts(int length)  throws IOException 
	{
		long[] a = new long[length];
		for (int i = 0; i < length; i++)
		{
			a[i] = readUnsignedInt();
		for (int i = 0; i < length; i++)
		{
			a[i] = readUnsignedInt();
		}
		return a;
	}

public TIFFDirectory(RandomAccessFile stream, int directory) throws IOException
{
	this(stream, 0, directory);
}

	
	public TIFFDirectory(RandomAccessFile stream, long ifd_offset, int directory) throws IOException
	long IFDOffset = 0;
	int numEntries = 0;
	long nextIFDOffset = 0;

	//the following must clear when IFDOffset is changed.
	TIFFField[] fields = null;
	//Hashtable fieldIndex;
	//private static final int[] sizeOfType;
  
	// ?R???X?g???N?^
	public TIFFDirectory(RandomAccessFile stream, int directory) throws IOException
	{
		this(stream, 0, directory);
	}
	
	public TIFFDirectory(RandomAccessFile stream, long ifd_offset, int directory) throws IOException
	{
		//ifd_offset = 0 ??A?o?????T????????????B
		stream.seek(0);
		
		byte tag0 = stream.readByte();
		byte tag1 = stream.readByte();
		if (tag0 == 'I' && tag1 == 'I') 
		{
			isBigEndian = false;
		} 
		else if (tag0 == 'M' && tag1 == 'M') 
		{
			isBigEndian = true;
		} 
		else 
		{
			throw new IOException("Invalid endian specification.");
		}
		
		reader = new StreamReader(stream, isBigEndian);
		
		short magicNumber = reader.readShort();
		if (magicNumber != 42) throw new IOException("Invalid TIFF magic number.");

		if (ifd_offset == 0) 
		{
			IFDOffset = reader.readInt();
		} 
		else 
		{
			IFDOffset = ifd_offset;
		}
		
		skip(directory);
	}


private static int DIRECTORY_LAST = -2;

//???????Adirectory????????B
private int skip(int directory) throws IOException
{
	boolean countDirsMode = false;
	if (directory == DIRECTORY_LAST) 
	{
		countDirsMode = true;
		directory = Integer.MAX_VALUE;
	}
	
	nextIFDOffset = IFDOffset;
	int i;
	for (i = 0; i <= directory; i++)
	{
		IFDOffset = nextIFDOffset;
		if (nextIFDOffset == 0) 
		{
			if (countDirsMode) 
			{
				return i;
			} 
			else 
			{
				throw new IOException("TIFF directory overrun.");
			}
		}
		nextIFDOffset = readNextIFDOffset();
	for (i = 0; i <= directory; i++)
	{
		IFDOffset = nextIFDOffset;
		if (nextIFDOffset == 0) 
		{
			if (countDirsMode) 
			{
				return i;
			} 
			else 
			{
				throw new IOException("TIFF directory overrun.");
			}
		}
		nextIFDOffset = readNextIFDOffset();
	}
	return i;
}


public boolean isBigEndian() 
{
	return isBigEndian;
}


public int getNumEntries() 
{
	return numEntries;
}


public long getIFDOffset() 
{
	return IFDOffset;
}


private long readNextIFDOffset() throws IOException
{
	fields = null;
	
	reader.seek(IFDOffset);
	numEntries = reader.readShort();
	reader.skipBytes(numEntries * TIFFField.size);
	return reader.readInt();
}


public long getNextIFDOffset() throws IOException
{
	return nextIFDOffset;
}


public static int getNumDirectories(RandomAccessFile seekableStream) throws IOException 
{ 
	TIFFDirectory dir= new TIFFDirectory(seekableStream, 0);
	return dir.skip(DIRECTORY_LAST);
}


	public TIFFField getField(int tag) throws IOException
	{
		TIFFField[] fields = getFields();
		
		for (int i = 0; i < fields.length; i++) 
		{
			if (fields[i].tag == tag) 
			{
				return fields[i];
		for (int i = 0; i < fields.length; i++) 
		{
			if (fields[i].tag == tag) 
			{
				return fields[i];
			}
		}
		return null;
	}

public TIFFField[] getFields()  throws IOException 
{
	if (fields != null) return fields;
	
	fields = new TIFFField[numEntries];
	
	for (int i = 0; i < numEntries; i++) 
	{
		reader.seek(IFDOffset + 2 + TIFFField.size * i);
		int tag = reader.readShort();
		int type = reader.readShort();
		int count = reader.readInt();
		
		int sizeOfValue = TIFFField.sizeOfType(type) * count;
		long offset = sizeOfValue > 4 ? reader.readInt() 
		reader.seek(offset);
		
		TIFFValueProxy data = new TIFFValueProxy(reader, reader.getFilePointer(), sizeOfValue, isBigEndian);
		
		fields[i] = new TIFFField(tag, type, count, data);
	for (int i = 0; i < numEntries; i++) 
	{
		reader.seek(IFDOffset + 2 + TIFFField.size * i);
		int tag = reader.readShort();
		int type = reader.readShort();
		int count = reader.readInt();
		
		int sizeOfValue = TIFFField.sizeOfType(type) * count;
		long offset = sizeOfValue > 4 ? reader.readInt() 
		reader.seek(offset);
		
		TIFFValueProxy data = new TIFFValueProxy(reader, reader.getFilePointer(), sizeOfValue, isBigEndian);
		
		fields[i] = new TIFFField(tag, type, count, data);
	}
	return fields;
}


public boolean isTagPresent(int tag) throws IOException
{
	return getField(tag) != null;
}


public int[] getTags()  throws IOException
{
	TIFFField[] fields = getFields();
	int[] tags = new int[numEntries];
	for (int i = 0; i < numEntries; i++)
	{
		tags[i] = fields[i].getTag();
	for (int i = 0; i < numEntries; i++)
	{
		tags[i] = fields[i].getTag();
	}
	return tags;
}


