	switch (type)
	{
	case TIFF_BYTE
	case TIFF_ASCII
	case TIFF_SBYTE
		return 1;
	case TIFF_SHORT
	case TIFF_SSHORT
		return 2;
	case TIFF_LONG
	case TIFF_SLONG
	case TIFF_FLOAT
		return 4;
	case TIFF_RATIONAL
	case TIFF_SRATIONAL
	case TIFF_DOUBLE
		return 8;
	case TIFF_UNDEFINED
		return 1;
	default
		throw new IllegalArgumentException();
	}
}


// ?t?B?[???h
public static final int TIFF_BYTE = 1;
public static final int TIFF_ASCII = 2;
public static final int TIFF_SHORT = 3;
public static final int TIFF_LONG = 4;
public static final int TIFF_RATIONAL = 5;
public static final int TIFF_SBYTE = 6;
public static final int TIFF_UNDEFINED = 7;
public static final int TIFF_SSHORT = 8;
public static final int TIFF_SLONG = 9;
public static final int TIFF_SRATIONAL = 10;
public static final int TIFF_FLOAT = 11;
public static final int TIFF_DOUBLE = 12;

int tag;
int type;
int count;
TIFFValueProxy data;

public TIFFField(int tag, int type, int count, TIFFValueProxy data)
{
	this.tag = tag;
	this.type = type;
	this.count = count;
	this.data = data;
}


	public int getTag() 
	{ 
		return tag;
	}

public int getType()
{ 
	return type;
}

public int getCount() 
{ 
	return count;
}

public byte[] getAsBytes() throws IOException
{ 
	data.stream().seek(data.position);
	byte[] a = new byte[data.size];
	data.stream.read(a);
	return a;
}

public char[] getAsChars()  throws IOException
{ 
	data.stream.seek(data.position);
	char[] a = new char[data.size];
	for (int i = 0; i < a.length; i++) {
		a[i] = data.stream.readChar();
	for (int i = 0; i < a.length; i++) {
		a[i] = data.stream.readChar();
	}
	return a;
}

public short[] getAsShorts()  throws IOException
{ 
	data.stream.seek(data.position);
	short[] a = new short[data.size];
	for (int i = 0; i < a.length; i++) {
		a[i] = data.stream.readShort();
	for (int i = 0; i < a.length; i++) {
		a[i] = data.stream.readShort();
	}
	return a;
}

public int[] getAsInts() throws IOException 
{
	data.stream.seek(data.position);
	int[] a = new int[data.size];
	for (int i = 0; i < a.length; i++) {
		a[i] = data.stream.readInt();
	for (int i = 0; i < a.length; i++) {
		a[i] = data.stream.readInt();
	}
	return a;
}

public long[] getAsLongs() throws IOException
{ 
	data.stream.seek(data.position);
	long[] a = new long[data.size];
	for (int i = 0; i < a.length; i++) {
		a[i] = data.stream.readUnsignedInt();
	for (int i = 0; i < a.length; i++) {
		a[i] = data.stream.readUnsignedInt();
	}
	return a;
}

public float[] getAsFloats() throws IOException
