		throw new IOException("Not implemented.");

	}
	

	public long[] getAsRational(int index) throws IOException 
	{
		data.stream.seek(data.position + sizeOfType(type) * index);
		return data.stream.readUnsignedInts(2);
	}

public int[] getAsSRational(int index) throws IOException 
{ 
	data.stream.seek(data.position + sizeOfType(type) * index);
	return data.stream.readInts(2);
}

public TIFFRational(long[] a)
{
	this(a[0], a[1]);
}

	
	public TIFFRational(long p0, long p1)
	private long[] a = new long[2];

