public TIFFValueProxy(StreamReader s, long pos, int size, boolean isBigEndian) 
{
	this.stream = s;
	this.position = pos;
	this.size = size;
	this.isBigEndian = isBigEndian;
}


public StreamReader stream()
{
	return this.stream;
}
