	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter writerA = PdfWriter.getInstance(document, new FileStream("Chap0111a.pdf", FileMode.Create));
	PdfWriter writerB = PdfWriter.getInstance(document, new FileStream("Chap0111b.pdf", FileMode.Create));

	// step 3
	// we add a Watermark that will show up on PAGE 1
	writerB.pause();
	try 
	{
		Watermark watermark = new Watermark(Image.getInstance("watermark.jpg"), 200, 420);
		document.add(watermark);
	}
	catch(Exception e) 
	{
		Console.WriteLine("Are you sure you have the file 'watermark.jpg' in the right path?");
	}
	writerB.resume();

	// we add a Header that will show up on PAGE 1
	HeaderFooter header = new HeaderFooter(new Phrase("This is a header"), false);
	document.setHeader(header);

	// we open the document
	document.open();

	// we rotate the page, starting from PAGE 2
	document.setPageSize(PageSize.A4.rotate());

	// we need to change the position of the Watermark
	try 
	{
		Watermark watermark = new Watermark(Image.getInstance("watermark.jpg"), 320, 200);
		document.add(watermark);
	}
	catch(Exception e) 
	{
		Console.WriteLine("Are you sure you have the file 'watermark.jpg' in the right path?");
	}

	// we add a Footer that will show up on PAGE 2
	HeaderFooter footer = new HeaderFooter(new Phrase("This is page
	document.setFooter(footer);

	// step 4

	// PAGE 1
	document.add(new Paragraph("Hello World"));

	// we trigger a page break
	document.newPage();

	// PAGE 2
	// we add some more content
	document.add(new Paragraph("Hello Earth"));

	// we remove the header starting from PAGE 3
	writerA.pause();
	document.resetHeader();
	writerA.resume();

	// we trigger a page break
	document.newPage();

	// PAGE 3
	// we add some more content
	document.add(new Paragraph("Hello Sun"));
	writerA.pause();
	document.add(new Paragraph("Remark
	writerA.resume();

	// we reset the page numbering
	writerB.pause();
	document.resetPageCount();
	writerB.resume();

	// we trigger a page break
	document.newPage();

	// PAGE 4
	// we add some more content
	document.add(new Paragraph("Hello Moon"));
	writerB.pause();
	document.add(new Paragraph("Remark
	writerB.resume();

	// step 5
	document.close();
}

{

	Console.WriteLine("Chapter 10 example 1

	// step 1
	Document document = new Document();

	// step 2
	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter writer = PdfWriter.getInstance(document, new FileStream("Chap1001.pdf", FileMode.Create));

	// step 3
	document.open();

	// step 4
	PdfContentByte cb = writer.getDirectContent();

	// an example of a rectangle with a diagonal in very thick lines
	cb.setLineWidth(10f);
	// draw a rectangle
	cb.rectangle(100, 700, 100, 100);
	// add the diagonal
	cb.moveTo(100, 700);
	cb.lineTo(200, 800);
	// stroke the lines
	cb.stroke();

	// an example of some circles
	cb.setLineDash(3, 3, 0);
	cb.setRGBColorStrokeF(0f, 255f, 0f);
	cb.circle(150f, 500f, 100f);
	cb.stroke();

	cb.setLineWidth(5f);
	cb.resetRGBColorStroke();
	cb.circle(150f, 500f, 50f);
	cb.stroke();

	// example with colorfill
	cb.setRGBColorFillF(0f, 255f, 0f);
	cb.moveTo(100f, 200f);
	cb.lineTo(200f, 250f);
	cb.lineTo(400f, 150f);
	// because we change the fill color BEFORE we stroke the triangle
	// the color of the triangle will be red instead of green
	cb.setRGBColorFillF(255f, 0f, 0f);
	cb.closePathFillStroke();

	// step 5
	document.close();
}

