private static bool StopOnError = false;

[STAThread]
public static int Main(string[] args)
{
	string ClassName = null;

{	
	string[] aargs = new String[args.Length+1];
	for (int i = 0; i < args.Length; i++)
		aargs[i] = args[i];
	for (int i = 0; i < args.Length; i++)
		aargs[i] = args[i];
	aargs[args.Length] = String.Empty;
	args = aargs;
}
	for (int i = 0; i < args.Length; i++)
	{
		string opt = args[i];
		if (opt == String.Empty) continue;

		if (opt[0] == '/') 
		{
			string OPT = opt.ToUpper();
			if (OPT == "/STOP-ON-ERROR") 
				StopOnError = true;
			else
				throw new ArgumentException("ArgumentException", opt);
		}
		else 
		{
			ClassName = opt;
		}
	}
	for (int i = 0; i < args.Length; i++)
	{
		string opt = args[i];
		if (opt == String.Empty) continue;

		if (opt[0] == '/') 
		{
			string OPT = opt.ToUpper();
			if (OPT == "/STOP-ON-ERROR") 
				StopOnError = true;
			else
				throw new ArgumentException("ArgumentException", opt);
		}
		else 
		{
			ClassName = opt;
		}
	}
	if (ClassName == null) 
		ClassName = "All";

	if (ClassName == "All")
		ExecuteAllExamples();
	else
	{
		ExecuteExample(ClassName);
	}
	return 0;
}

private static bool StopOnError = false;

[STAThread]
public static int Main(string[] args)
{
	string ClassName = null;

{	
	string[] aargs = new String[args.Length+1];
	for (int i = 0; i < args.Length; i++)
		aargs[i] = args[i];
	aargs[args.Length] = String.Empty;
	args = aargs;
}
	for (int i = 0; i < args.Length; i++)
	{
		string opt = args[i];
		if (opt == String.Empty) continue;

		if (opt[0] == '/') 
		{
			string OPT = opt.ToUpper();
			if (OPT == "/STOP-ON-ERROR") 
				StopOnError = true;
			else
				throw new ArgumentException("ArgumentException", opt);
		}
		else 
		{
			ClassName = opt;
		}
	}
	if (ClassName == null) 
		ClassName = "All";

	if (ClassName == "All")
		ExecuteAllExamples();
	else
	{
		ExecuteExample(ClassName);
	}
	return 0;
}

public const string InputFilesDirectory = "Inputs";
private static ArrayList exeExts;

static MainModule()
{
	exeExts = new ArrayList();
	exeExts.Add(".exe");
	exeExts.Add(".bat");
	exeExts.Add(".cmd");
	exeExts.Add(".com");
	exeExts.Add(".lnk");
	exeExts.Add(".config");
	exeExts.Add(".manifest");
	exeExts.Add(".dll");
	exeExts.Add(".pdb");
}


private static void deleteNonExecutables(String dir) 
{
	foreach (String file in Directory.GetFiles(dir)) 
	{
		if (!exeExts.Contains(Path.GetExtension(file))) 
		{
			File.Delete(file);
		}
	}
	foreach (String file in Directory.GetFiles(dir)) 
	{
		if (!exeExts.Contains(Path.GetExtension(file))) 
		{
			File.Delete(file);
		}
	}
}


private static void copyFiles(string[] filenames, String destDir) 
{
	foreach (string filename in filenames)
	{
		System.IO.File.Copy(filename, 
			Path.Combine(destDir, 
			Path.GetFileName(filename)), true);
	}
	foreach (string filename in filenames)
	{
		System.IO.File.Copy(filename, 
			Path.Combine(destDir, 
			Path.GetFileName(filename)), true);
	}
}

