	// we create a writer that listens to the document
	// and directs a PDF-stream to a file
	PdfWriter.getInstance(document, new FileStream("Chap0901.pdf", FileMode.Create));

	// step 3
	document.open();

	// step 4
	BaseFont helvetica = BaseFont.createFont(BaseFont.HELVETICA, BaseFont.CP1252, BaseFont.NOT_EMBEDDED);
	Console.WriteLine(helvetica.getPostscriptFontName());
	Font font = new Font(helvetica, 12, Font.NORMAL);
	Chunk chunk = new Chunk("Sponsor this example and send me 1\u20ac. These are some special characters
	document.add(chunk);

	// step 5
	document.close();
}

